    "contracts/KiwiStaker.sol": {
      "content": "// SPDX-License-Identifier: UNLICENSED
pragma solidity 0.8.19;

import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/security/ReentrancyGuard.sol";
import "@openzeppelin/contracts/token/ERC721/IERC721Receiver.sol";
import '@openzeppelin/contracts/utils/Address.sol';

import "./interfaces/ISwapRouter.sol";
import "./interfaces/INonfungiblePositionManager.sol";
import "./interfaces/IMasterChefV3.sol";
import "./interfaces/IPancakeV3Factory.sol";


error TokenNotAllowed(address token);
error InvalidInputData();
error TransferFailed();


enum Status {
    IDLE, 
    FARMING
}

struct ConstructorParams {
    address nftManager;
    address masterChefV3;
    address smartRouterV3;
    address v3Factory;
    address WETH;
    address kiwi;
    address[] wallets;
    uint32[] fees;
    uint32 defaultFee;
}

struct MintOutput {
    uint256 tokenId;
    uint128 liquidity;
    uint256 amount0;
    uint256 amount1;
}

struct LiquidityIncreaseOutput {
    uint128 liquidity;
    uint256 amount0;
    uint256 amount1;
}

int24 constant MIN_TICK = -887272;
int24 constant MAX_TICK = -MIN_TICK;


contract KiwiStaker is Ownable, ReentrancyGuard, IERC721Receiver {

    address public nftManager;
    address public masterChefV3;
    address public smartRouterV3;
    address public v3Factory;
    address public WETH;
    address public kiwi;
    uint32 public defaultFee;

    mapping(address => uint32) public fees;

    event NewDeposit(address[] beneficiaries, address[] tokens, uint256[] amounts);
    event NewMint(INonfungiblePositionManager.MintParams params, MintOutput output);
    event LiquidityIncreased(uint256 indexed tokenId, LiquidityIncreaseOutput output);
    event DecreasedLiquidity(uint256 indexed tokenId, uint256 amount0, uint256 amount1);
    event NewCollect(uint256 indexed tokenId, uint256 amount0, uint256 amount1);
    event NewHarvest(uint256 indexed tokenId);
    event NewStake(uint256 indexed tokenId);
    event NewUnstake(uint256 indexed tokenId);
    event Payout(address indexed to, address[] tokens, uint256[] amounts);



    function onERC721Received(address operator, address from, uint256 tokenId, bytes calldata data) public virtual override returns (bytes4) {
        return this.onERC721Received.selector;
    }



    constructor (
        ConstructorParams memory params
    ) {
        nftManager = params.nftManager;
        masterChefV3 = params.masterChefV3;
        smartRouterV3 = params.smartRouterV3;
        v3Factory = params.v3Factory;
        WETH = params.WETH;
        _handleFeesConfig(params.kiwi, params.wallets, params.fees, params.defaultFee);
    }


    // Admin related methods

    function config(
        ConstructorParams calldata params
    ) external onlyOwner {
        nftManager = params.nftManager;
        masterChefV3 = params.masterChefV3;
        smartRouterV3 = params.smartRouterV3;
        v3Factory = params.v3Factory;
        WETH = params.WETH;
        _handleFeesConfig(params.kiwi, params.wallets, params.fees, params.defaultFee);
    }


    // User related methods

    // 1 - Users buy an NFT, this method will be called on their behalf
    /**
     * @param tokens The address of the ERC20 tokens
     * @param beneficiaries The beneficiaries of the deposit operations
     * @param amounts The amounts to be deposited
     */
    function deposit(
        address[] calldata tokens,
        address[] calldata beneficiaries, 
        uint256[] calldata amounts
    ) external nonReentrant payable {

        if ( tokens.length != beneficiaries.length || beneficiaries.length != amounts.length ) {
            revert InvalidInputData();
        }

        uint256 totalWei;
        for ( uint256 i = 0 ; i < tokens.length ; ++i ) {

            if ( tokens[i] != address(0) ) {
                if ( amounts[i] > 0 ) {
                    IERC20(tokens[i]).transferFrom(msg.sender, address(this), amounts[i]);
                }
            } else {
                totalWei += amounts[i];
            }

        }

        if ( totalWei != msg.value ) {
            revert InvalidInputData();
        }

        emit NewDeposit(beneficiaries, tokens, amounts);
    }



    // Bot related methods

    function payout(
        address to, 
        address[] calldata tokens, 
        uint256[] calldata amounts
    ) external onlyOwner {
        if ( tokens.length != amounts.length ) {
            revert InvalidInputData();
        }

        for ( uint256 i = 0 ; i < tokens.length ; ++i ) {

            uint256 operationDefaultFee = fees[to] > 0 && fees[to] < defaultFee ? ( amounts[i] * fees[to] ) / 100 : ( amounts[i] * defaultFee ) / 100;

            bool success1; 
            bool success2;

		    if ( tokens[i] == address(0) ) {

            	if ( Address.isContract(to) ) {
                    (success1, )= to.call{ value: amounts[i] - operationDefaultFee }("");
                } else {
                    success1 = payable(to).send(amounts[i] - operationDefaultFee);
                }

                if ( Address.isContract(kiwi) ) {
                    (success2, )= kiwi.call{ value: operationDefaultFee }("");
                } else {
                    success2 = payable(kiwi).send(operationDefaultFee);
                }

            } else {
                success1 = IERC20(tokens[i]).transfer(to, amounts[i] - operationDefaultFee);
                success2 = IERC20(tokens[i]).transfer(kiwi, operationDefaultFee);
            }

            if ( !success1 || !success2 ) {
                revert TransferFailed();
            }

        }

        emit Payout(to, tokens, amounts);
    }

    function increaseLiquidity(
        address tokenIn,
        address tokenOut,
        uint256 tokenId,
        uint256 amountIn,
        uint256 amount0Min, 
        uint256 amount1Min,
        uint24 fee
    ) external onlyOwner {
        uint256 amountOut = _swapExactInputSingle(tokenIn, tokenOut, amountIn/2, 0, fee);
        (uint128 liquidity, uint256 amount0, uint256 amount1) =  INonfungiblePositionManager(nftManager).increaseLiquidity(
            INonfungiblePositionManager.IncreaseLiquidityParams({
                tokenId: tokenId, 
                amount0Desired: amountIn/2, 
                amount1Desired: amountOut, 
                amount0Min: amount0Min, 
                amount1Min: amount1Min,
                deadline: block.timestamp
            })
        );
        emit LiquidityIncreased(tokenId, LiquidityIncreaseOutput({
            liquidity: liquidity, 
            amount0: amount0,
            amount1: amount1
        }));
    }

    function decreaseLiquidity(
        address token, 
        uint256 tokenId, 
        uint256 amount0Min,
        uint256 amount1Min,
        uint128 liquidity
    ) external onlyOwner {

        (uint256 amount0, uint256 amount1) = _decreaseLiquidity(tokenId, liquidity, amount0Min, amount1Min);
        emit DecreasedLiquidity(tokenId, amount0, amount1);

        _collect(token, tokenId);

    }

    function stake(
        uint256 tokenId
    ) external onlyOwner {
        IMasterChefV3(nftManager).safeTransferFrom(address(this), masterChefV3, tokenId);
        emit NewStake(tokenId);
    }

    function unstake(
        uint256 tokenId
    ) external onlyOwner {
        IMasterChefV3(masterChefV3).withdraw(tokenId, address(this));
        emit NewUnstake(tokenId);
    }

    // Add Liquidity
    function addLiquidity(
        address tokenIn,
        address tokenOut,
        uint256 amountIn,
        uint256 amount0Min, 
        uint256 amount1Min,
        uint24 fee
    ) external onlyOwner {

        uint256 amountOut = _swapExactInputSingle(tokenIn, tokenOut, amountIn/2, 0, fee);

        int24 tickSpacing = IPancakeV3Factory(v3Factory).feeAmountTickSpacing(fee);

        INonfungiblePositionManager.MintParams memory params = INonfungiblePositionManager.MintParams(
            {
                token0: tokenIn,
                token1: tokenOut,
                fee: fee,
                tickLower: (MIN_TICK / tickSpacing) * tickSpacing,
                tickUpper: (MAX_TICK / tickSpacing) * tickSpacing,
                amount0Desired: amountIn/2,
                amount1Desired: amountOut,
                amount0Min: amount0Min,
                amount1Min: amount1Min,
                recipient: address(this),
                deadline: block.timestamp
            }
        );


        (
            uint256 tokenId, 
            uint128 liquidity, 
            uint256 amount0, 
            uint256 amount1
        ) = INonfungiblePositionManager(nftManager).mint(params);

        emit NewMint(
            params, 
            MintOutput({ 
                tokenId: tokenId, 
                liquidity: liquidity, 
                amount0: amount0, 
                amount1: amount1 
            })
        );
    }

    function harvest(
        uint256 tokenId
    ) external onlyOwner {
        IMasterChefV3(masterChefV3).harvest(tokenId, address(this));
    }



    // Internal / Private methods

    function _collect(
        address tokenIn, 
        uint256 tokenId
    ) internal {
        INonfungiblePositionManager.CollectParams memory params = INonfungiblePositionManager.CollectParams({
            tokenId: tokenId,
            recipient: tokenIn == WETH ? address(0) : address(this),
            amount0Max: type(uint128).max,
            amount1Max: type(uint128).max
        });
        (uint256 amount0, uint256 amount1) = INonfungiblePositionManager(nftManager).collect(params);
        if ( tokenIn == WETH ) {
            IPeripheryPayments(nftManager).sweepToken(tokenIn, 0, address(this));
        }
        emit NewCollect(tokenId, amount0, amount1);
    }

    function _approveTokenIfNeeded(
        address token, 
        address spender, 
        uint256 amount
    ) private {
        if (IERC20(token).allowance(address(this), spender) < amount) {
            IERC20(token).approve(spender, type(uint256).max);
        }
    }

    function _decreaseLiquidity(
        uint256 tokenId,
        uint128 liquidity,
        uint256 amount0Min,
        uint256 amount1Min
    ) internal returns (uint256 amount0, uint256 amount1) {
        INonfungiblePositionManager.DecreaseLiquidityParams memory params = INonfungiblePositionManager.DecreaseLiquidityParams({
            tokenId: tokenId,
            liquidity: liquidity,
            amount0Min: amount0Min,
            amount1Min: amount1Min,
            deadline: block.timestamp
        });
        (amount0, amount1) = INonfungiblePositionManager(nftManager).decreaseLiquidity(params);
    }

    function _swapExactInputSingle(
        address tokenIn,
        address tokenOut,
        uint256 amountIn,
        uint256 amountOutMin, 
        uint24 poolFee
    ) internal returns (uint256) {
        _approveTokenIfNeeded(tokenIn, nftManager, amountIn);
        _approveTokenIfNeeded(tokenIn, smartRouterV3, type(uint256).max);
        _approveTokenIfNeeded(tokenOut, nftManager, type(uint256).max);

        ISwapRouter.ExactInputSingleParams memory params = ISwapRouter
        .ExactInputSingleParams({
            tokenIn: tokenIn,
            tokenOut: tokenOut,
            fee: poolFee,
            recipient: address(this),
            amountIn: amountIn,
            amountOutMinimum: amountOutMin,
            sqrtPriceLimitX96: 0
        });

        return ISwapRouter(smartRouterV3).exactInputSingle{value:0}(params);
    }

    function _handleFeesConfig(
        address configKiwi,
        address[] memory configWallets, 
        uint32[] memory configFees, 
        uint32 configFee
    ) internal {
        kiwi = configKiwi;
        defaultFee = configFee;

        if ( configWallets.length != configFees.length ) {
            revert InvalidInputData();
        }

        for ( uint256 i = 0 ; i < configWallets.length ; ++i ) {
            fees[configWallets[i]] = configFees[i];
        }
    }

