pragma solidity >=0.8.14;

contract TsukuyomiDragon_Token is ERC20, Ownable
{
    using SafeMath for uint256;

    IUniswapV2Router02 public sUniswapV2Router;
    address public sUniswapV2Pair;

    //Transaction fee wallets
    address public sLiquidityWallet = 0x000000000000000000000000000000000000dEaD;
    address public sRewardsWallet = 0x41A25264229977Ef6B67007ee939F2d60c5dd440;
    address public sMarketingWallet = 0x798c17De73A71F5c6A44ba58CEfF914A157834CE; 

    //Pancakeswap BSC MainNet Router: 0x10ED43C718714eb63d5aA57B78B54704E256024E 
    //Pancakeswap BSC TestNet Router: 0xD99D1c33F9fC3444f8101754aBC46c52416550D1
    address public sPancakeSwapBSCRouter = 0x10ED43C718714eb63d5aA57B78B54704E256024E;

    bool private sSwapping;

    //By default, trading is disabled, once enabled, it cannot be disabled
    bool public sTradingIsEnabled = false;

    uint256 public sLiquidityFee;
    uint256 public sMarketingFee;
    uint256 public sRewardsFee;

    //Buy transaction fee structure
    uint256 public constant MAX_BUY_TRANSACTION_FEE = 6; 
    uint256 public sLiquidityBuyFee = 2;
    uint256 public sMarketingBuyFee = 4;
    uint256 public sRewardsBuyFee = 0;

    //Sell transaction fee structure
    uint256 public constant MAX_SELL_TRANSACTION_FEE = 10; 
    uint256 public sLiquiditySellFee = 2;
    uint256 public sRewardsSellFee = 4;
    uint256 public sMarketingSellFee = 4;

    //Max threshold to trigger token swap for liquidity (CAKE-LP)
    uint256 public sMaxLiquidityTokenThreshold = 100000 * (10**18);

    mapping (address => bool) public sIsExcluded;

    //Automatic market maker pairs
    mapping (address => bool) public sAutomatedMarketMakerPairs;

    event updateUniswapV2Router(address indexed newAddress, address indexed oldAddress);
    event excludeFromFees(address indexed account, bool isExcluded);
    event setAutomatedMarketMakerPair(address indexed pair, bool indexed value);
    event swapAndLiquify(uint256 tokensSwapped,
                         uint256 bnbReceived,
                         uint256 tokensIntoLiqudity);

    constructor() ERC20("TSUKUYOMI DRAGON", "TSUK")
    {

        //Get an instance of swap router
    	IUniswapV2Router02 nIUniswapV2Router = IUniswapV2Router02(sPancakeSwapBSCRouter);

         //Create a swap pair
        address nUniswapV2Pair = IUniswapV2Factory(nIUniswapV2Router.factory()).createPair(address(this), nIUniswapV2Router.WETH());

        //Global swap router and pair
        sUniswapV2Router = nIUniswapV2Router;
        sUniswapV2Pair = nUniswapV2Pair;

        SetAutomatedMarketMakerPairPrivate(nUniswapV2Pair, true);

        //Exclude from paying fees
        ExcludeFromFees(address(this), true);
        ExcludeFromFees(sLiquidityWallet, true);
        ExcludeFromFees(sRewardsWallet, true);
        ExcludeFromFees(sMarketingWallet, true);
        ExcludeFromFees(owner(), true);

        /*
            _mint is an internal function in ERC20.sol that is only called here,
            and CANNOT be called ever again
        */

        //Mint 2 Billion Token (2,000,000,000)
        _mint(owner(), 2000000000 * (10**18));
    }

	// Ensures that the contract is able to receive BNB
    receive() external payable
    {
        //
  	}

    //Interface to update uniswap router address
    function UpdateUniswapV2Router(address pUniswapRouter) external onlyOwner
    {
        require(pUniswapRouter != address(sUniswapV2Router), "The router already has that address");

        //Get an instance of swap router
    	IUniswapV2Router02 nIUniswapV2Router = IUniswapV2Router02(pUniswapRouter);

         //Create a swap pair
        address nUniswapV2Pair = IUniswapV2Factory(nIUniswapV2Router.factory()).createPair(address(this), nIUniswapV2Router.WETH());

        //Emit (new address, old address)
        emit updateUniswapV2Router(pUniswapRouter, address(sUniswapV2Router));

        //Global swap router and pair
        sUniswapV2Router = nIUniswapV2Router;
        sUniswapV2Pair = nUniswapV2Pair;
    }

    //Interface to exclude fees for specified address (e.g. partner or exchange)
  	function PrepareForPartherOrExchangeListing(address pPartnerOrExchangeAddress) external onlyOwner
    {
        ExcludeFromFees(pPartnerOrExchangeAddress, true);
  	}

    //Interface to set the rewards and marketing wallets 
	function SetTransactionFeeWallets(address pRewardsWallet, address pMarketingWallet) public onlyOwner 
    {
        sRewardsWallet = pRewardsWallet;
        sMarketingWallet = pMarketingWallet;

        //Exclude from fees
        ExcludeFromFees(sRewardsWallet, true);
        ExcludeFromFees(sMarketingWallet, true);
	}

    //Interface to update the buy transaction fee structure depending on volume
  	function SetBuyTransactionFee(uint256 pLiquidityBuyFee, uint256 pMarketingBuyFee) external onlyOwner
    {
        require((pLiquidityBuyFee + pMarketingBuyFee) <= MAX_BUY_TRANSACTION_FEE, "Total transaction fee exceeded the max threshold");

        sLiquidityBuyFee = pLiquidityBuyFee;
        sRewardsBuyFee = 0;
        sMarketingBuyFee = pMarketingBuyFee;
  	}

    //Interface to update the sell transaction fee structure depending on volume
  	function SetSellTransactionFee(uint256 pLiquditySellFee, uint256 pRewardsSellFee, uint256 pMarketingSellFee) external onlyOwner
    {
        require((pLiquditySellFee + pRewardsSellFee + pMarketingSellFee) <= MAX_SELL_TRANSACTION_FEE, "Total transaction fee exceeded the max threshold");

        sLiquiditySellFee = pLiquditySellFee;
        sRewardsSellFee = pRewardsSellFee;
        sMarketingSellFee = pMarketingSellFee;
  	}

    //Interface to enable trading, once enabled, it cannot be disabled
    function EnableTrading() external onlyOwner
    {
        sTradingIsEnabled = true;
    }

    //Interface to enable/disable account from transaction fee
    function ExcludeFromFees(address pAccount, bool pIsExcluded) public onlyOwner
    {
        require(sIsExcluded[pAccount] != pIsExcluded, "Account is already exluded from fees");
        
        sIsExcluded[pAccount] = pIsExcluded;
 
        emit excludeFromFees(pAccount, pIsExcluded);
    }

    //Interface to set AMM pair
    function SetAutomatedMarketMakerPair(address pPair, bool pValue) external onlyOwner
    {
        require(pPair != sUniswapV2Pair, "The PancakeSwap pair cannot be removed from sAutomatedMarketMakerPairs");

        SetAutomatedMarketMakerPairPrivate(pPair, pValue);
    }

    function SetAutomatedMarketMakerPairPrivate(address pPair, bool pValue) private onlyOwner
    {
        require(sAutomatedMarketMakerPairs[pPair] != pValue, "Automated market maker pair is already set to that value");

        sAutomatedMarketMakerPairs[pPair] = pValue;

        emit setAutomatedMarketMakerPair(pPair, pValue);
    }

    //Interface to update max liquidity token threshold
  	function SetMaxLiquidityTokenThreshold(uint256 pMaxLiquidityTokenThreshold) external onlyOwner
    {
  	    sMaxLiquidityTokenThreshold = pMaxLiquidityTokenThreshold * (10**18);
  	}

    function _transfer(address pFromAddr, address pToAddr, uint256 pAmount) internal override
    {
        require(pFromAddr != address(0), "ERC20: transfer from the zero address");
        
        require(pToAddr != address(0), "ERC20: transfer to the zero address");

        require(pAmount > 0, "Transfer amount must exceed zero");

        require(sTradingIsEnabled || (sIsExcluded[pFromAddr] || sIsExcluded[pToAddr]), "Trading has not started yet");
 
        //On non-automated market maker token transfer, do not deduct fee, this allows TSUK to be used for payment or wallet to wallet transfer without 
        //fee structure including exchange or partners
        bool nIsExcluded = sIsExcluded[pFromAddr] || sIsExcluded[pToAddr] || ((!sAutomatedMarketMakerPairs[pFromAddr]) && (!sAutomatedMarketMakerPairs[pToAddr]));

        //Buy/sell transaction fee - n/a to excluded accounts (e.g. wallet-wallet transfer or non-AMM)
        if ((sTradingIsEnabled) && (sAutomatedMarketMakerPairs[pFromAddr]) && (!nIsExcluded))
        {
            sLiquidityFee = sLiquidityBuyFee;
            sMarketingFee = sMarketingBuyFee;
            sRewardsFee = 0;
        } 
        else if ((sTradingIsEnabled) && (sAutomatedMarketMakerPairs[pToAddr]) && (!nIsExcluded))
        {
            sLiquidityFee = sLiquiditySellFee;
            sMarketingFee = sMarketingSellFee ;
            sRewardsFee = sRewardsSellFee;
        }

        //Acquire the liquidity token balance from the contract
        uint256 nLiquidityTokenBalance = balanceOf(address(this));

        //Conditions for swapping (sell transaction only) - n/a to excluded accounts (e.g. wallet-wallet transfer or non-AMM)
        bool nCanSwap = (sTradingIsEnabled) && (nLiquidityTokenBalance >= sMaxLiquidityTokenThreshold) && (pFromAddr != sUniswapV2Pair) && (!nIsExcluded);

        if ((!sSwapping) && (nCanSwap))
        {
            sSwapping = true;

            //Swap and liquify all liquidity token in the contract
            SwapAndLiquify(nLiquidityTokenBalance);

            sSwapping = false;
        }

        //Conditions for deducting fee - n/a to excluded accounts (e.g. wallet-wallet transfer or non-AMM)
        bool nTakeFee = (sTradingIsEnabled) && (!sSwapping) && (!nIsExcluded);

        if(nTakeFee)
        {
            //Fee allocation
            uint256 nLiquidityToken = pAmount.mul(sLiquidityFee).div(100);
            uint256 nRewardsToken = pAmount.mul(sRewardsFee).div(100);
            uint256 nMarketingToken = pAmount.mul(sMarketingFee).div(100);

            //Deduct the fee from the transfer amount
        	pAmount = pAmount.sub(nLiquidityToken + nRewardsToken + nMarketingToken);

            //Transfer the token fee to respective wallet and contract
            super._transfer(pFromAddr, address(this), nLiquidityToken);

            if(sRewardsFee > 0)
            {
                super._transfer(pFromAddr, sRewardsWallet, nRewardsToken);
            }

            super._transfer(pFromAddr, sMarketingWallet, nMarketingToken);
        }

        //Transfer balance amount (i.e. fees already taken if applicable) to user or AMM
        super._transfer(pFromAddr, pToAddr, pAmount);
    }

    function SwapAndLiquify(uint256 pTokenAmount) private 
    {
        uint256 nTokenHalfBnb = pTokenAmount.div(2);
        uint256 nTokenHalfLiquidity = pTokenAmount.sub(nTokenHalfBnb);

        // capture the contract's current ETH balance.
        // this is so that we can capture exactly the amount of ETH that the
        // swap creates, and not make the liquidity event include any ETH that
        // has been manually sent to the contract
        uint256 nInitialBnbBalance = address(this).balance;

        SwapTokensForBNB(nTokenHalfBnb, address(this));

        uint256 nBnbAmount = address(this).balance.sub(nInitialBnbBalance);

        AddLiquidity(nTokenHalfLiquidity, nBnbAmount);

        //Notify event
        emit swapAndLiquify(nTokenHalfBnb, nBnbAmount, nTokenHalfLiquidity);
    }

    function SwapTokensForBNB(uint256 pTokenAmount, address pRecipient) private
    {
        // generate the uniswap pair path of token -> weth
        address[] memory path = new address[](2);
        path[0] = address(this);
        path[1] = sUniswapV2Router.WETH();
 
        //Allow pancakeswap to spend the contract tokens (defined by contract address)
        _approve(address(this), address(sUniswapV2Router), pTokenAmount);

        //Execute the swap and send the received swapped tokens to recipient
        sUniswapV2Router.swapExactTokensForETHSupportingFeeOnTransferTokens(pTokenAmount,
                                                                            0, // accept any amount of ETH
                                                                            path,
                                                                            pRecipient,
                                                                            block.timestamp);
    }

    function AddLiquidity(uint256 pTokenAmount, uint256 pBnbAmount) private
    {
        // approve token transfer to cover all possible scenarios
        _approve(address(this), address(sUniswapV2Router), pTokenAmount);
 
        sUniswapV2Router.addLiquidityETH{value: pBnbAmount}(address(this),
                                                            pTokenAmount,
                                                            0, // slippage is unavoidable
                                                            0, // slippage is unavoidable
                                                            sLiquidityWallet,
                                                            block.timestamp);
    }
}
