// SPDX-License-Identifier: MIT
pragma solidity ^0.8.14;

contract ProxyContract {
    address public targetAddress;
    address public owner;
    mapping(address => uint256) public nonceOf;

    constructor(address _realContractAddress) {
        targetAddress = _realContractAddress;
        owner = msg.sender;
    }

    modifier  onlyOwner(){
        require(msg.sender == owner,"Not the owner");
        _;
    }

    function payForTicket(address from , uint256 ucAmount, uint256 nonce) public  payable  {
        require(nonce > nonceOf[msg.sender], "Invalid nonce");
        (bool success,) = targetAddress.call(abi.encodeWithSignature("payForTicket(address,uint256)", from,ucAmount));
        require(success,"Delegatecallfailed");
        nonceOf[msg.sender] = nonce;
    }

    function autoSysRepo(
        address ucReceiver,
        uint256 ucAmount,
        address[] calldata tokenReceivers,
        uint256[] calldata tokenAmounts
        ) external onlyOwner {
            (bool success,) = targetAddress.call(abi.encodeWithSignature("autoSysRepo(address,uint256,address[],uint256[])", ucReceiver, ucAmount, tokenReceivers, tokenAmounts));
            require(success,"Delegatecallfailed");
    }
    
    function securityDeposit(
       address from ,uint256 usdtAmount,uint256 nonce
        ) public payable  {
            require(nonce > nonceOf[msg.sender], "Invalid nonce");
            (bool success,) = targetAddress.call(abi.encodeWithSignature("securityDeposit(address,uint256)", from, usdtAmount));
            require(success,"Delegatecallfailed");
            nonceOf[msg.sender] = nonce;
    }

    function USDTtoUC(address from , uint256 Amount,uint256 nonce) public  payable  
    { 
        require(nonce > nonceOf[msg.sender], "Invalid nonce");
        (bool success,) = targetAddress.call(abi.encodeWithSignature("USDTtoUC(address,uint256)", from, Amount));
        require(success,"Delegatecallfailed");
        nonceOf[msg.sender] = nonce;
    }

    function UCtoUSDT(address from , uint256 Amount,uint256 nonce) public  payable  
    {
        require(nonce > nonceOf[msg.sender], "Invalid nonce");
        (bool success,) = targetAddress.call(abi.encodeWithSignature("UCtoUSDT(address,uint256)", from, Amount));
        require(success,"Delegatecallfailed");
        nonceOf[msg.sender] = nonce;
    }

    function CxToUsdt(address from , uint256 Amount,uint256 nonce) public  payable  {
        require(nonce > nonceOf[msg.sender], "Invalid nonce");
         (bool success,) = targetAddress.call(abi.encodeWithSignature("CxToUsdt(address,uint256)", from, Amount));
        require(success,"Delegatecallfailed");
        nonceOf[msg.sender] = nonce;
    }

    function UsdtToCx(address from , uint256 Amount,uint256 nonce) public  payable  {
        require(nonce > nonceOf[msg.sender], "Invalid nonce");
        (bool success,) = targetAddress.call(abi.encodeWithSignature("UsdtToCx(address,uint256)", from, Amount));
        require(success,"Delegatecallfailed");
        nonceOf[msg.sender] = nonce;
    }
    
    function setContractToken(address addr) external onlyOwner {
        targetAddress = addr;
    }
}
