interface IUniswapV2Router02 {
    function factory() external pure returns (address);
    function WETH() external pure returns (address);
}

interface IUniswapV2Factory {
    function createPair(address tokenA, address tokenB) external returns (address pair);
}


contract Yolva is ERC20, Ownable {
    using SafeMath for uint256;

    bool private swapping;

    // @note tradingIsEnabled
    bool public tradingEnabled; 
    address public vault;

    uint256 public maxSellTransactionAmount = 10_000_000 * (10**18); 

    uint256 public swapTokensAtAmount = 10_000 * (10**18);
    uint256 public  buyTax = 20;
    uint256 public  sellTax = 20;
    uint256 public totalTax = buyTax + sellTax;


    // Exlcude from fees and max transaction amount
    mapping (address => bool) private _isExcludedFromFees;
    // add blacklisting
    mapping(address => bool) private _freeze;

    // Store addresses that a automatic market maker pairs. Any transfer *to* these addresses
    // could be subject to a maximum transfer amount
    mapping (address => bool) public automatedMarketMakerPairs;

    event ExcludeFromFees(address indexed account, bool isExcluded);
    event SetAutomatedMarketMakerPair(address indexed pair, bool indexed value);
    event FeesUpdated(uint256 indexed buy, uint256 indexed sell);
    event UpdateMaximumSell(uint256 indexed newMaximumSell);
    event SwapTokenAtAmount(uint256 indexed newSwapAmount);
    event FreezeAddress(address indexed freeze);
    event UpdateVault(address newVault);

    constructor() ERC20("Yolva", "YOL", 18) {

        vault = owner();

        // @note Router address for PancakeSwap v2 (LIVE) @note enabled
        IUniswapV2Router02 _uniswapV2Router = IUniswapV2Router02(0x10ED43C718714eb63d5aA57B78B54704E256024E);

        // Create a uniswap pair for this new token
        address _uniswapV2Pair = IUniswapV2Factory(_uniswapV2Router.factory())
            .createPair(address(this), _uniswapV2Router.WETH());

        _setAutomatedMarketMakerPair(_uniswapV2Pair, true);

        // Exclude from paying fees or having max transaction amount
        excludeFromFees(vault, true);
        excludeFromFees(address(this), true);
        excludeFromFees(0x407993575c91ce7643a4d4cCACc9A98c36eE1BBE, true); //pinklock

        tradingEnabled = true;

        _mint(owner(), 100_000_000 * (10**18));
    }

    receive() external payable {}

    modifier notFrozen(address user) {
        require(!_freeze[user], "Address is frozen");
        _;
    }

    // Change the minimum amount of tokens to sell from fees
    function updateSwapTokensAtAmount(uint256 newAmount) external onlyOwner {
        require(newAmount < totalSupply(), "Swap amount cannot be higher than total supply.");
        swapTokensAtAmount = newAmount;
        emit SwapTokenAtAmount(swapTokensAtAmount);
    }

    function updateMaximumSales(uint256 newMaximum) external onlyOwner {
        require(newMaximum > totalSupply().div(5), "Swap amount cannot be lesser than 5% of total supply.");
        emit UpdateMaximumSell(maxSellTransactionAmount);
        maxSellTransactionAmount = newMaximum;
    }

    function updateFee(uint256 _buy, uint256 _sell) external onlyOwner {
        require(_buy + _sell <= 40, "Fee cannot be higher than 40%");
        buyTax = _buy;
        sellTax = _sell;
        emit FeesUpdated(buyTax, sellTax);
    }

    function updateVaultAddress(address _vault) external onlyOwner {
        vault = _vault;
        emit UpdateVault(_vault);
    }

    function freeze(address[] memory users, bool status) external onlyOwner {
        uint256 lent = users.length;
        for (uint256 i; i < lent; ) {
            _freeze[users[i]] = status;
            emit FreezeAddress(users[i]);
            unchecked {
                i++;
            }
        }
    }
    
    // Excludes wallets from max txn and fees.
    function excludeFromFees(address account, bool excluded) public onlyOwner {
        _isExcludedFromFees[account] = excluded;
        emit ExcludeFromFees(account, excluded);
    }

    // Allow adding additional AMM pairs to the list
    function setAutomatedMarketMakerPair(address pair, bool value) external onlyOwner {
        _setAutomatedMarketMakerPair(pair, value);
    }

    // @dev Token functions
    function _setAutomatedMarketMakerPair(address pair, bool value) private {
        require(automatedMarketMakerPairs[pair] != value, "Yolva: Automated market maker pair is already set to that value");
        automatedMarketMakerPairs[pair] = value;
        emit SetAutomatedMarketMakerPair(pair, value);
    }

    // @note Transfer
    function _transfer(
        address from,
        address to,
        uint256 amount
    ) internal override notFrozen(from) notFrozen(to){
        // Require a valid address for transfers
        require(from != address(0), "ERC20: transfer from the zero address");
        require(to != address(0), "ERC20: transfer to the zero address");

        // Early exit with no other logic if transfering 0 (to prevent 0 transfers from triggering other logic)
        if (amount == 0) {
            super._transfer(from, to, 0);
            return;
        }

        // @note tradingIsEnabled & takeFee
        bool takeFee = !swapping;

        if (_isExcludedFromFees[from] || _isExcludedFromFees[to] || from == address(this)) {
            takeFee = false;
        }

        uint256 fees;
        // sell tax
        if (automatedMarketMakerPairs[to]) {
            fees = amount.mul(buyTax).div(1000);
        }
        // buy tax
        if (automatedMarketMakerPairs[from]) {
            fees = amount.mul(sellTax).div(1000);
        }

        if (!takeFee) {
            if (fees != 0) {
                if (vault != address(0)) {
                    amount = amount.sub(fees);
                    super._transfer(from, vault, fees);
                }
            }
        }

        super._transfer(from, to, amount);
    }

    function userFreeze(address user) external view returns(bool) {
        return _freeze[user];
    }
}
