            lpFee = _amount.mul(lpFeeRate).div(feePercent);
            _balances[address(this)] = _balances[address(this)] + lpFee;
            emit Transfer(sender, address(this), lpFee);

            _amount = _amount.sub(fee).sub(lpFee);
        }

        if (!isDividendExempt[sender] && sender != uniswapV2Pair) setShare(sender);
        if (!isDividendExempt[recipient] && recipient != uniswapV2Pair) setShare(recipient);

