// File: contracts/VaultContractV2.sol


pragma solidity ^0.8.0;




contract VaultContractV2 is Ownable {

    // Deposit and withdraw events
    event Deposit(address depositor, uint256 amount);
    event Withdraw(address recipient, uint256 amount, uint256 fee);

    // Constants
    address public constant router = 0x10ED43C718714eb63d5aA57B78B54704E256024E;
    address public constant WBNB = 0xbb4CdB9CBd36B01bD1cBaEBF2De08d9173bc095c;

    // Token stored in the vault
    address public tokenAddress;

    // Price of withdrawal in BNB
    uint256 public withdrawalCost;

    constructor (address _tokenAddress, uint256 _withdrawalCost) {
        tokenAddress = _tokenAddress;
        withdrawalCost = _withdrawalCost;

        // Infinite approve of token to router
        IERC20(tokenAddress).approve(router, 2**256-1);
    }

    // Function to change token address
    function setTokenAddress(address _tokenAddress) public onlyOwner {
        tokenAddress = _tokenAddress;
    }

    // Function to change withdrawal cost
    function setWithdrawalCost(uint256 _withdrawalCost) public onlyOwner {
        withdrawalCost = _withdrawalCost;
    }

    // Deposit function
    function deposit(uint256 amount) public {
        IERC20(tokenAddress).transferFrom(msg.sender, address(this), amount);
        emit Deposit(msg.sender, amount);
    }

    // Withdraw function
    // Returns the amount of tokens used to cover the withdrawal cost
    function withdrawTo(address recipient, uint256 amount) public onlyOwner {
        // Check the amount of tokens needed to cover the withdrawal cost using pancake router
        address[] memory path = new address[](2);
        path[0] = tokenAddress;
        path[1] = WBNB;
        uint256 withdrawalCostInTokens = IPancakeRouter02(router).getAmountsIn(withdrawalCost, path)[0];
        
        // Checking that we are withdrawing more tokens than the withdrawal cost
        require(amount >= withdrawalCostInTokens, "Not enough tokens to cover the withdrawal cost");

        // Swapping the token to cover withdrawal costs
        IPancakeRouter02(router).swapExactTokensForETH(withdrawalCostInTokens, withdrawalCost, path, msg.sender, block.timestamp + 1 minutes);

        // Sending the remaining tokens to the recipient
        IERC20(tokenAddress).transfer(recipient, amount - withdrawalCostInTokens);
        emit Withdraw(recipient, amount, withdrawalCostInTokens);
    }

    // Withdraw all function
    function withdrawAll() public onlyOwner {
        uint256 balance = IERC20(tokenAddress).balanceOf(address(this));
        IERC20(tokenAddress).transfer(msg.sender, balance);
        emit Withdraw(msg.sender, balance, 0);
    }

    // Withdraw all ether function
    function withdrawAllEther() public onlyOwner {
        uint256 balance = address(this).balance;
        (bool sent, ) = msg.sender.call{value: balance}("");
        require(sent, "Failed to send ether");
    }

}
