abstract contract Context {
    function _msgSender() internal view virtual returns (address) {
        return msg.sender;
    }

    function _msgData() internal view virtual returns (bytes calldata) {
        return msg.data;
    }
}

abstract contract Ownable is Context {
    address private _owner;

    event OwnershipTransferred(
        address indexed previousOwner,
        address indexed newOwner
    );

    /**
     * @dev Initializes the contract setting the deployer as the initial owner.
     */
    constructor() {
        _transferOwnership(_msgSender());
    }

    /**
     * @dev Returns the address of the current owner.
     */
    function owner() public view virtual returns (address) {
        return _owner;
    }

    /**
     * @dev Throws if called by any account other than the owner.
     */
    modifier onlyOwner() {
        require(owner() == _msgSender(), "Ownable: caller is not the owner");
        _;
    }

    /**
     * @dev Leaves the contract without owner. It will not be possible to call
     * `onlyOwner` functions anymore. Can only be called by the current owner.
     *
     * NOTE: Renouncing ownership will leave the contract without an owner,
     * thereby removing any functionality that is only available to the owner.
     */
    function renounceOwnership() public virtual onlyOwner {
        _transferOwnership(address(0));
    }

    /**
     * @dev Transfers ownership of the contract to a new account (`newOwner`).
     * Can only be called by the current owner.
     */
    function transferOwnership(address newOwner) public virtual onlyOwner {
        require(
            newOwner != address(0),
            "Ownable: new owner is the zero address"
        );
        _transferOwnership(newOwner);
    }

    /**
     * @dev Transfers ownership of the contract to a new account (`newOwner`).
     * Internal function without access restriction.
     */
    function _transferOwnership(address newOwner) internal virtual {
        address oldOwner = _owner;
        _owner = newOwner;
        emit OwnershipTransferred(oldOwner, newOwner);
    }
}

interface IPancakeFactory {
    event PairCreated(address indexed token0, address indexed token1, address pair, uint);

    function feeTo() external view returns (address);
    function feeToSetter() external view returns (address);

    function getPair(address tokenA, address tokenB) external view returns (address pair);
    function allPairs(uint) external view returns (address pair);
    function allPairsLength() external view returns (uint);

    function createPair(address tokenA, address tokenB) external returns (address pair);

    function setFeeTo(address) external;
    function setFeeToSetter(address) external;
}

interface IPancakeRouter01 {
    function factory() external pure returns (address);
    function WETH() external pure returns (address);

    function addLiquidity(
        address tokenA,
        address tokenB,
        uint amountADesired,
        uint amountBDesired,
        uint amountAMin,
        uint amountBMin,
        address to,
        uint deadline
    ) external returns (uint amountA, uint amountB, uint liquidity);
    function addLiquidityETH(
        address token,
        uint amountTokenDesired,
        uint amountTokenMin,
        uint amountETHMin,
        address to,
        uint deadline
    ) external payable returns (uint amountToken, uint amountETH, uint liquidity);
    function removeLiquidity(
        address tokenA,
        address tokenB,
        uint liquidity,
        uint amountAMin,
        uint amountBMin,
        address to,
        uint deadline
    ) external returns (uint amountA, uint amountB);
    function removeLiquidityETH(
        address token,
        uint liquidity,
        uint amountTokenMin,
        uint amountETHMin,
        address to,
        uint deadline
    ) external returns (uint amountToken, uint amountETH);
    function removeLiquidityWithPermit(
        address tokenA,
        address tokenB,
        uint liquidity,
        uint amountAMin,
        uint amountBMin,
        address to,
        uint deadline,
        bool approveMax, uint8 v, bytes32 r, bytes32 s
    ) external returns (uint amountA, uint amountB);
    function removeLiquidityETHWithPermit(
        address token,
        uint liquidity,
        uint amountTokenMin,
        uint amountETHMin,
        address to,
        uint deadline,
        bool approveMax, uint8 v, bytes32 r, bytes32 s
    ) external returns (uint amountToken, uint amountETH);
    function swapExactTokensForTokens(
        uint amountIn,
        uint amountOutMin,
        address[] calldata path,
        address to,
        uint deadline
    ) external returns (uint[] memory amounts);
    function swapTokensForExactTokens(
        uint amountOut,
        uint amountInMax,
        address[] calldata path,
        address to,
        uint deadline
    ) external returns (uint[] memory amounts);
    function swapExactETHForTokens(uint amountOutMin, address[] calldata path, address to, uint deadline)
        external
        payable
        returns (uint[] memory amounts);
    function swapTokensForExactETH(uint amountOut, uint amountInMax, address[] calldata path, address to, uint deadline)
        external
        returns (uint[] memory amounts);
    function swapExactTokensForETH(uint amountIn, uint amountOutMin, address[] calldata path, address to, uint deadline)
        external
        returns (uint[] memory amounts);
    function swapETHForExactTokens(uint amountOut, address[] calldata path, address to, uint deadline)
        external
        payable
        returns (uint[] memory amounts);

    function quote(uint amountA, uint reserveA, uint reserveB) external pure returns (uint amountB);
    function getAmountOut(uint amountIn, uint reserveIn, uint reserveOut) external pure returns (uint amountOut);
    function getAmountIn(uint amountOut, uint reserveIn, uint reserveOut) external pure returns (uint amountIn);
    function getAmountsOut(uint amountIn, address[] calldata path) external view returns (uint[] memory amounts);
    function getAmountsIn(uint amountOut, address[] calldata path) external view returns (uint[] memory amounts);
}

interface IPancakeRouter02 is IPancakeRouter01 {
    function removeLiquidityETHSupportingFeeOnTransferTokens(
        address token,
        uint liquidity,
        uint amountTokenMin,
        uint amountETHMin,
        address to,
        uint deadline
    ) external returns (uint amountETH);
    function removeLiquidityETHWithPermitSupportingFeeOnTransferTokens(
        address token,
        uint liquidity,
        uint amountTokenMin,
        uint amountETHMin,
        address to,
        uint deadline,
        bool approveMax, uint8 v, bytes32 r, bytes32 s
    ) external returns (uint amountETH);

    function swapExactTokensForTokensSupportingFeeOnTransferTokens(
        uint amountIn,
        uint amountOutMin,
        address[] calldata path,
        address to,
        uint deadline
    ) external;
    function swapExactETHForTokensSupportingFeeOnTransferTokens(
        uint amountOutMin,
        address[] calldata path,
        address to,
        uint deadline
    ) external payable;
    function swapExactTokensForETHSupportingFeeOnTransferTokens(
        uint amountIn,
        uint amountOutMin,
        address[] calldata path,
        address to,
        uint deadline
    ) external;
}

contract IBAT is Context, IERC20, Ownable {
    using SafeMath for uint256;
    using SafeERC20 for IERC20;
    using Address for address;

    receive() external payable {}

    constructor(
        address _marketing,
        address _lp,
        address _airdrop,
        address _swap,
        address _transferAddress,
        address _block
    ) {
        _name = "IBAT";
        _symbol = "IBAT";

        IPancakeRouter02 _router = IPancakeRouter02(
            0x10ED43C718714eb63d5aA57B78B54704E256024E
        );

        marketingAddress = _marketing;
        lpRewardAddress = _lp;
        airdropAddress = _airdrop;
        swapAddress = _swap;
        transferAddress = _transferAddress;
        blockAddress = _block;

        buyLpRate = 1;
        buyLiquidityRate = 1;
        buyMarketRate = 2;
        buyBurnRate = 1;

        sellLpRate = 0;
        sellLiquidityRate = 1;
        sellMarketRate = 3;
        sellBurnRate = 1;
        transferRate = 0;

        maxSellRate = 99;
        buyRate = 1000;

        router = _router;
        pairAddress = IPancakeFactory(_router.factory()).createPair(
            address(this),
            _router.WETH()
        );

        isExcludedFromFee[marketingAddress] = true;
        isExcludedFromFee[lpRewardAddress] = true;
        isExcludedFromFee[airdropAddress] = true;
        isExcludedFromFee[swapAddress] = true;
        isExcludedFromFee[transferAddress] = true;
        isExcludedFromFee[blockAddress] = true;

        isExcludedFromFee[owner()] = true;
        isExcludedFromFee[address(this)] = true;

        isMarketPair[address(pairAddress)] = true;
        isExcludedAddLiquidityLimit[address(this)] = true;

        addLiquidityEnabled = false;
        swapEnabled = false;
        takeFeeDisabled = false;
        swapAndAddLiquidityEnabled = true;

        _balances[_msgSender()] = _totalSupply;
        emit Transfer(address(0), _msgSender(), _totalSupply);
    }

    string private _name;
    string private _symbol;
    uint8 private _decimals = 18;

    address private marketingAddress;
    address private transferAddress;
    address private lpRewardAddress;
    address private swapAddress;
    address private blockAddress;

    address private airdropAddress;

    uint256 public liquidityBalance;

    uint256 private buyLpRate;
    uint256 private buyLiquidityRate;
    uint256 private buyMarketRate;
    uint256 private buyBurnRate;

    uint256 private sellLpRate;
    uint256 private sellLiquidityRate;
    uint256 private sellMarketRate;
    uint256 private sellBurnRate;

    uint256 private transferRate;

    uint256 private maxSellRate;
    uint256 private buyRate;

    mapping(address => uint256) private _receiveTotal;
    mapping(address => uint256) private _buyTotal;

    mapping(address => uint256) _balances;
    mapping(address => mapping(address => uint256)) private _allowances;

    mapping(address => bool) public isExcludedFromFee;
    mapping(address => bool) public isExcludedAddLiquidityLimit;
    mapping(address => bool) public isMarketPair;

    mapping(address => bool) public isBlackList;

    bool public takeFeeDisabled;
    bool public swapEnabled;
    bool public addLiquidityEnabled;
    bool public swapAndAddLiquidityEnabled;

    uint256 private startBlock;

    function rescueToken(address tokenAddress, uint256 tokens)
        public
        onlyOwner
        returns (bool success)
    {
        return IERC20(tokenAddress).transfer(msg.sender, tokens);
    }

    function rescueBNB(address payable _recipient) public onlyOwner {
        _recipient.transfer(address(this).balance);
    }

    address public immutable deadAddress =
        0x000000000000000000000000000000000000dEaD;

    uint256 private _totalSupply = 1000000000 * 10**_decimals;

    IPancakeRouter02 public router;

    address public pairAddress;

    bool inSwapAndLiquify;

    modifier lockTheSwap() {
        inSwapAndLiquify = true;
        _;
        inSwapAndLiquify = false;
    }

    function name() public view returns (string memory) {
        return _name;
    }

    function symbol() public view returns (string memory) {
        return _symbol;
    }

    function decimals() public view returns (uint8) {
        return _decimals;
    }

    function totalSupply() public view override returns (uint256) {
        return _totalSupply;
    }

    function balanceOf(address account) public view override returns (uint256) {
        return _balances[account];
    }

    function allowance(address owner, address spender)
        public
        view
        override
        returns (uint256)
    {
        return _allowances[owner][spender];
    }

    function increaseAllowance(address spender, uint256 addedValue)
        public
        virtual
        returns (bool)
    {
        _approve(
            _msgSender(),
            spender,
            _allowances[_msgSender()][spender].add(addedValue)
        );
        return true;
    }

    function decreaseAllowance(address spender, uint256 subtractedValue)
        public
        virtual
        returns (bool)
    {
        _approve(
            _msgSender(),
            spender,
            _allowances[_msgSender()][spender].sub(
                subtractedValue,
                "ERC20: decreased allowance below zero"
            )
        );
        return true;
    }

    function approve(address spender, uint256 amount)
        public
        override
        returns (bool)
    {
        _approve(_msgSender(), spender, amount);
        return true;
    }

    function _approve(
        address owner,
        address spender,
        uint256 amount
    ) private {
        require(owner != address(0), "ERC20: approve from the zero address");
        require(spender != address(0), "ERC20: approve to the zero address");

        require(
            spender != address(router) ||
                addLiquidityEnabled ||
                isExcludedAddLiquidityLimit[owner],
            "can't add liquidity"
        );

        _allowances[owner][spender] = amount;
        emit Approval(owner, spender, amount);
    }

    function setMarketPairStatus(address account, bool newValue)
        public
        onlyOwner
    {
        isMarketPair[account] = newValue;
    }

    function setTakeFeeDisabled(bool _value) public onlyOwner {
        takeFeeDisabled = _value;
    }

    function setSwapEnabled(bool _value) public onlyOwner {
        swapEnabled = _value;
    }

    function setSwapAndAddLiquidityEnabled(bool _value) public onlyOwner {
        swapAndAddLiquidityEnabled = _value;
    }

    function setAddLiquidityEnabled(bool _value) public onlyOwner {
        addLiquidityEnabled = _value;
    }

    function setIsExcludedFromFee(address account, bool newValue)
        public
        onlyOwner
    {
        isExcludedFromFee[account] = newValue;
    }

    function setIsBlackList(address account, bool newValue) public onlyOwner {
        isBlackList[account] = newValue;
    }

    function setIsExcludedAddLiquidityLimit(address account, bool newValue)
        public
        onlyOwner
    {
        isExcludedAddLiquidityLimit[account] = newValue;
    }

    function setRate(
        uint256 _buyMarket,
        uint256 _buyLP,
        uint256 _buyBurn,
        uint256 _buyLiquidity,
        uint256 _transferRate,
        uint256 _sellMarket,
        uint256 _sellLP,
        uint256 _sellBurn,
        uint256 _sellLiquidity,
        uint256 _maxSell,
        uint256 _buyRate
    ) external onlyOwner {
        buyLpRate = _buyLP;
        buyLiquidityRate = _buyLiquidity;
        buyMarketRate = _buyMarket;
        buyBurnRate = _buyBurn;

        sellLpRate = _sellLP;
        sellLiquidityRate = _sellLiquidity;
        sellMarketRate = _sellMarket;
        sellBurnRate = _sellBurn;
        transferRate = _transferRate;

        maxSellRate = _maxSell;
        buyRate = _buyRate;
    }

    function setAddress(
        address _marketing,
        address _lp,
        address _transferAddress,
        address _swap,
        address _airdrop,
        address _block
    ) external onlyOwner {
        marketingAddress = _marketing;
        lpRewardAddress = _lp;
        airdropAddress = _airdrop;
        swapAddress = _swap;
        transferAddress = _transferAddress;
        blockAddress = _block;

        isExcludedFromFee[transferAddress] = true;
        isExcludedFromFee[marketingAddress] = true;
        isExcludedFromFee[lpRewardAddress] = true;
        isExcludedFromFee[_swap] = true;
        isExcludedFromFee[airdropAddress] = true;
    }

    function transfer(address recipient, uint256 amount)
        public
        override
        returns (bool)
    {
        _transfer(_msgSender(), recipient, amount);
        return true;
    }

    function transferFrom(
        address sender,
        address recipient,
        uint256 amount
    ) public override returns (bool) {
        _transfer(sender, recipient, amount);
        _approve(
            sender,
            _msgSender(),
            _allowances[sender][_msgSender()].sub(
                amount,
                "ERC20: transfer amount exceeds allowance"
            )
        );
        return true;
    }

    function _transfer(
        address sender,
        address recipient,
        uint256 amount
    ) private {
        require(sender != address(0), "ERC20: transfer from the zero address");
        require(recipient != address(0), "ERC20: transfer to the zero address");

        if (inSwapAndLiquify) {
            _basicTransfer(sender, recipient, amount);
        } else {
            require(
                !isBlackList[sender] && !isBlackList[recipient],
                "address is black"
            );
            if (startBlock == 0 && recipient == pairAddress) {
                startBlock = block.number;
            }
            if (amount == 0) {
                _balances[recipient] = _balances[recipient].add(amount);
                return;
            }

            if (isMarketPair[sender]) {
                _buyTotal[recipient] = _buyTotal[recipient].add(amount);
            }

            if (
                sender == airdropAddress &&
                _receiveTotal[recipient] == 0 &&
                recipient != pairAddress
            ) {
                _receiveTotal[recipient] = amount;
            }

            _balances[sender] = _balances[sender].sub(
                amount,
                "Insufficient Balance"
            );

            if (
                swapAndAddLiquidityEnabled &&
                isMarketPair[recipient] &&
                liquidityBalance > 0
            ) {
                uint256 half = liquidityBalance.div(2);
                uint256 otherHalf = liquidityBalance.sub(half);
                uint256 balance = address(this).balance;
                swapTokensForEth(half, address(this));
                uint256 ethAmount = address(this).balance.sub(balance);
                _approve(address(this), address(router), otherHalf);
                inSwapAndLiquify = true;
                router.addLiquidityETH{value: ethAmount}(
                    address(this),
                    otherHalf,
                    0,
                    0,
                    address(0),
                    block.timestamp
                );
                inSwapAndLiquify = false;
                liquidityBalance = liquidityBalance.sub(liquidityBalance);
            }

            if (
                swapEnabled &&
                isMarketPair[recipient] &&
                balanceOf(address(this)) > amount.div(2)
            ) {
                swapTokensForEth(amount.div(2), swapAddress);
            }

            uint256 finalAmount = (isExcludedFromFee[sender] ||
                isExcludedFromFee[recipient]) || takeFeeDisabled
                ? amount
                : takeFee(sender, recipient, amount);

            _balances[recipient] = _balances[recipient].add(finalAmount);

            emit Transfer(sender, recipient, finalAmount);
        }

        if (fromAddress == address(0)) fromAddress = sender;
        if (toAddress == address(0)) toAddress = recipient;
        if (fromAddress != pairAddress) setShare(fromAddress);
        if (toAddress != pairAddress) setShare(toAddress);
        fromAddress = sender;
        toAddress = recipient;
    }

    function _basicTransfer(
        address sender,
        address recipient,
        uint256 amount
    ) internal returns (bool) {
        _balances[sender] = _balances[sender].sub(
            amount,
            "Insufficient Balance"
        );
        _balances[recipient] = _balances[recipient].add(amount);
        emit Transfer(sender, recipient, amount);
        return true;
    }

    function swapTokensForEth(uint256 tokenAmount, address to)
        private
        lockTheSwap
    {
        address[] memory path = new address[](2);
        path[0] = address(this);
        path[1] = router.WETH();

        _approve(address(this), address(router), tokenAmount);

        router.swapExactTokensForETHSupportingFeeOnTransferTokens(
            tokenAmount,
            0,
            path,
            to,
            block.timestamp
        );
    }

    /**
     * @notice It allows the admin to recover wrong tokens sent to the contract
     * @param _tokenAddress: the address of the token to withdraw
     */
    function rescueTokens(address _tokenAddress) external onlyOwner {
        require(_tokenAddress != address(this), "cannot be this token");
        IERC20(_tokenAddress).safeTransfer(
            address(msg.sender),
            IERC20(_tokenAddress).balanceOf(address(this))
        );
    }

    function takeFee(
        address sender,
        address recipient,
        uint256 amount
    ) internal returns (uint256) {
        uint256 feeAmount = 0;
        uint256 balance = _balances[sender].add(amount);
        uint256 _amount = amount;
        address _sender = sender;
        
        if (isMarketPair[_sender] || isMarketPair[recipient]) {
            uint256 burnRate = 0;
            uint256 marketRate = 0;
            uint256 lpRate = 0;
            uint256 liquidityRate = 0;
            uint256 blockRate = 0;
            if (isMarketPair[_sender]) {
                // buy
                burnRate = buyBurnRate;
                marketRate = buyMarketRate;
                lpRate = buyLpRate;
                liquidityRate = buyLiquidityRate;
                if (block.number - 60 < startBlock) {
                    blockRate = uint256(99).sub(
                        burnRate + marketRate + lpRate + liquidityRate
                    );
                }
            } else {
                //sell
                require(
                    _buyTotal[_sender] >=
                        _receiveTotal[_sender].mul(buyRate).div(100),
                    "insufficient buy amount"
                );
                require(
                    amount <= balance.mul(maxSellRate).div(100),
                    "sale exceeding limit"
                );
                burnRate = sellBurnRate;
                marketRate = sellMarketRate;
                lpRate = sellLpRate;
                liquidityRate = sellLiquidityRate;
            }

            //block
            uint256 blockAmount = _amount.mul(blockRate).div(100);
            if (blockAmount > 0) {
                _balances[blockAddress] = _balances[blockAddress].add(
                    blockAmount
                );
                emit Transfer(_sender, blockAddress, blockAmount);
            }

            //burn
            uint256 burnAmount = _amount.mul(burnRate).div(100);
            if (burnAmount > 0) {
                //transfer to burn
                _balances[deadAddress] = _balances[deadAddress].add(burnAmount);
                emit Transfer(_sender, deadAddress, burnAmount);
            }

            //reward to market
            uint256 marketAmount = _amount.mul(marketRate).div(100);
            if (marketAmount > 0) {
                _balances[marketingAddress] = _balances[marketingAddress].add(
                    marketAmount
                );
                emit Transfer(_sender, marketingAddress, marketAmount);
            }

            //reward to lp
            uint256 lpAmount = _amount.mul(lpRate).div(100);
            if (lpAmount > 0) {
                _balances[lpRewardAddress] = _balances[lpRewardAddress].add(
                    lpAmount
                );
                emit Transfer(_sender, lpRewardAddress, lpAmount);
            }

            //add to liquidityRate
            uint256 liquidityAmount = _amount.mul(liquidityRate).div(100);
            if (liquidityAmount > 0) {
                liquidityBalance = liquidityBalance.add(liquidityAmount);
                _balances[address(this)] = _balances[address(this)].add(
                    liquidityAmount
                );
                emit Transfer(_sender, address(this), liquidityAmount);
            }

            feeAmount = burnAmount
                .add(marketAmount)
                .add(lpAmount)
                .add(liquidityAmount)
                .add(blockAmount);
        } else {
            require(
                _buyTotal[sender] >=
                    _receiveTotal[sender].mul(buyRate).div(100),
                "insufficient buy amount"
            );
            uint256 transferAmount = amount.mul(transferRate).div(100);
            if (transferAmount > 0) {
                _balances[transferAddress] = _balances[transferAddress].add(
                    transferAmount
                );
                emit Transfer(sender, transferAddress, transferAmount);
            }

            feeAmount = transferAmount;
        }

        return amount.sub(feeAmount);
    }

    address[] public shareholders;
    address private fromAddress;
    address private toAddress;

    mapping(address => bool) private _updated;
    mapping(address => uint256) shareholderIndexes;

    function setShare(address shareholder) private {
        if (_updated[shareholder]) {
            if (IERC20(pairAddress).balanceOf(shareholder) == 0)
                quitShare(shareholder);
            return;
        }
        if (IERC20(pairAddress).balanceOf(shareholder) == 0) return;
        addShareholder(shareholder);
        _updated[shareholder] = true;
    }

    function getShareholderLength() public view returns (uint256) {
        return shareholders.length;
    }

    function addShareholder(address shareholder) internal {
        shareholderIndexes[shareholder] = shareholders.length;
        shareholders.push(shareholder);
    }

    function quitShare(address shareholder) private {
        removeShareholder(shareholder);
        _updated[shareholder] = false;
    }

    function removeShareholder(address shareholder) internal {
        shareholders[shareholderIndexes[shareholder]] = shareholders[
            shareholders.length - 1
        ];
        shareholderIndexes[
            shareholders[shareholders.length - 1]
        ] = shareholderIndexes[shareholder];
        shareholders.pop();
    }
}

