      "content": "// SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;

import "@openzeppelin/contracts/token/ERC20/IERC20.sol";

contract Staking {
    IERC20 public trotelCoin;
    mapping(address => bool) public admins;
    mapping(address => uint256) public stakingBalance;
    mapping(address => uint256) public startTime;

    uint256 public constant stakingDuration = 30 days;
    uint256 public constant annualInterest = 5;

    event Staked(address indexed user, uint256 amount);
    event Withdrawn(address indexed user, uint256 amount);

    constructor(address _trotelCoin) {
        trotelCoin = IERC20(_trotelCoin);
        admins[msg.sender] = true;
    }

    modifier onlyAdmin() {
        require(admins[msg.sender], "Not an admin");
        _;
    }

    function addAdmin(address _newAdmin) external onlyAdmin {
        admins[_newAdmin] = true;
    }

    function removeAdmin(address _admin) external onlyAdmin {
        require(msg.sender != _admin, "Can't remove yourself");
        admins[_admin] = false;
    }

    function stake(uint256 amount) external {
        require(amount > 0, "Amount should be greater than 0");

        require(
            trotelCoin.transferFrom(msg.sender, address(this), amount),
            "Transfer failed"
        );

        stakingBalance[msg.sender] += amount;
        startTime[msg.sender] = block.timestamp;

        emit Staked(msg.sender, amount);
    }

    function calculateRewards(address user) public view returns (uint256) {
        uint256 timeElapsed = block.timestamp - startTime[user];
        uint256 stakedAmount = stakingBalance[user];
        uint256 yearlyReward = (stakedAmount * annualInterest) / 100;
        return (yearlyReward * timeElapsed) / (stakingDuration * 365 days);
    }

    function withdraw() external {
        require(
            block.timestamp >= startTime[msg.sender] + stakingDuration,
            "Staking duration not completed"
        );

        uint256 reward = calculateRewards(msg.sender);
        uint256 totalAmount = stakingBalance[msg.sender] + reward;

        require(totalAmount > 0, "Nothing to withdraw");

        delete startTime[msg.sender];
        delete stakingBalance[msg.sender];

        trotelCoin.transfer(msg.sender, totalAmount);

        emit Withdrawn(msg.sender, totalAmount);
    }
}
