    uint256 public LIMIT_ClAIM_SECOND;


    constructor () ERC20("XDU", "XDU") 
    {   
        LIMIT_ClAIM_SECOND = 60*60;
    }

    receive() external payable {

  	}

    function setLIMIT_ClAIM_SECOND(uint256 _LIMIT_ClAIM_SECOND) external onlyOwner  whenNotPaused{ 
        LIMIT_ClAIM_SECOND = _LIMIT_ClAIM_SECOND;
    }

    function setadmin(address _admin) external onlyOwner  whenNotPaused{ 
        admin = _admin;
    }

    function claim(uint256 amount, uint8 _v, bytes32 _r, bytes32 _s)   external whenNotPaused {
        require(lastTimeClaim[msg.sender] + LIMIT_ClAIM_SECOND < block.timestamp, "limit claim time");
        string memory msgSender = Strings.toHexString(uint160(msg.sender), 20);
        string memory signText = string.concat(msgSender, "_", Strings.toString(amount), "_", Strings.toString(claimTimes[msg.sender]));
        signText = _toLower(signText);
        bytes memory prefix = "\x19Ethereum Signed Message:\n32";
        bytes32 _hashedMessage = getHashValue(signText);

        bytes32 prefixedHashMessage = keccak256(abi.encodePacked(prefix, _hashedMessage));
        address signer = ecrecover(prefixedHashMessage, _v, _r, _s);
        require(signer == admin, "sign msg not correct ");
        lastTimeClaim[msg.sender] = block.timestamp;
        claimTimes[msg.sender]++;
        _mint(msg.sender, amount);
    }

    function getHashValue(string memory signText) internal pure returns(bytes32){
        return keccak256(abi.encodePacked(signText));
    }

    function _toLower(string memory str) internal pure returns (string memory) {
        bytes memory bStr = bytes(str);
        bytes memory bLower = new bytes(bStr.length);
        for (uint i = 0; i < bStr.length; i++) {
            // Uppercase character...
            if ((uint8(bStr[i]) >= 65) && (uint8(bStr[i]) <= 90)) {
                // So we add 32 to make it lowercase
                bLower[i] = bytes1(uint8(bStr[i]) + 32);
            } else {
                bLower[i] = bStr[i];
            }
        }
        return string(bLower);
    }

}
