      "content": "// SPDX-License-Identifier: MIT
pragma solidity ^0.8.7;

import "@openzeppelin/contracts/token/ERC721/extensions/IERC721Enumerable.sol";
import "./ILqtCollection.sol";
import "@openzeppelin/contracts/access/Ownable.sol";

import "@chainlink/contracts/src/v0.8/VRFConsumerBase.sol";

contract MomCorp is Ownable, VRFConsumerBase {
    /**
      * @dev Emitted when changing `unpackStatus`.
      */
    event UnpackSwitch(bool status);

    /**
      * @dev Emitted when creating unpack request `requestId` for `user` and burning `amount` of containers `containerId`.
      */
    event UnpackContainers(address indexed user, uint256 indexed containerId, uint256 amount, bytes32 indexed requestId);

    /**
      * @dev Emitted when container unpacks with `robotId` for `requestId` of `user`.
      */
    event UnpackRobot(address indexed user, bytes32 indexed requestId, uint256 indexed robotId);


    struct Request {
        address user;
        uint8 amount;
        bool fulfilled;
    }

    bytes32 internal keyHash;
    uint256 internal fee;

    ILqtCollection public containers;
    IERC721Enumerable public robots;
    address public losMachines;

    bool public unpackStatus = false;

    mapping(bytes32 => Request) internal requests;

    constructor(ILqtCollection _containers, IERC721Enumerable _robots, address _losMachines)
    VRFConsumerBase(
        0x747973a5A2a4Ae1D3a8fDF5479f1514F65Db9C31, // VRF Coordinator
        0x404460C6A5EdE2D891e8297795264fDe62ADBB75  // LINK Token
    )
    {
        keyHash = 0xc251acd21ec4fb7f31bb8868288bfdbaeb4fbfec2df3735ddbd4f7dc8d60103c;
        fee = 0.2 * 10 ** 18; // 0.2 LINK (Varies by network)

        containers = _containers;
        robots = _robots;
        losMachines = _losMachines;
    }

    /**
      * @dev Creates open request for `amount` of `containerId`.
      *
      * @param containerId id of container
      * @param amount amount of containers
      */
    function openContainer(uint256 containerId, uint8 amount) public returns (bytes32 requestId) {
        require(unpackStatus, "Please wait for announce");
        require(LINK.balanceOf(address(this)) >= fee, "Not enough LINK");

        uint8 total = 0;
        if (containerId == 3126816) {
            total = amount;
        }
        if (containerId == 516156) {
            total = amount * 3;
        }
        if (containerId == 5363370) {
            total = amount * 6;
        }

        require(total > 0 && total <= 6, "Wrong amount of containers");
        require(containers.balanceOf(msg.sender, containerId) >= amount, "Insufficient containers balance");
        require(robots.balanceOf(losMachines) >= total, "LosMachines balance exceeded");

        containers.burn(msg.sender, containerId, amount);

        requestId = requestRandomness(keyHash, fee);

        requests[requestId] = Request(
            msg.sender,
            total,
            false
        );

        emit UnpackContainers(msg.sender, containerId, amount, requestId);
    }

    /**
      * @dev Callback for ChainLink VRF.
      *
      * @param requestId id of request
      * @param randomness random result
      */
    function fulfillRandomness(bytes32 requestId, uint256 randomness) internal override {
        Request storage request = requests[requestId];
        require(!request.fulfilled, "Requiest already fulfilled");
        uint256 losMachinesBalance = robots.balanceOf(losMachines);
        for (uint8 i = 0; i < request.amount;) {
            uint256 randomIndex = uint256(keccak256(abi.encode(randomness, i))) % losMachinesBalance;
            uint256 tokenId = robots.tokenOfOwnerByIndex(losMachines, randomIndex);

            unchecked {
                losMachinesBalance--;
                i++;
            }

            robots.safeTransferFrom(losMachines, request.user, tokenId);
            emit UnpackRobot(request.user, requestId, tokenId);
        }

        request.fulfilled = true;
    }

    /**
      * @dev Returns request by `requestId`
      *
      * @param requestId id of request
      */
    function getRequest(bytes32 requestId) external view returns (Request memory) {
        return requests[requestId];
    }

    /**
      * @dev Set fulfilled to true for request with `requestId`
      *
      * @param requestId id of request
      */
    function emergencyFulfillRequest (bytes32 oldRequestId) external onlyOwner returns (bytes32 requestId) {
        require(unpackStatus, "Please wait for announce");
        require(LINK.balanceOf(address(this)) >= fee, "Not enough LINK");

        Request storage request = requests[oldRequestId];

        require(!request.fulfilled, "Requiest already fulfilled");
        request.fulfilled = true;

        requestId = requestRandomness(keyHash, fee);

        requests[requestId] = Request(
            request.user,
            request.amount,
            false
        );

        emit UnpackContainers(request.user, 0, request.amount, requestId);
    }

    /**
      * @dev Switch status of unpack
      */
    function switchUnpack() external onlyOwner {
        unpackStatus = !unpackStatus;
        emit UnpackSwitch(unpackStatus);
    }

    /**
      * @dev Withdraw unused LINK tokens
      */
    function withdrawLink() external onlyOwner {
        require(LINK.transfer(msg.sender, LINK.balanceOf(address(this))), "Unable to transfer");
    }
}"
