    "contracts/GeneralTypes.sol": {
      "content": "// SPDX-License-Identifier: UNLICENSED
pragma solidity ^0.8.0;

enum FundType {
    RegularFunds,
    SpecialFunds
}

enum FundState {
    FUNDING,
    RUNNING,
    CLOSED,
    CANCELLED
}

enum BlockSecurityPolicyType {
    NONE,
    FULL,
    STAKING,
    BALANCE
}
"
