    "contracts/InsuranceEvaluator.sol": {
      "content": "// SPDX-License-Identifier: UNLICENSED
pragma solidity ^0.8.0;

import "./GeneralTypes.sol";

contract InsuranceEvaluator {

    constructor(){
    }

    modifier onlyPositiveAmountAvailableErrorMessage(string memory errorMsg, uint256 _tokenAmount) {
        require(_tokenAmount > 0, errorMsg);
        _;
    }

    modifier onlyAvailableInsurancePercentErrorMessage(string memory errorMsg, uint256 _insurancePercent) {
        require(_insurancePercent == 50 || _insurancePercent == 100 , errorMsg);
        _;
    }

    function estimateInsurancePolicyAmount(FundType _fundType, uint256 _tokenAmount, uint256 _insurancePercent)
        public
        pure
        onlyPositiveAmountAvailableErrorMessage("Token amount is not correct", _tokenAmount)
        onlyAvailableInsurancePercentErrorMessage("Insurance percent (50% or 100%) is not correct", _insurancePercent)
        returns (uint256) {

        if ( _fundType == FundType.RegularFunds && _insurancePercent == 100) {
            return calculatePercent( _tokenAmount, 10 );
        }

        if (_fundType == FundType.SpecialFunds && _insurancePercent == 50) {
            return calculatePercent( _tokenAmount, 10 );
        }

        if (_fundType == FundType.SpecialFunds && _insurancePercent == 100) {
            return calculatePercent( _tokenAmount, 20 );
        }

        return 0;
    }

    function calculatePercent(uint256 _tokenAmount, uint256 _insurancePercent) private pure returns (uint256) {
        return ( _tokenAmount * _insurancePercent ) / 100;
    }
}
"
    },
    "contracts/SCAToken.sol": {
      "content": "// SPDX-License-Identifier: MIT
pragma solidity 0.8.18;

import "./GeneralTypes.sol";
import "@openzeppelin/contracts/token/ERC20/ERC20.sol";
import "@openzeppelin/contracts/access/AccessControl.sol";
import "@openzeppelin/contracts/utils/structs/EnumerableSet.sol";

/// @title SCAToken.sol Token Contract
/// @notice A simple ERC20 token contract with minting and staking transfer functionality.
contract SCAToken is ERC20, AccessControl {
    struct Transaction {
        uint256 timestamp;
        uint256 balance;
    }
    struct LoyaltyBonus {
        uint256 startDate;
        uint256 endDate;
        uint256 loyaltyBonusAmount;
    }
    bytes32 private constant MINTER_ROLE = keccak256("MINTER_ROLE");
    bytes32 public constant STAKING_ROLE = keccak256("STAKING_ROLE");

    mapping(address => uint256) private bonusAmount;
    using EnumerableSet for EnumerableSet.AddressSet;
    EnumerableSet.AddressSet private bonusWhitelist;
    mapping(address => BlockSecurityPolicyType) private blockedUsers;
    mapping(address => Transaction[]) private transactions;
    mapping(address => LoyaltyBonus[]) private loyaltyBonuses;

    /// @notice Initializes the token with the name "SCAToken.sol" and symbol "SCAToken.sol".
    /// Grants the DEFAULT_ADMIN_ROLE to the contract deployer.
    constructor() ERC20("SCA", "SCA") {
        _grantRole(DEFAULT_ADMIN_ROLE, msg.sender);
        _setRoleAdmin(MINTER_ROLE, DEFAULT_ADMIN_ROLE);
        _setRoleAdmin(STAKING_ROLE, DEFAULT_ADMIN_ROLE);
        _mint(msg.sender, 50000000 ether);
    }

    /// @notice Mints new tokens to the specified address.
    /// @dev Can only be called by addresses with the MINTER_ROLE.
    /// @param to The address to mint tokens to.
    /// @param amount The amount of tokens to mint.
    function mint(address to, uint256 amount) public onlyRole(MINTER_ROLE) {
        require(
            blockedUsersOf(msg.sender) != BlockSecurityPolicyType.FULL &&
            blockedUsersOf(msg.sender) != BlockSecurityPolicyType.STAKING &&
            blockedUsersOf(msg.sender) != BlockSecurityPolicyType.BALANCE, "SCA: This user is blocked");

        uint256 currentBalance = getCurrentBalance(to);
        uint256 timestamp = block.timestamp;
        transactions[to].push(Transaction(timestamp, currentBalance + amount));

        _mint(to, amount);
    }

    // mint bonus tokens to the specified address.
    function mintBonus(
        address _to,
        uint256 _amount
    ) public onlyRole(MINTER_ROLE) {
        _mint(_to, _amount);
        bonusAmount[_to] += _amount;
    }

    /// @notice Returns the amount of bonus tokens that the specified address has.
    /// @param _account The address to check the bonus tokens of.
    /// @return The amount of bonus tokens.
    function bonusOf(address _account) public view returns (uint256) {
        return bonusAmount[_account];
    }

    // @notice Minus the amount of bonus tokens that the specified address has.
    // @param _account The address to check the bonus tokens of.
    // @param _amount The amount of tokens to remove from bonusAmount.
    // @return The amount of bonus tokens.
    function minusBonus(address _account, uint256 _amount) public onlyRole(MINTER_ROLE) {
        bonusAmount[_account] -= _amount;
    }

    // @notice Remove the amount of bonus tokens that the specified address has.
    // @param _account The address to check the bonus tokens of.
    // @return The amount of bonus tokens.
    function removeBonus(address _account) public onlyRole(MINTER_ROLE) {
        delete bonusAmount[_account];
    }

    /// @notice Adds an address to the bonus whitelist.
    /// @dev Can only be called by addresses with the MINTER_ROLE.
    /// @param _account The address to add to the bonus whitelist.
    function addBonusWhitelist(address _account) public onlyRole(DEFAULT_ADMIN_ROLE) {
        bonusWhitelist.add(_account);
    }

    /// @notice Removes an address from the bonus whitelist.
    /// @dev Can only be called by addresses with the MINTER_ROLE.
    /// @param _account The address to remove from the bonus whitelist.
    function removeBonusWhitelist(
        address _account
    ) public onlyRole(MINTER_ROLE) {
        bonusWhitelist.remove(_account);
    }

    /// @notice Transfers tokens between two addresses for staking purposes.
    /// @dev Can only be called by addresses with the STAKING_ROLE.
    /// @param from The address to transfer tokens from.
    /// @param to The address to transfer tokens to.
    /// @param amount The amount of tokens to transfer.
    function stakingTransfer(
        address from,
        address to,
        uint256 amount
    ) public onlyRole(STAKING_ROLE) {
        require(blockedUsersOf(msg.sender) != BlockSecurityPolicyType.STAKING, "SCA: Stacking is not allowed for this user");
        require(amount > 0, "SCA: transfer amount should be greater than 0");
        _transfer(from, to, amount);
        uint256 currentBalance = getCurrentBalance(from);
        uint256 timestamp = block.timestamp;
        transactions[from].push(Transaction(timestamp, currentBalance - amount));
    }

    /// @notice Hook that is called before any token transfer.
    /// @dev Checks if the destination address is in the whitelist or the sender's balance is greater than or equal to their bonus amount.
    /// @param _from The address tokens are being transferred from.
    /// @param _to The address tokens are being transferred to.
    /// @param _amount The amount of tokens being transferred.
    function _beforeTokenTransfer(
        address _from,
        address _to,
        uint256 _amount
    ) internal virtual override {
        super._beforeTokenTransfer(_from, _to, _amount);
        if (_from == address(0)) {
            return;
        }

        if (bonusWhitelist.contains(_to)) {
            return;
        }

        if (balanceOf(_from) >= _amount) {
            // avoid overflow. transfer more than you have will be handler elsewhere
            require(
                balanceOf(_from) - _amount >= bonusAmount[_from],
                "SCA: Sender's remaining balance must be greater than or equal to their bonus amount"
            );
        }
    }

    function burn(
        address _account,
        uint256 _amount
    ) public onlyRole(MINTER_ROLE) {
        require(
            blockedUsersOf(msg.sender) != BlockSecurityPolicyType.FULL &&
            blockedUsersOf(msg.sender) != BlockSecurityPolicyType.STAKING &&
            blockedUsersOf(msg.sender) != BlockSecurityPolicyType.BALANCE, "SCA: This user is blocked");

        _burn(_account, _amount);

        uint256 currentBalance = getCurrentBalance(_account);
        uint256 timestamp = block.timestamp;
        transactions[_account].push(Transaction(timestamp, currentBalance - _amount));
    }

    /// @notice Returns the type of block that the specified address has.
    /// @param _account The address to check in the block user list.
    /// @return The type of block.
    function blockedUsersOf(address _account) public view returns (BlockSecurityPolicyType) {
        return blockedUsers[_account];
    }

    /// @notice Adds an address to the block user list.
    /// @dev Can only be called by addresses with the DEFAULT_ADMIN_ROLE.
    /// @param _account The address to add to the block user list.
    /// @param userBlockType The type of block to add to the block user list.
    function addBlockedUser(address _account, BlockSecurityPolicyType userBlockType) public onlyRole(DEFAULT_ADMIN_ROLE) {
        blockedUsers[_account] = userBlockType;
    }

    /// @notice Removes an address from the block user list.
    /// @dev Can only be called by addresses with the DEFAULT_ADMIN_ROLE.
    /// @param _account The address to remove from the block user list.
    function removeBlockList(address _account) public onlyRole(DEFAULT_ADMIN_ROLE) {
        delete blockedUsers[_account];
    }

    function getCurrentBalance(address user) public view returns (uint256) {
        Transaction[] storage userTransactions = transactions[user];
        if (userTransactions.length == 0) {
            return 0;
        }
        return userTransactions[userTransactions.length - 1].balance;
    }

    function getCurrentTimeStamp() public view returns (uint256) {
        return block.timestamp;
    }

    function getLowestBalance(address user, uint256 startDate, uint256 endDate) public view returns (uint256) {
        Transaction[] storage userTransactions = transactions[user];
        if (userTransactions.length == 0) {
            return 0;
        }
        uint256 lowestBalanceInPeriod = 0;
        uint256 previousBalance = 0;
        bool isFirstValueInPeriod = true;
        for (uint256 i = 0; i < userTransactions.length; i++) {
            Transaction storage transaction = userTransactions[i];
            if (transaction.timestamp <= startDate) {
                previousBalance = transaction.balance;
            } else if (transaction.timestamp > startDate && transaction.timestamp <= endDate) {
                if (isFirstValueInPeriod) {
                    lowestBalanceInPeriod = transaction.balance;
                    isFirstValueInPeriod = false;
                } else {
                    lowestBalanceInPeriod = transaction.balance < lowestBalanceInPeriod ? transaction.balance : lowestBalanceInPeriod;
                }

            }
        }
        return (lowestBalanceInPeriod < previousBalance) && !isFirstValueInPeriod ? lowestBalanceInPeriod : previousBalance;
    }

    function calculateAndSetLoyaltyBonus(address user, uint256 startDate, uint256 endDate, uint256 loyaltyPercent) external {
        uint256 lowestBalance = getLowestBalance(user, startDate, endDate);
        uint256 loyaltyBonusAmount = (lowestBalance * loyaltyPercent) / 100; // loyaltyPercent% from the lowest balance limit

        loyaltyBonuses[user].push(LoyaltyBonus(startDate, endDate, loyaltyBonusAmount));
        mintBonus(user, loyaltyBonusAmount);
    }

    function getLoyaltyBonuses(address user) public view returns (LoyaltyBonus[] memory) {
        return loyaltyBonuses[user];
    }
}
"
    },
    "contracts/StakingV2.sol": {
      "content": "// SPDX-License-Identifier: UNLICENSED
pragma solidity 0.8.18;

import "@openzeppelin/contracts-upgradeable/access/AccessControlUpgradeable.sol";
import "@openzeppelin/contracts-upgradeable/proxy/utils/Initializable.sol";
import "@openzeppelin/contracts-upgradeable/utils/structs/EnumerableSetUpgradeable.sol";
import "./SCAToken.sol";
import "./GeneralTypes.sol";
import "./InsuranceEvaluator.sol";

contract StakingV2 is Initializable, AccessControlUpgradeable {
    struct Stake {
        uint256 amount;
        uint256 insuranceAmount;
        uint256 withdrawFreq;
        uint256 lastWithdrawAt;
        uint256 totalWithdrawals;
    }

    struct StakeView {
        uint256 fundId;
        FundType fundType;
        uint256 amount;
        uint256 insuranceAmount;
        FundState state;
        uint256 interestRate;
        uint256 withdrawFreq;
        uint256 lastWithdrawAt;
        uint256 totalWithdrawals;
        uint256 availbleToWithdraw;
    }

    struct Fund {
        uint256 volume;
        uint256 startTime;
        uint256 duration;
        FundType fundType;
        uint256 defaultInterestRate;
        mapping(address => Stake) stakers;
        EnumerableSetUpgradeable.AddressSet addressSet;
        bool cancelled;
    }

    event FundCreated(
        uint256 id,
        FundType fundType,
        uint256 volume,
        uint256 startTime,
        uint256 duration,
        uint256 defaultInterestRate,
        uint256[] earlyWithdrawFreqs,
        uint256[] earlyWithdrawRates
    );

    struct FundView {
        uint256 id;
        uint256 volume;
        uint256 startTime;
        uint256 duration;
        uint256 defaultInterestRate;
        uint256 remainingAmount;
        FundState state;
        uint256[] earlyWithdrawFreqs;
        uint256[] earlyWithdrawRates;
        FundType fundType;
    }

    event Staked(
        uint256 indexed fundId,
        address indexed staker,
        uint256 amount,
        uint256 insuranceAmount,
        uint256 withdrawFreq
    );

    event Unstaked(
        uint256 indexed fundId,
        address indexed staker,
        uint256 amount,
        uint256 profit
    );

    using EnumerableSetUpgradeable for EnumerableSetUpgradeable.AddressSet;
    using EnumerableSetUpgradeable for EnumerableSetUpgradeable.UintSet;

    Fund[] private funds;
    uint256 public fundsCount;
    SCAToken private scaToken;
    mapping(uint256 => mapping(uint256 => uint256))
        private fundEarlyWithdrawRates;
    mapping(uint256 => EnumerableSetUpgradeable.UintSet)
        private fundEarlyWithdrawFreqs;

    InsuranceEvaluator insuranceEvaluator;

    function initialize(address scaTokenAddress) public initializer {
        scaToken = SCAToken(scaTokenAddress);
        insuranceEvaluator = new InsuranceEvaluator();
        __AccessControl_init(); // Initialize the AccessControl contract
        _setupRole(DEFAULT_ADMIN_ROLE, msg.sender);
    }

    function createFund(
        FundType _fundType,
        uint256 _volume,
        uint256 _startTime,
        uint256 _duration,
        uint256 _defaultInterestRate
    ) external onlyRole(DEFAULT_ADMIN_ROLE) returns (uint256) {
        return
            createFund(
                _fundType,
                _volume,
                _startTime,
                _duration,
                _defaultInterestRate,
                new uint256[](0),
                new uint256[](0)
            );
    }


    modifier onlyPositiveVolumeAvailableErrorMessage(string memory errorMsg, uint256 _volume) {
        require(_volume > 0, errorMsg);
        _;
    }

    modifier onlyCorrectStartTimeValueErrorMessage(string memory errorMsg, uint256 _startTime) {
        require(_startTime > block.timestamp, errorMsg);
        _;
    }

    modifier onlyPositiveDurationErrorMessage(string memory errorMsg, uint256 _duration) {
        require(_duration > 0, errorMsg);
        _;
    }

    modifier onlyPositiveInterestRateErrorMessage(string memory errorMsg, uint256 _defaultInterestRate) {
        require( _defaultInterestRate > 0, errorMsg );
        _;
    }

    modifier onlyTheSameArraysSizeRateErrorMessage(string memory errorMsg, uint256[] memory earlyWithdrawFreq, uint256[] memory earlyWithdrawRates) {
        require( earlyWithdrawFreq.length == earlyWithdrawRates.length, errorMsg );
        _;
    }

    function createFund(
        FundType _fundType,
        uint256 _volume,
        uint256 _startTime,
        uint256 _duration,
        uint256 _defaultInterestRate,
        uint256[] memory earlyWithdrawFreq,
        uint256[] memory earlyWithdrawRates
    ) public
    onlyRole(DEFAULT_ADMIN_ROLE)
    onlyPositiveVolumeAvailableErrorMessage( "Volume must be greater than 0", _volume)
    onlyCorrectStartTimeValueErrorMessage( "Start time must be in the future", _startTime)
    onlyPositiveDurationErrorMessage( "Duration must be greater than 0", _duration)
    onlyPositiveInterestRateErrorMessage( "Interest rate must be greater than 0", _defaultInterestRate )
    onlyTheSameArraysSizeRateErrorMessage( "InvalidEarlyWithdrawRates", earlyWithdrawFreq, earlyWithdrawRates )
    returns (uint256) {
        funds.push();
        uint256 fundId = fundsCount;
        Fund storage fund = funds[fundId];
        fundsCount++;
        fund.fundType = _fundType;
        fund.volume = _volume;
        fund.startTime = _startTime;
        fund.duration = _duration;
        fund.defaultInterestRate = _defaultInterestRate;

        for (uint i = 0; i < earlyWithdrawFreq.length; i++) {
            fundEarlyWithdrawRates[fundId][
                earlyWithdrawFreq[i]
            ] = earlyWithdrawRates[i];
            fundEarlyWithdrawFreqs[fundId].add(earlyWithdrawFreq[i]);
        }

        emit FundCreated(
            fundId,
            _fundType,
            _volume,
            _startTime,
            _duration,
            _defaultInterestRate,
            earlyWithdrawFreq,
            earlyWithdrawRates
        );

        return fundsCount;
    }

    event FundUpdated(
        uint256 id,
        uint256 volume,
        uint256 startTime,
        uint256 duration
    );

    function updateFund(
        uint256 fundId,
        uint256 _volume,
        uint256 _startTime,
        uint256 _duration
    ) public onlyRole(DEFAULT_ADMIN_ROLE) {
        require(fundId < fundsCount, "InvalidFundId");
        Fund storage fund = funds[fundId];
        require(_startTime > fund.startTime, "InvalidStartTime");
        require(_duration > 0, "InvalidDuration");
        fund.startTime = _startTime;
        fund.volume = _volume;
        fund.duration = _duration;

        emit FundUpdated(fundId, _volume, _startTime, _duration);
    }

    function cancelFund(
        uint256 fundId
    ) external onlyRole(DEFAULT_ADMIN_ROLE) returns (uint256) {
        // TODO: Run any checks?

        require(fundId < fundsCount, "FundNotFound");
        Fund storage fund = funds[fundId];
        fund.cancelled = true;
        uint256 amount;
        uint256 userCount = fund.addressSet.length();
        for (uint256 i = 0; i < userCount; i++) {
            address stakerAddress = fund.addressSet.at(i);
            uint256 _amount = fund.stakers[stakerAddress].amount;
            amount += _amount;
            scaToken.transferFrom(address(this), msg.sender, _amount);
            fund.stakers[stakerAddress].amount = 0;
        }

        return amount;
    }

    function totalStakedAmount(uint256 fundId) private view returns (uint256) {
        Fund storage fund = funds[fundId];
        uint256 stakersCount = fund.addressSet.length();
        uint256 remainingAmount = fund.volume;
        for (uint i = 0; i < stakersCount; i++) {
            address stakerAddress = fund.addressSet.at(i);
            remainingAmount -= fund.stakers[stakerAddress].amount;
        }

        return remainingAmount;
    }

    function getFundState(Fund storage fund) private view returns (FundState) {
        return
            fund.cancelled
                ? FundState.CANCELLED
                : block.timestamp >= fund.startTime
                ? block.timestamp >= fund.startTime + fund.duration
                    ? FundState.CLOSED
                    : FundState.RUNNING
                : FundState.FUNDING;
    }

    function getFund(uint256 id) external view returns (FundView memory) {
        require(id < fundsCount, "FundNotFound");

        Fund storage fund = funds[id];

        uint256 remainingAmount = totalStakedAmount(id);
        uint256[] memory earlyWithdrawRates = new uint256[](
            fundEarlyWithdrawFreqs[id].length()
        );
        uint256[] memory earlyWithdrawFreqs = new uint256[](
            fundEarlyWithdrawFreqs[id].length()
        );

        for (uint i = 0; i < fundEarlyWithdrawFreqs[id].length(); i++) {
            earlyWithdrawFreqs[i] = fundEarlyWithdrawFreqs[id].at(i);
            earlyWithdrawRates[i] = fundEarlyWithdrawRates[id][
                earlyWithdrawFreqs[i]
            ];
        }

        return
            FundView({
                id: id,
                volume: fund.volume,
                startTime: fund.startTime,
                duration: fund.duration,
                defaultInterestRate: fund.defaultInterestRate,
                remainingAmount: remainingAmount,
                state: getFundState(fund),
                earlyWithdrawFreqs: earlyWithdrawFreqs,
                earlyWithdrawRates: earlyWithdrawRates,
                fundType: fund.fundType
        });
    }

    function stake(uint256 _fundId, uint256 _amount, uint256 _insurancePercent) external {
        stake(_fundId, _amount, _insurancePercent, 0);
    }

    function stake(
        uint256 _fundId,
        uint256 _amount,
        uint256 _insurancePercent,
        uint256 _withdrawFreq
    ) public {
        require(scaToken.blockedUsersOf(msg.sender) != BlockSecurityPolicyType.STAKING, "StackingIsNotAllowedForThisUser");
        require(_fundId < fundsCount, "FundNotFound");
        require(_amount > 0, "InvalidAmount");
        require(
            _withdrawFreq == 0 ||
                fundEarlyWithdrawFreqs[_fundId].contains(_withdrawFreq),
            "InvalidWithdrawFrequency"
        );

        uint256 remainingAmount = totalStakedAmount(_fundId);
        require(remainingAmount >= _amount, "FundFull");

        Fund storage fund = funds[_fundId];
        require(!fund.cancelled, "FundCancelled");
        require(fund.startTime > block.timestamp, "FundStartedOrCancelled");

        uint256 insuranceAmount = insuranceEvaluator.estimateInsurancePolicyAmount(fund.fundType, _amount, _insurancePercent );
        uint256 _stakeAmount = _amount - insuranceAmount;

        uint256 bonusAmount = scaToken.bonusOf(msg.sender);
        if (_amount < bonusAmount) {
            scaToken.minusBonus(msg.sender, _amount);
        } else {
            scaToken.removeBonus(msg.sender);
        }

        fund.addressSet.add(msg.sender);

        if (fund.stakers[msg.sender].amount == 0) {
            fund.stakers[msg.sender].withdrawFreq = _withdrawFreq;
        }

        fund.stakers[msg.sender].amount += _stakeAmount;
        fund.stakers[msg.sender].insuranceAmount += insuranceAmount;

        scaToken.stakingTransfer(msg.sender, address(this), _amount);

        emit Staked(_fundId, msg.sender, _stakeAmount, insuranceAmount, _withdrawFreq);
    }

    function getStakeInFund(
        uint256 _fundId,
        address _userAddress
    ) external view returns (StakeView memory) {
        require(_fundId < fundsCount, "FundNotFound");

        Fund storage fund = funds[_fundId];
        Stake memory _stake = fund.stakers[_userAddress];
        uint256 _profitSinceStart = profitSinceStart(_fundId, _userAddress);

        uint256 interestRate = fundEarlyWithdrawRates[_fundId][
            _stake.withdrawFreq
        ];
        if (interestRate == 0) {
            interestRate = fund.defaultInterestRate;
        }

        return
            StakeView({
                fundId: _fundId,
                fundType : fund.fundType,
                amount: _stake.amount,
                insuranceAmount: _stake.insuranceAmount,
                state: getFundState(fund),
                interestRate: interestRate,
                withdrawFreq: _stake.withdrawFreq,
                lastWithdrawAt: _stake.lastWithdrawAt,
                totalWithdrawals: _stake.totalWithdrawals,
                availbleToWithdraw: _profitSinceStart > _stake.totalWithdrawals
                    ? _profitSinceStart - _stake.totalWithdrawals
                    : 0
            });
    }

    function getStakes(
        address _userAddress
    ) external view returns (StakeView[] memory) {
        require(_userAddress != address(0), "InvalidAddress");

        if (_userAddress == address(0)) {
            _userAddress = msg.sender;
        }

        StakeView[] memory _stakes = new StakeView[](funds.length);
        uint256 counter;
        for (uint i = 0; i < funds.length; i++) {
            Fund storage fund = funds[i];
            Stake memory _stake = fund.stakers[_userAddress];
            uint256 _profitSinceStart = profitSinceStart(i, _userAddress);

            if (fund.stakers[_userAddress].amount > 0) {
                _stakes[counter++] = StakeView({
                    fundId: i,
                    fundType : fund.fundType,
                    amount: _stake.amount,
                    insuranceAmount: 0,
                    state: getFundState(fund),
                    interestRate: fundEarlyWithdrawRates[i][
                        _stake.withdrawFreq
                    ],
                    withdrawFreq: _stake.withdrawFreq,
                    lastWithdrawAt: _stake.lastWithdrawAt,
                    totalWithdrawals: _stake.totalWithdrawals,
                    availbleToWithdraw: _profitSinceStart >
                        _stake.totalWithdrawals
                        ? _profitSinceStart - _stake.totalWithdrawals
                        : 0
                });
            }
        }

        uint decreaseBy = funds.length - counter;
        assembly {
            mstore(_stakes, sub(mload(_stakes), decreaseBy))
        }

        return _stakes;
    }

    function unstake(uint256 _fundId) external returns (uint256) {
        require(scaToken.blockedUsersOf(msg.sender) != BlockSecurityPolicyType.STAKING, "UnstackingIsNotAllowedForThisUser");
        require(_fundId < fundsCount, "FundNotFound");

        Fund storage fund = funds[_fundId];
        require(
            block.timestamp > fund.startTime + fund.duration,
            "FundNotStarted"
        );
        require(!fund.cancelled, "FundCnacelled");

        require(fund.addressSet.contains(msg.sender), "NotAStaker");

        Stake memory _stake = fund.stakers[msg.sender];
        uint256 _amount = _stake.amount;
        uint256 rate = fund.defaultInterestRate;
        if (_stake.withdrawFreq > 0) {
            rate = fundEarlyWithdrawRates[_fundId][_stake.withdrawFreq];
        }
        uint256 profit = (_amount * rate) / 10_000 - _stake.totalWithdrawals;
        scaToken.stakingTransfer(address(this), msg.sender, _amount);
        scaToken.mint(msg.sender, profit);

        fund.stakers[msg.sender].amount = 0;

        emit Unstaked(_fundId, msg.sender, _amount, profit);

        return _amount + profit;
    }

    function profitSinceStart(
        uint256 _fundId,
        address _staker
    ) private view returns (uint256) {
        Fund storage fund = funds[_fundId];
        Stake storage _stake = fund.stakers[_staker];

        if (_stake.withdrawFreq == 0 || block.timestamp <= fund.startTime) {
            return 0;
        }

        uint256 totalProfit = (_stake.amount *
            fundEarlyWithdrawRates[_fundId][_stake.withdrawFreq]) / 10_000;

        uint256 _now = block.timestamp;
        if (_now > fund.startTime + fund.duration) {
            _now = fund.startTime + fund.duration;
        }
        uint256 intervalsSinceStart = (_now - fund.startTime) /
            _stake.withdrawFreq; // rounds down to full intervals

        return
            (intervalsSinceStart * _stake.withdrawFreq * totalProfit) /
            fund.duration;
    }

    function withdrawProfit(uint256 _fundId) external returns (uint256) {
        require(_fundId < fundsCount, "FundNotFound");

        Fund storage fund = funds[_fundId];
        require(block.timestamp > fund.startTime, "FundNotStarted");
        require(!fund.cancelled, "FundCnacelled");

        require(fund.addressSet.contains(msg.sender), "NotAStaker");

        Stake storage _stake = fund.stakers[msg.sender];
        require(_stake.withdrawFreq > 0, "ProfitAtMaturity");

        uint256 _profitSinceStart = profitSinceStart(_fundId, msg.sender);

        require(
            _profitSinceStart > _stake.totalWithdrawals,
            "NoProfitToWithdraw"
        );

        uint256 profit = _profitSinceStart - _stake.totalWithdrawals;

        _stake.totalWithdrawals = _profitSinceStart;
        scaToken.mint(msg.sender, profit);

        emit Unstaked(_fundId, msg.sender, 0, profit);

        return profit;
    }
}
"
    }
