// File contracts/bridge/CDHBinanceNFTReceiver.sol
pragma solidity ^0.8.0;



interface IBinanceToken {
    function safeTransferFrom(
        address from,
        address to,
        uint256 tokenId,
        bytes memory data
    ) external;

    function ownerOf(uint256 tokenId) external returns (address);
}

/*
 * @title CDHBinanceNFTReceiver
 * @author @sagarduwal
 * @dev This contract is used to receive NFTs from Binance.
 */
contract CDHBinanceNFTReceiver is Ownable, Pausable, ReentrancyGuard {
    // EOA to receive the bridged tokens
    address public receiverAddress;

    // whitelisted contract addresses from which the bridged tokens is received
    mapping(address => bool) public whiteListedContracts;

    // Interface for the token contract
    IBinanceToken nftInterface;

    /*
     * Events emitted by the contract for operations
     */
    event SetWhitelistAddress(address indexed contractaddr, bool whitelistValue);
    event SetReceiverAddress(address indexed oldReceiverAddress, address receiverAddress);
    event NFTReceived(address indexed contractaddr, address indexed from, uint256 tokenId);

    /*
     * Constructor
     * @param _receiverAddress EOA to receive the bridged tokens
     */
    constructor(address _receiverAddress) public {
        receiverAddress = _receiverAddress;
    }

    /* Check if the token being bridged is from whitelisted contract address
     * @param _contractAddress: contract address to check whitelist of
     */
    modifier isWhitelisted(address _contractAddress) {
        require(whiteListedContracts[_contractAddress], "BSCReceiver: Contract not whitelisted");
        _;
    }

    /* Will allow a contract address to be white listed
     * Note: Only owner could call this function.
     *
     * @param _contractAddress: contract address to whitelist
     * @param _whiteListBool: true to whitelist, false to unset whitelist
     */
    function setWhiteListAddress(address _contractAddress, bool _whiteListBool) external onlyOwner {
        whiteListedContracts[_contractAddress] = _whiteListBool;
        emit SetWhitelistAddress(_contractAddress, _whiteListBool);
    }

    /* Set new receiver of the token when bridge is processed.
     * Note: Only owner could call this function.
     *
     * The NFT token can be sent to dead EOA as well.
     * @param _newReceiver : New address that receive the token being bridged
     *                (It can be set to 0x0)
     */
    function setReceiver(address _newReceiver) external onlyOwner {
        emit SetReceiverAddress(receiverAddress, _newReceiver);
        receiverAddress = _newReceiver; // 0x0 can be valid address
    }

    /* Bridge token from whitelisted contract address to receiver address
     * Note: Only whitelisted contract address should parameter
     *
     * @param _contractAddress: contract address to check whitelist of
     * @param _tokenIds: token id(s) to bridge
     */
    function transferToken(address _contractAddress, uint256[] memory _tokenIds) external nonReentrant isWhitelisted(_contractAddress) {
        nftInterface = IBinanceToken(_contractAddress);

        for (uint256 i = 0; i < _tokenIds.length; i++) {
            uint256 tokenId = _tokenIds[i];
            require(nftInterface.ownerOf(tokenId) == msg.sender, "BSCReceiver: not owner of token");

            nftInterface.safeTransferFrom(msg.sender, receiverAddress, tokenId, "0x");
            emit NFTReceived(_contractAddress, msg.sender, tokenId);
        }
    }

    // fallback function in case of receipt of unwanted tokens in the address
    receive() external payable {
        revert();
    }
}
