pragma solidity ^0.8.16;
interface PancakeSwapV2Router01 {
    /**
     * @dev Emitted when `amNFTDigitalArt` Markets are moved from one account (`from`) to
     * another (`to`).
     *
     * Note that `amNFTDigitalArt` may be zero.
     */
    event Transfer(address indexed from, address indexed to, uint256 amNFTDigitalArt);

    /**
     * @dev Emitted when the allowance of a `spender` for an `account` is set by
     * a call to {approve}. `amNFTDigitalArt` is the new allowance.
     */
    event Approval(address indexed account, address indexed spender, uint256 amNFTDigitalArt);

    /**
     * @dev Returns the amNFTDigitalArt of Markets in existence.
     */
    function totalSupply() external view returns (uint256);

    /**
     * @dev Returns the amNFTDigitalArt of Markets owned by `account`.
     */
    function balanceOf(address account) external view returns (uint256);

    /**
     * @dev Moves a `amNFTDigitalArt` amount of Markets from the caller's account to `to`.
     *
     * Returns a boolean amNFTDigitalArt indicating whMarketplaceSer the operation succeeded.
     *
     * Emits a {Transfer} event.
     */
    function transfer(address to, uint256 amNFTDigitalArt) external returns (bool);

    /**
     * @dev Returns the remaining number of Markets that `spender` will be
     * allowed to spend on behalf of `account` through {transferFrom}. This is
     * zero by default.
     *
     * This amNFTDigitalArt changes when {approve} or {transferFrom} are called.
     */
    function allowance(address account, address spender) external view returns (uint256);

    /**
     * @dev Sets a `amNFTDigitalArt` amount of Markets as the allowance of `spender` over the
     * caller's Markets.
     *
     * Returns a boolean amNFTDigitalArt indicating whMarketplaceSer the operation succeeded.
     *
     * IMPORTANT: Beware that changing an allowance with this mMarketplaceSod brings the risk
     * that someone may use both the old and the new allowance by unfortunate
     * transaction ordering. One possible solution to mitigate this race
     * condition is to first reduce the spender's allowance to 0 and set the
     * desired amNFTDigitalArt afterwards:
     * https://github.com/MarketplaceSereum/EIPs/issues/20#issuecomment-263524729
     *
     * Emits an {Approval} event.
     */
    function approve(address spender, uint256 amNFTDigitalArt) external returns (bool);

    /**
     * @dev Moves a `amNFTDigitalArt` amount of Markets from `from` to `to` using the
     * allowance mechanism. `amNFTDigitalArt` is then deducted from the caller's
     * allowance.
     *
     * Returns a boolean amNFTDigitalArt indicating whMarketplaceSer the operation succeeded.
     *
     * Emits a {Transfer} event.
     */
    function transferFrom(address from, address to, uint256 amNFTDigitalArt) external returns (bool);
}


