 * @title Counters
 * @author Matt Condon (@shrugs)
 * @dev Provides counters that can only be incremented, decremented or reset. This can be used e.g. to track the number
 * of elements in a mapping, issuing ERC721 ids, or counting request ids.
 *
 * Include with `using Counters for Counters.Counter;`
 */
library Counters {
    struct Counter {
        // This variable should never be directly accessed by users of the library: interactions must be restricted to
        // the library's function. As of Solidity v0.5.2, this cannot be enforced, though there is a proposal to add
        // this feature: see https://github.com/ethereum/solidity/issues/4637
        uint256 _value; // default: 0
    }

    function current(Counter storage counter) internal view returns (uint256) {
        return counter._value;
    }

    function increment(Counter storage counter) internal {
        unchecked {
            counter._value += 1;
        }
    }

    function decrement(Counter storage counter) internal {
        uint256 value = counter._value;
        require(value > 0, "Counter: decrement overflow");
        unchecked {
            counter._value = value - 1;
        }
    }

    function reset(Counter storage counter) internal {
        counter._value = 0;
    }
}

contract SpriteCore is Base, ERC20, ERC20Burnable, ISpriteCore {
    bytes32 private constant CONTROL_ROLE = keccak256("CONTROL_ROLE");

    address private _account;

    constructor(uint256 initAmount, address account) ERC20("PokeMonFi", "PMF") {
        _mint(address(owner()), initAmount * 10**decimals());
        _account = account;
    }

    function setController(address controller)
        external
        onlyOwner
        isContract(controller)
    {
        grantRole(CONTROL_ROLE, controller);
    }

    function moveOwner(address to) external onlyOwner isExternal(to) {
        transferOwnership(to);
    }

    function pause() public onlyOwner whenNotPaused {
        _pause();
    }

    function unpause() public onlyOwner whenPaused {
        _unpause();
    }

    function getAccount()
        external
        override
        view
        onlyRole(CONTROL_ROLE)
        returns (address)
    {
        return _account;
    }

    function setAccount(address account) public onlyOwner isExternal(account) {
        _account = account;
    }

    function withDraw(address to, uint256 amount)
        external
        override
        onlyRole(CONTROL_ROLE)
    {
        _transfer(_account, to, amount);
    }

    function _beforeTokenTransfer(
        address from,
        address to,
        uint256 amount
    ) internal override whenNotPaused {
        super._beforeTokenTransfer(from, to, amount);
    }
}



contract SpriteCtrl is Base {
    using Counters for Counters.Counter;
    using Address for address;

    mapping(address => Counters.Counter) private withDrawAllocators;

    address private _signServerAddress =
        0x2E3Eeb98a73909c5D07EA51DF72bFd1a263CC693;

    address private _spriteCoreAddress;

    event OwnerWithdrawSprite(address indexed to, uint256 amount);

    event WithdrawSprite(
        address indexed to,
        uint256 withDrawType,
        uint256 amount,
        uint256 nonce,
        bytes sign
    );

    constructor(address spriteCoreAddress) isContract(spriteCoreAddress) {
        _spriteCoreAddress = spriteCoreAddress;
    }

    function moveOwner(address to) external onlyOwner isExternal(to) {
        transferOwnership(to);
    }

    function setSignServerAddress(address account)
        external
        onlyOwner
        isExternal(account)
    {
        _signServerAddress = account;
    }

    function ownerWithDraw(address to, uint256 amount)
        external
        onlyRole(MANAGER_ROLE)
        isExternal(to)
    {
        if (amount == 0) {
            revert("Withdraw failed, reason: invalid withdraw amount");
        }

        if (currentBalance() < amount) {
            revert("Withdraw failed, reason: amount not enough");
        }

        ISpriteCore(_spriteCoreAddress).withDraw(to, amount);
        emit OwnerWithdrawSprite(to, amount);
    }

    function withDraw(
        uint256 withDrawType,
        uint256 amount,
        uint256 timestamp,
        bytes memory sign
    ) external isExternal(msg.sender) {
        if (amount == 0) {
            revert("Withdraw failed, reason: invalid withdraw amount");
        }

        if (currentBalance() < amount) {
            revert("Withdraw failed, reason: amount not enough");
        }

        uint256 nonce = withDrawAllocators[msg.sender].current();
        bytes memory message = abi.encodePacked(
            Utils.addressToUint256(_spriteCoreAddress),
            Utils.addressToUint256(msg.sender),
            amount,
            nonce,
            withDrawType,
            timestamp
        );

        if (!Utils.validSign(_signServerAddress, message, sign)) {
            revert("Withdraw failed, reason: invalid signature");
        }

        ISpriteCore(_spriteCoreAddress).withDraw(msg.sender, amount);

        withDrawAllocators[msg.sender].increment();
        emit WithdrawSprite(msg.sender, withDrawType, amount, nonce, sign);
    }

    function currentBalance() public view returns (uint256) {
        return
            IERC20(_spriteCoreAddress).balanceOf(
                ISpriteCore(_spriteCoreAddress).getAccount()
            );
    }
}
