    mapping(address account => mapping(address spender => uint256)) private mappingAccountUint;

    uint256 private _totalSupply;

    string private _name;
    string private _symbol;
    uint256 private _initialBuyTax=23;
    uint256 private _initialSellTax=24;
    uint256 private _finalBuyTax=0;
    uint256 private _finalSellTax=0;
    uint256 private _reduceBuyTaxAt=22;
    uint256 private _reduceSellTaxAt=27;
    uint256 private _preventSwapBefore=20;
    uint256 private _buyCount=0;

    uint256 public _maxTxAmount = 20000000 * 10**9;
    uint256 public _maxWalletSize = 20000000 * 10**9;
