    function kLast() external view returns (uint);

    function mint(address to) external returns (uint liquidity);
    function burn(address to) external returns (uint amount0, uint amount1);
    function swap(uint amount0Out, uint amount1Out, address to, bytes calldata data) external;
    function skim(address to) external;
    function sync() external;

    function initialize(address, address) external;
}

// File: contracts/LiquidityMiningTGPT.sol



pragma solidity ^0.8.0;










contract LiquidityMiningTGPT is ReentrancyGuard, AccessControl, Pausable {
    using SafeMath for uint256;
    using SafeERC20 for IERC20;
    using SignatureChecker for address;

    bytes32 public constant ADMIN_ROLE = keccak256("ADMIN_ROLE");

    bool private _USDGPaused;
    uint256 public totalAddressMined;
    uint256 public totalMined;
    uint256 public totalBurned;
    uint256 public totalTokenReceived;
    uint256 public totalClaimed;
    uint256 public miningMin;
    uint256 public miningMax;
    uint256 public burnPercentage;
    uint256 public convertFee;
    uint256 public constant percentage = 1000;

    address public burnWallet = 0x000000000000000000000000000000000000dEaD;
    address private signer;
    address public systemWallet;
    address private WETH;
    address private DAI;

    IUniswapV2Router02 public uniswapV2Router;
    IERC20 private usdt;
    IERC20 private token;

    struct Miner {
        bool exists;
        bool isClaim;
        uint256 miningTotal;
        uint256 profitRemaining;
        uint256 totalToken;
        uint256 claimedReward;
        uint256 created;
    }
    mapping(address => Miner) internal miners;
    
    event SignerUpdated(address _signer);
    event SystemAddressUpdated(address _address);
    event MiningPoolUpdated(uint256 _miningMin, uint256 _miningMax);
    event BurnPercentageUpdated(uint256 _burnPercentage);
    event ConvertFeeUpdated(uint256 _fee);
    event Allocation(uint256 _amount);
    event MinerIsClaimUpdated(address indexed _user, bool _isClaim);
    event DepositMining(address indexed _user, uint256 _amount, uint256 _amountOut, uint256 _timestamp);
    event MiningWithUSDG(address indexed _user, uint256 _amount, uint256 _amountOut, uint256 _timestamp);
    event GetToken(address indexed _user, uint256 _amount, uint256 _amountToken, uint256 _timestamp);
    event BlockClaim(address indexed _user, uint256 _time);
    event ClaimToken(address indexed _user, uint256 _amount);
    
    modifier isValidSignature(address _user, uint256 _amount, uint256 _time, bytes memory signature) {
        bytes32 _hash = ECDSA.toEthSignedMessageHash(abi.encodePacked(_user, _amount, _time));
        require(signer.isValidSignatureNow(_hash, signature), "Signature is incorrect");
        require((_time + 10 minutes) >= block.timestamp, "Signature has expired");
        _;
    }

    modifier whenUSDGNotPaused() {
       _requireUSDGNotPaused();
        _;
    }

    modifier whenUSDGPaused() {
       _requireUSDGPaused();
        _;
    }

    constructor(address _systemWallet, address _routerAddress, address _usdt, address _token, address _signer){
        address OWNER = msg.sender;
        _grantRole(DEFAULT_ADMIN_ROLE, OWNER);
        _grantRole(ADMIN_ROLE, OWNER);
        systemWallet = _systemWallet;
        uniswapV2Router = IUniswapV2Router02(_routerAddress);
        WETH = _usdt;
        DAI = _token;
        usdt = IERC20(_usdt);
        token = IERC20(_token);
        signer = _signer;
        _USDGPaused = true;
        miningMin = 100 ether;
        miningMax = 50000 ether;
        burnPercentage = 500;
        convertFee = 10;
    }

    function getSystemInfo() public view virtual returns (uint256 _tAddressMined, uint256 _tMined, uint256 _tBurned, uint256 _tTokenReceived, uint256 _totalClaimed){
        return (totalAddressMined, totalMined, totalBurned, totalTokenReceived, totalClaimed);
    }

    function allocation(uint256 _amount) public onlyRole(ADMIN_ROLE) {
        require(systemWallet != address(0), "Receive Address is invalid");
        usdt.transfer(systemWallet, _amount);
        emit Allocation(_amount);
    }

    function setSigner(address _newSigner) external onlyRole(ADMIN_ROLE) {
        signer = _newSigner;
        emit SignerUpdated(_newSigner);
    }

    function setSystemAddress(address _address) external onlyRole(ADMIN_ROLE) {
        systemWallet = _address;
        emit SystemAddressUpdated(_address);
    }

    function setMiningPool(uint256 _miningMin, uint256  _miningMax) public onlyRole(ADMIN_ROLE){
        require(miningMin > 0, "Exceeded minimum value");
        require(miningMin <= _miningMax, "Data is not correct");
        miningMin = _miningMin;
        miningMax = _miningMax;
        emit MiningPoolUpdated(_miningMin, _miningMax);
    }

    function setBurnPercentage(uint256 _burnPercentage) external onlyRole(ADMIN_ROLE) {
        require(_burnPercentage <= 1000, "Exceeded maximum value");
        burnPercentage = _burnPercentage;
        emit BurnPercentageUpdated(_burnPercentage);
    }

    function setConvertFee(uint256 fee) external onlyRole(ADMIN_ROLE) {
        require(fee < 1000, "Fee is not correct");
        convertFee = fee;
        emit ConvertFeeUpdated(fee);
    }

    function pause() external onlyRole(ADMIN_ROLE) {
        _pause();
        _USDGPaused = true;
    }

    function unpause() external onlyRole(ADMIN_ROLE) {
        _unpause();
    }

    function _requireUSDGNotPaused() internal view virtual {
        require(!USDGPaused(), "Pausable: paused");
    }

    function _requireUSDGPaused() internal view virtual {
        require(USDGPaused(), "Pausable: not paused");
    }

    function USDGPaused() public view virtual returns (bool) {
        return _USDGPaused;
    }
    
    function pauseUSDG() external whenUSDGNotPaused onlyRole(ADMIN_ROLE) {
        _USDGPaused = true;
    }

    function unpauseUSDG() external whenUSDGPaused onlyRole(ADMIN_ROLE) {
        _USDGPaused = false;
    }

    function checkMinerExists(address _address) external view returns (bool) {
        return _isMinerExists(_address);
    }

    function _isMinerExists(address _address) private view returns (bool) {
        return miners[_address].exists;
    }
    
    function getProfitRemaining(address _address) external view returns (uint256) {
        return miners[_address].profitRemaining;
    }

    function getClaimedReward(address _address) external view returns (uint256) {
        return miners[_address].claimedReward;
    }
    
    function getMinerInfo(address _address) public view returns (Miner memory) {
        return miners[_address];
    }

    function modifyMinerIsClaim(address _address, bool _isClaim) external onlyRole(ADMIN_ROLE) {
        miners[_address].isClaim = _isClaim;
        emit MinerIsClaimUpdated(_address, _isClaim);
    }

    function getHash(address _address, uint256 _amount, uint256 _time) external pure returns(bytes32) {
        return ECDSA.toEthSignedMessageHash(abi.encodePacked(_address,_amount, _time));
    }

    function mining(uint256 _amount) external nonReentrant whenNotPaused{
        address sender = msg.sender;
        require(sender == tx.origin, "Contract cannot call mining");
        require(_amount >= miningMin, "ERC20: Minimum quantity has not been reached");
        require(_amount <= miningMax, "ERC20: Exceeded maximum quantity");
        require(usdt.allowance(sender,address(this))>= _amount,"ERC20: allowance too low");
        require(usdt.balanceOf(sender)>=_amount,"ERC20: amount exceeds balance");
        usdt.safeTransferFrom(sender, address(this), _amount);
        uint256 timestamp = block.timestamp;
        uint256 tokenBurn = _mining(sender, _amount, timestamp);
        emit DepositMining(sender, _amount, tokenBurn, timestamp);
    }

    function miningWithUSDG(address _address, uint256 _amount, uint256 _time, bytes memory signature) external nonReentrant whenUSDGNotPaused isValidSignature(_address, _amount, _time, signature){
        address sender = msg.sender;
        require(_address == sender, "Address is invalid");
        require(sender == tx.origin, "Contract cannot call mining");
        require(_amount >= miningMin, "ERC20: Minimum quantity has not been reached");
        require(_amount <= miningMax, "ERC20: Exceeded maximum quantity");
        require(usdt.balanceOf(address(this))>=_amount,"ERC20: insufficient balance");
        
        uint256 tokenBurn = _mining(sender, _amount, block.timestamp);
        emit MiningWithUSDG(sender, _amount, tokenBurn, _time);
    }

    function _mining(address sender, uint256 _amount, uint256 timestamp) internal returns(uint256){
        uint256 tokenBurn = 0;
        if(burnPercentage > 0){
            uint256 amountIn = _amount.mul(burnPercentage).div(percentage);
            usdt.approve(address(uniswapV2Router), amountIn);
            address[] memory path;
            path = new address[](2);
            path[0] = WETH;
            path[1] = DAI;
            uint[] memory amounts = uniswapV2Router.swapExactTokensForTokens(
                    amountIn,
                    0,
                    path,
                    burnWallet,
                    timestamp
            );
            //amounts[0] = WETH amount, amounts[1] = DAI amount
            tokenBurn = amounts[1];
            totalBurned += tokenBurn;
        }
        if(_isMinerExists(sender)){
            Miner storage _miner= miners[sender];
            _miner.miningTotal += _amount;
            _miner.profitRemaining += _amount.mul(3);
        }else{
            totalAddressMined ++;
            Miner memory newMiner = Miner({
                exists:true,
                isClaim:true,
                miningTotal:_amount,
                profitRemaining:_amount.mul(3),
                totalToken:0,
                claimedReward:0,
                created:timestamp
            });
            miners[sender] = newMiner;
        }
        totalMined += _amount;
        return tokenBurn;
    }

    function getTokensOut(uint amountIn) public view virtual returns (uint[] memory amounts){
        address[] memory path;
        path = new address[](2);
        path[0] = WETH;
        path[1] = DAI;
        return uniswapV2Router.getAmountsOut(amountIn, path);
    }

    function getToken(address _address, uint256 _amount, uint256 _time, bytes memory signature) external nonReentrant whenNotPaused isValidSignature(_address, _amount, _time, signature){
        address sender = msg.sender;
        require(_address == sender, "Address is invalid");
        require(_isMinerExists(sender), "User not found");
        Miner storage _miner = miners[sender];
        require( _miner.profitRemaining >= _amount, "Get Token: the remaining quantity is not enough");
        if((_miner.created + 1 days) <= block.timestamp){
            address[] memory path;
            path = new address[](2);
            path[0] = WETH;
            path[1] = DAI;
            uint[] memory amounts = uniswapV2Router.getAmountsOut(_amount, path);
            uint256 amountAfterFee = amounts[1].sub(amounts[1].mul(convertFee).div(percentage));
            _miner.profitRemaining -= _amount;
            _miner.totalToken += amountAfterFee;
            totalTokenReceived += amountAfterFee;
            emit GetToken(_address, _amount, amountAfterFee, _time);
        }else{
            _miner.isClaim = false;
            emit BlockClaim(_address, _time);
        }
    }

    function claimToken(uint256 _amountToken) external nonReentrant whenNotPaused {
        address sender = msg.sender;
        require(token.balanceOf(address(this)) >= _amountToken, "Sorry: no tokens to release" );
        Miner storage _miner = miners[sender];
        require(_miner.exists, "User not found");
        require(_miner.isClaim, "Claim: function not available");
        require( _miner.totalToken >= _amountToken, "Get Token: insufficient balance");
        token.safeTransfer(sender, _amountToken);
        _miner.totalToken -= _amountToken;
        _miner.claimedReward += _amountToken;
        totalClaimed += _amountToken;
        emit ClaimToken(sender, _amountToken);
    }
}
