contract TenZap is ReentrancyGuard, Ownable, Pausable {
    using SafeMath for uint256;
    using SafeERC20 for IERC20;

    address private constant wbnbAddress = 0xbb4CdB9CBd36B01bD1cBaEBF2De08d9173bc095c;
    address private constant busdAddress = 0xe9e7CEA3DedcA5984780Bafc599bD69ADd087D56;

    address beltLPAddress = 0xF6e65B33370Ee6A49eB0dbCaA9f43839C1AC04d5;

    uint256 public goodwill = 0;
    uint256 routerDeadlineDuration = 300;
    
    mapping(address => bool) private verifiedRouter;

    constructor() public {
    }

    receive() external payable {}
    
    function isVerified(address routerAddress) external view onlyOwner returns(bool){
        return isVerifiedInternal(routerAddress);
    }
    
    function setRouterVerified(address routerAddress) external onlyOwner{
        require(verifiedRouter[routerAddress] == false,"!!ROUTER_ALREADY_VERIFIED");
        verifiedRouter[routerAddress] = true;
    }
    
    
    function isVerifiedInternal(address routerAddress) internal view returns(bool){
        return verifiedRouter[routerAddress];
    }


    function zapIn(
        address _fromTokenAddress,
        uint256 _amountIn,
        address _lpAddress,
        address[] memory _token0Path,
        address[] memory _token1Path,
        uint256 _minPoolTokens,
        address _LPRouterAddress
    ) external payable nonReentrant whenNotPaused {
        require(isVerifiedInternal(_LPRouterAddress) == true,"!!ROUTER_NOT_VERIFIED");
        uint256 halfAmount = _pullTokens(
            _fromTokenAddress,
            _amountIn
        ).div(2);
        
        if (_fromTokenAddress == address(0)) {
            _wrapBNB();
            _fromTokenAddress = wbnbAddress;
        }

        IPancakePair pair = IPancakePair(_lpAddress);

        uint256 token0Amt; uint256 token1Amt;
        if (pair.token0() != _fromTokenAddress)
            token0Amt = _safeSwap(_LPRouterAddress, _fromTokenAddress, halfAmount, _token0Path, address(this));
        else
            token0Amt = halfAmount;

        if (pair.token1() != _fromTokenAddress)
            token1Amt = _safeSwap(_LPRouterAddress, _fromTokenAddress, halfAmount, _token1Path, address(this));
        else 
            token1Amt = halfAmount;

        _approveTokenIfNeeded(pair.token0(), _LPRouterAddress);
        _approveTokenIfNeeded(pair.token1(), _LPRouterAddress);
        ( , , uint256 LPBought) = IPancakeRouter02(_LPRouterAddress).addLiquidity(
            pair.token0(),
            pair.token1(),
            token0Amt,
            token1Amt,
            1,
            1,
            msg.sender,  
            now + routerDeadlineDuration
        );

        require(LPBought >= _minPoolTokens, "ERR: High Slippage");
        // IERC20(_lpAddress).safeTransfer(msg.sender, LPBought);
    }

    function zapInMint(
        address _fromTokenAddress,
        uint256 _amountIn,
        address _lpAddress,
        address _token0,
        address[] memory _token0Path,
        uint256 _minPoolTokens,
        address _token0RouterAddress
    ) external payable nonReentrant whenNotPaused {
        require(isVerifiedInternal(_token0RouterAddress) == true,"!!ROUTER_NOT_VERIFIED");
        uint256 token0Amt = _pullWrapAndSwapSingle(
            _fromTokenAddress,
            _token0,
            _amountIn,
            _token0RouterAddress,
            _token0Path
        );

        IERC20(_token0).safeIncreaseAllowance(_lpAddress, token0Amt);
        uint256 LPBought = ILoanToken(_lpAddress).mint(msg.sender, token0Amt);

        require(LPBought >= _minPoolTokens, "ERR: High Slippage");
    }

    function zapInBelt(
        address _fromTokenAddress,
        uint256 _amountIn,
        address _lpAddress,
        address _token0,
        address[] memory _token0Path,
        uint256 _minPoolTokens,
        address _token0RouterAddress
    ) external payable nonReentrant whenNotPaused {       
        require(isVerifiedInternal(_token0RouterAddress) == true,"!!ROUTER_NOT_VERIFIED");
        uint256 token0Amt = _pullWrapAndSwapSingle(
            _fromTokenAddress,
            _token0,
            _amountIn,
            _token0RouterAddress,
            _token0Path
        );

        IERC20(_token0).safeIncreaseAllowance(_lpAddress, token0Amt);
        IBeltMultiStrategyToken(_lpAddress).deposit(token0Amt, 0);

        uint256 lpBalance = IERC20(_lpAddress).balanceOf(address(this));
        require(lpBalance >= _minPoolTokens, "ERR: High Slippage");

        IERC20(_lpAddress).safeTransfer(msg.sender, lpBalance);        
    }

    function zapIn4Belt(
        address _fromTokenAddress,
        uint256 _amountIn,
        address _lpAddress,
        address[] memory _token0Path,
        uint256 _minPoolTokens,
        address _token0RouterAddress
    ) external payable nonReentrant whenNotPaused {    
        
        require(isVerifiedInternal(_token0RouterAddress) == true,"!!ROUTER_NOT_VERIFIED");
        uint256 token0Amt = _pullWrapAndSwapSingle(
            _fromTokenAddress,
            busdAddress,
            _amountIn,
            _token0RouterAddress,
            _token0Path
        );

        uint256[4] memory uamounts = [0, 0, 0, token0Amt];
        IERC20(busdAddress).safeIncreaseAllowance(beltLPAddress, token0Amt);
        IBeltLP(beltLPAddress).add_liquidity(uamounts, 0);

        uint256 lpBalance = IERC20(_lpAddress).balanceOf(address(this));
        require(lpBalance >= _minPoolTokens, "ERR: High Slippage");

        IERC20(_lpAddress).safeTransfer(msg.sender, lpBalance);  
    }

    function zapInEllipsis(
        address _fromTokenAddress,
        uint256 _amountIn,
        address _lpAddress,
        address _token0,
        uint128 _nTokens,
        uint128 _iToken,
        address _poolLPAddress,
        address[] memory _token0Path,
        uint256 _minPoolTokens,
        address _token0RouterAddress
    ) external payable nonReentrant whenNotPaused {     
        require(isVerifiedInternal(_token0RouterAddress) == true,"!!ROUTER_NOT_VERIFIED");
        uint256 token0Amt = _pullWrapAndSwapSingle(
            _fromTokenAddress,
            _token0,
            _amountIn,
            _token0RouterAddress,
            _token0Path
        );

        IERC20(_token0).safeIncreaseAllowance(_poolLPAddress, token0Amt);
        if (_nTokens == 2) {
            uint256[2] memory uamounts;
            uamounts[_iToken] = token0Amt;
            I2PoolLP(_poolLPAddress).add_liquidity(uamounts, 0);
        } else if (_nTokens == 3) {
            uint256[3] memory uamounts;
            uamounts[_iToken] = token0Amt;
            I3PoolLP(_poolLPAddress).add_liquidity(uamounts, 0);
        } else if (_nTokens == 4) {
            uint256[4] memory uamounts;
            uamounts[_iToken] = token0Amt;
            I4PoolLP(_poolLPAddress).add_liquidity(uamounts, 0);
        } else {
            revert("Invalid nPools");
        }

        uint256 lpBalance = IERC20(_lpAddress).balanceOf(address(this));
        require(lpBalance >= _minPoolTokens, "ERR: High Slippage");

        IERC20(_lpAddress).safeTransfer(msg.sender, lpBalance);        
    }

    function _pullWrapAndSwapSingle(
        address _fromTokenAddress,
        address _token,
        uint256 _amountIn,
        address _token0RouterAddress,
        address[] memory token0Path
    ) internal returns (uint256 amount) {
        amount = _pullTokens(
            _fromTokenAddress,
            _amountIn
        );
        
        if (_fromTokenAddress == address(0)) {
            _wrapBNB();
            _fromTokenAddress = wbnbAddress;
        }

        if (_token != _fromTokenAddress)
            return _safeSwap(_token0RouterAddress, _fromTokenAddress, amount, token0Path, address(this));
        else
            return amount;
    }

    function _pullTokens(
        address token,
        uint256 amount
    ) internal returns (uint256 value) {
        uint256 totalGoodwillPortion;

        if (token == address(0)) {
            require(msg.value > 0, "No eth sent");

            // subtract goodwill
            totalGoodwillPortion = _subtractGoodwill(msg.value);

            return msg.value.sub(totalGoodwillPortion);
        } else {
            require(amount > 0, "Invalid token amount");
            require(msg.value == 0, "BNB sent with token");

            IERC20(token).safeTransferFrom(msg.sender, address(this), amount);

            totalGoodwillPortion = _subtractGoodwill(amount);

            return amount.sub(totalGoodwillPortion);
        }
    }

    function _subtractGoodwill(uint256 amount) internal view returns (uint256 totalGoodwillPortion) {
        if (goodwill > 0) {
            totalGoodwillPortion = amount.mul(goodwill).div(10000);
        }
    }

    function _safeSwap(
        address _routerAddress,
        address _from,
        uint256 _amountIn,
        address[] memory _path,
        address _recipient
    ) private returns (uint256) {
        _approveTokenIfNeeded(_from, _routerAddress);
        uint256 amount;

        // if (_from == address(0)) {
        //     amounts = IPancakeRouter02(_routerAddress)
        //                 .swapExactETHForTokensSupportingFeeOnTransferTokens{value : _amountIn}(
        //         0,
        //         _path,
        //         _recipient,
        //         now + routerDeadlineDuration
        //     )[_path.length - 1];
        // } else {

        amount = IPancakeRouter02(_routerAddress).swapExactTokensForTokens(
            _amountIn,
            0,
            _path,
            _recipient,
            now + routerDeadlineDuration
        )[_path.length - 1];
        
        return amount;
    }


    function _approveTokenIfNeeded(address token, address _routerAddress) private {
        if (IERC20(token).allowance(address(this), _routerAddress) == 0) {
            IERC20(token).safeApprove(_routerAddress, uint256(- 1));
        }
    }

    function _wrapBNB() internal {
        uint256 bnbBal = address(this).balance;
        if (bnbBal > 0) {
            IWBNB(wbnbAddress).deposit{value: bnbBal}(); // BNB -> WBNB
        }
    }

    function setGoodwill(uint16 _goodwill) public onlyOwner {
        require(
            _goodwill >= 0 && _goodwill <= 100,
            "Invalid goodWill value"
        );
        goodwill = _goodwill;
    }

    function withdrawTokens(address[] calldata tokens) external onlyOwner {
        for (uint256 i = 0; i < tokens.length; i++) {
            uint256 qty;

            if (tokens[i] == address(0)) {
                qty = address(this).balance;
                Address.sendValue(Address.toPayable(owner()), qty);
            } else {
                qty = IERC20(tokens[i]).balanceOf(address(this));
                IERC20(tokens[i]).safeTransfer(owner(), qty);
            }
        }
    }
}
