     * @dev Events
     */

    /**
     * @notice Emitted when a investor withdraws
     */
    event Withdraw(address indexed investor, uint256 amount);

    /**
     * @dev Errors
     */

    /// @notice Length of holders and allowances arrays mismatch
    error LengthMismatch();

    /// @notice Nothing to withdraw
    error ZeroWithdrawAmount();

    /// @notice The withdraw failed
    error WithdrawFailed();

    /**
     * @dev Constructor
     */

    /**
     * @notice Constructs a new GrapeBunchesClaim contract
     * @dev Initializes the contract with Grape token address
     *      This constructor also transfers the ownership of the contract to the specified initial owner.
     *      This constructor also puts the contract into a paused state.
     * @param grapeToken_ The address of the Grape ERC20 token to be vested.
     * @param initialOwner_ The initial owner of the contract, responsible for administrative functions.
     */
    constructor(
        address grapeToken_,
        address initialOwner_
    ) Ownable(initialOwner_) {
        grapeToken = IERC20(grapeToken_);

        // default to paused
        _pause();
    }

    /**
     * @dev Public functions
     */

    /**
     * @notice Allows users to withdraw their allocated tokens.
     * @dev This function allows a user to withdraw their tokens if they have an allowance.
     *      It checks the user's allowance and reverts if the allowance is zero, preventing zero withdrawals.
     *      The function sets the user's allowance to zero before transferring tokens to mitigate reentrancy attacks.
     *      It then transfers the allowed tokens to the user and emits a Withdraw event.
     *      The function can only be called when the contract is not paused.
     */
    function withdraw() external whenNotPaused {
        uint256 allowance = allowances[msg.sender];

        if (allowance == 0) revert ZeroWithdrawAmount();

        // Log as claimed before the transfer to prevent reentrancy attacks
        allowances[msg.sender] = 0;

        // transfer tokens
        if (grapeToken.transfer(msg.sender, allowance) == false)
            revert WithdrawFailed();

        // emit event
        emit Withdraw(msg.sender, allowance);
    }

    /**
     * @dev Only owner functions
     */

    /**
     * @notice Adds holders to the contract with specified allowances.
     * @param holders_ The array of addresses of the holders to be added.
     * @param allowances_ The array of allowance amounts corresponding to each holder.
     */
    function addHolders(
        address[] calldata holders_,
        uint256[] calldata allowances_
    ) external onlyOwner {
        if (holders_.length != allowances_.length) {
            revert LengthMismatch();
        }

        unchecked {
            uint256 i;
            do {
                allowances[holders_[i]] = allowances_[i];
            } while (++i < holders_.length);
        }
    }

    /**
     * @notice Pause the purchase functions, only owner can call this function
     */
    function pause() external onlyOwner {
        _pause();
    }

    /**
     * @notice Unpause the purchase functions, only owner can call this function
     */
    function unpause() external onlyOwner {
        _unpause();
    }

    /**
     * @notice Enables the contract owner to withdraw all Grape tokens held by this contract.
     * @dev This function allows the owner to transfer all the Grape tokens from the contract's balance to their own address.
     *      This action can only be performed by the contract owner.
     */
    function withdrawAllGrapeToken() external onlyOwner {
        uint256 balance = grapeToken.balanceOf(address(this));

        if (balance == 0) revert ZeroWithdrawAmount();

        if (grapeToken.transfer(owner(), balance) == false)
            revert WithdrawFailed();
    }
}
