    "./contracts/Distribution.sol": {
      "content": "// SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;

import "./ArrayUtils.sol";
import "@openzeppelin/contracts/access/AccessControl.sol";
import "@openzeppelin/contracts/token/ERC20/ERC20.sol";
import "@openzeppelin/contracts/token/ERC721/extensions/ERC721Enumerable.sol";
import "@openzeppelin/contracts/token/ERC721/ERC721.sol";
import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/security/ReentrancyGuard.sol";
import "@openzeppelin/contracts/utils/Strings.sol";

contract Distribution is AccessControl,Ownable,ReentrancyGuard{

    bytes32 public constant OPERATOR_ROLE = keccak256("OPERATOR_ROLE");
    string[] eventNames;
    enum ClaimStatus { Unclaim, Claimed }
    mapping(string => bool) internal _eventRegistry;
    mapping(uint256 => bool) internal _claimStatus;
    //nft-address=> payment address => tokenIds => balance;
    mapping(string => DistributionRecord) internal distributionRecords;
    mapping(address => ClaimRecord[]) claimRecords;
   //nft->payment token->events
    mapping (address => mapping(address=>string[])) _events;
    //nft->payment->token_id->last event token index
    mapping (address => mapping(address=>mapping(uint256=>uint256))) tokenNextClaimEventIndex;
    mapping (address => mapping(address=>mapping(string=>uint256))) eventClaimEligibility;


    struct DistributionRecord{
      string eventName;
      uint256 date;
      uint256 totalAmount;
      uint256 amountPerToken;
      address paymentTokenAddress;
      address nftAddress;
      uint256 eligibleStartTokenId;
      uint256 eligibleLastTokenId;
    }

    struct ClaimRecord{
      uint256 claimId;
      address claimAddress;
      address paymentTokenAddress;
      address nftAddress;
      uint256 date;
      uint256 claimedAmount;
      uint256[] tokenIds;
    }

    struct RewardClaimRecord {
      uint256 tokenId;
      uint256 claimId;
      uint256 amount;
      uint256 claimDate;
      string eventName;
      address nftAddress;
      address paymentTokenAddress;
      address claimantAddress;
      ClaimStatus status;
    }

    event DistributionEvent(string eventName,address nftAddress,address paymentTokenAddress,uint256 date,uint256 totalAmount,uint256 amountPerToken,uint256 startTokenId,uint256 lastTokenId);
    event RewardClaimEvent(uint256 tokenId,uint256 claimId,string eventName,uint256 amount,uint256 date,address claimAddress,address paymentTokenAddress,address nftAddress);

    function distributionRecordOf(string memory eventName) public view returns(DistributionRecord memory){
      return distributionRecords[eventName];
    }

    function listEventNames() public view returns(string[] memory){
      return eventNames;
    }

    function listEventNamesOf(address nftAddress,address paymentTokenAddress) public view returns(string[] memory){
      return _events[nftAddress][paymentTokenAddress];
    }

    function isEventExist(string memory eventName) public view returns (bool){
      return _eventRegistry[eventName];
    }

    function listDistributionRecords() public view returns(DistributionRecord[] memory){
      DistributionRecord[] memory records = new DistributionRecord[](eventNames.length);
      for (uint i=0;i < eventNames.length;i++){
        records[i] = (distributionRecords[eventNames[i]]);
      }
      return records;
    }

    function totalDistributedOf(address nftAddress,address paymentTokenAddress) public view returns (uint256){
      DistributionRecord[] memory records = listDistributionRecords();
      uint256 totalDistributed =0;
      for (uint i=0;i < records.length;i++){
        if (records[i].nftAddress == nftAddress && records[i].paymentTokenAddress == paymentTokenAddress){
          totalDistributed = totalDistributed + records[i].totalAmount;
        }
      }
      return totalDistributed;
    }

    function hashToSign(address nftAddress,address paymentTokenAddress, uint256 amount, address claimantAddress,uint256 claimId)
     public pure  returns (bytes32) {
        return _hashClaim(nftAddress,paymentTokenAddress,amount,claimantAddress,claimId);
    }

    function _hashClaim(address nftAddress,address paymentTokenAddress, uint256 amount, address claimantAddress,uint256 claimId)
    internal pure returns (bytes32) {
      return keccak256(
          abi.encodePacked("\x19\x01", hashClaim(nftAddress,paymentTokenAddress, amount,claimantAddress,claimId))
      );
    }

    function hashClaim(address nftAddress,address paymentTokenAddress, uint256 amount, address claimantAddress,uint256 claimId)
    internal pure  returns (bytes32 hash) {
      uint size = 800;
      bytes memory array = new bytes(size);
      uint index;
      assembly {
        index := add(array, 0x20)
      }
      index = ArrayUtils.unsafeWriteAddressWord(index, nftAddress);
      index = ArrayUtils.unsafeWriteAddressWord(index, paymentTokenAddress);
      index = ArrayUtils.unsafeWriteUint(index, amount);
      index = ArrayUtils.unsafeWriteAddressWord(index, claimantAddress);
      index = ArrayUtils.unsafeWriteUint(index, claimId);

      assembly {
        hash := keccak256(add(array, 0x20), size)
      }
      return hash;
    }

    function balanceOf(address nftAddress,address paymentTokenAddress,address owner) public view returns (uint256){
      uint256 balance;
      uint256[] memory  ownerTokens = retrieveOwnerTokens(nftAddress,owner);
      for (uint i=0; i<ownerTokens.length; i++){
        uint tokenId = ownerTokens[i];
        balance = balance + tokenBalanceOf(nftAddress,paymentTokenAddress,tokenId);
      }
      return balance;
    }

    function tokenBalanceOf(address nftAddress,address paymentTokenAddress,uint256 tokenId) public view returns (uint256){
      uint256 balance;
      uint tokenNextClaimIndex = tokenNextClaimEventIndex[nftAddress][paymentTokenAddress][tokenId];
      string memory eventName;
      for (uint i=tokenNextClaimIndex;i < _events[nftAddress][paymentTokenAddress].length;i++){
        eventName = _events[nftAddress][paymentTokenAddress][i];
        if (tokenId <= eventClaimEligibility[nftAddress][paymentTokenAddress][eventName]){
          balance = balance + distributionRecords[eventName].amountPerToken;
        }
      }
      return balance;
    }

    function tokensBalanceOf(address nftAddress,address paymentTokenAddress,uint256[] memory tokenIds) public view returns (uint256[] memory){
      uint256[] memory balances = new uint256[](tokenIds.length);
      for (uint i=0;i<tokenIds.length;i++){
        balances[i] = tokenBalanceOf(nftAddress,paymentTokenAddress,tokenIds[i]);
      }
      return balances;
    }

    function claimReward(address nftAddress,address paymentTokenAddress,
      address claimantAddress,uint256 date,uint256 amount,uint256 claimId)
      public  nonReentrant {
      require(msg.sender == claimantAddress,"Invalid claimant");
      require(!_claimStatus[claimId],"Invalid claim");
      _claimStatus[claimId] = true;
      ERC20 token = ERC20(paymentTokenAddress);
      uint256 ownerBalance = balanceOf(nftAddress,paymentTokenAddress,claimantAddress);
      require(token.balanceOf(address(this))>= ownerBalance,"Insufficient balance");
      require(ownerBalance > 0,"User has no reward to claim");
      require(ownerBalance == amount,"Unmatched claim amount");
      uint256[] memory ownerTokens = retrieveOwnerTokens(nftAddress,claimantAddress);
      RewardClaimRecord[] memory rewardClaimRecords = retrieveRewardClaimRecords(nftAddress,paymentTokenAddress,claimantAddress,date,claimId,ownerTokens);
      updateTokenNextClaimIndex(nftAddress,paymentTokenAddress,ownerTokens);
      require(token.transfer(claimantAddress, ownerBalance),"Failed to claim");
      emitRewardClaimEvents(rewardClaimRecords);
    }

    function retrieveRewardClaimRecords(address nftAddress,address paymentTokenAddress,address claimantAddress,uint256 date,
      uint256 claimId,uint256[] memory ownerTokens) internal view
      returns (RewardClaimRecord[] memory){
      uint recordIndex;
      uint totalRecords = getTotalOfRewardClaimRecord(nftAddress,paymentTokenAddress,ownerTokens);
      RewardClaimRecord[] memory records = new RewardClaimRecord[](totalRecords);
      for (uint i=0;i<ownerTokens.length;i++){
        string[] memory tokenEligibleEvents = getTokenEligibleEvents(nftAddress,paymentTokenAddress,ownerTokens[i]);
        for (uint ti=0;ti < tokenEligibleEvents.length;ti++){
          DistributionRecord memory distRecord = distributionRecords[tokenEligibleEvents[ti]];
          RewardClaimRecord memory record;
          record.tokenId = ownerTokens[i];
          record.claimId = claimId;
          record.amount = distRecord.amountPerToken;
          record.claimDate = date;
          record.nftAddress = nftAddress;
          record.paymentTokenAddress = paymentTokenAddress;
          record.claimantAddress =claimantAddress;
          record.eventName = tokenEligibleEvents[ti];
          record.status = ClaimStatus.Claimed;
          records[recordIndex] = record;
          ++recordIndex;
        }
      }
      return records;
    }

    function getTotalOfRewardClaimRecord(address nftAddress, address paymentTokenAddress,uint256[] memory tokenIds)
    internal view returns (uint256){
      uint totalRecords;
      for (uint i=0;i<tokenIds.length;i++){
        totalRecords+= getTokenEligibleEvents(nftAddress,paymentTokenAddress,tokenIds[i]).length;
      }
      return totalRecords;
    }

    function updateTokenNextClaimIndex(address nftAddress,address paymentTokenAddress,
      uint256[] memory ownerTokens) internal{
      string[] memory events = _events[nftAddress][paymentTokenAddress];
      for (uint i=0;i<ownerTokens.length;i++){
        tokenNextClaimEventIndex[nftAddress][paymentTokenAddress][ownerTokens[i]] = events.length;
      }
    }

    function getTokenEligibleEvents(address nftAddress,address paymentTokenAddress,uint tokenId) internal view returns(string[] memory){
      string[] memory events = _events[nftAddress][paymentTokenAddress];
      uint tokenNextClaimIndex = tokenNextClaimEventIndex[nftAddress][paymentTokenAddress][tokenId];
      string[] memory tokenEligibleEvents;
      uint index = 0;
      if ( tokenNextClaimIndex == 0 ){
        uint counter = 0;
        for (uint i=0;i< events.length;i++){
          if (tokenId <= eventClaimEligibility[nftAddress][paymentTokenAddress][events[i]]){
            ++counter;
          }
        }
        tokenEligibleEvents = new string[](counter);
        for (uint i=0;i< events.length;i++){
          if (tokenId <= eventClaimEligibility[nftAddress][paymentTokenAddress][events[i]]){
            tokenEligibleEvents[index] = events[i];
            ++index;
          }
        }
      }else{
        tokenEligibleEvents = new string[](events.length-tokenNextClaimIndex);
        for (uint i=tokenNextClaimIndex;i < events.length;i++){
          tokenEligibleEvents[index] = events[i];
          ++index;
        }
      }
      return tokenEligibleEvents;
    }

    function emitRewardClaimEvent(RewardClaimRecord memory record) internal{
      emit RewardClaimEvent(record.tokenId,record.claimId,record.eventName,record.amount,record.claimDate,record.claimantAddress,record.paymentTokenAddress,record.nftAddress);
    }

    function emitRewardClaimEvents(RewardClaimRecord[] memory records) internal{
      for (uint i=0;i < records.length;i++){
        RewardClaimRecord memory record = records[i];
        emitRewardClaimEvent(record);
      }
    }

    function distributeFund(address nftAddress,address paymentTokenAddress,uint256 allocatedFund,uint256 date,string memory eventName) public onlyRole(OPERATOR_ROLE){
      require(!_eventRegistry[eventName],"Duplicate Event");
      _eventRegistry[eventName] = true;
      ERC721Enumerable nft = ERC721Enumerable(nftAddress);
      ERC20 token = ERC20(paymentTokenAddress);
      require(token.transferFrom(msg.sender,address(this),allocatedFund),"Failed fund transfer");
      uint256 totalSupply = nft.totalSupply();
      uint256 startTokenId =nft.tokenByIndex(0);
      uint256 lastTokenId = nft.tokenByIndex(totalSupply-1);
      uint256 fund = allocatedFund/totalSupply;
      distributionRecords[eventName] = DistributionRecord(eventName,date,allocatedFund,fund,paymentTokenAddress,nftAddress,startTokenId,lastTokenId);
      eventNames.push(eventName);
      _events[nftAddress][paymentTokenAddress].push(eventName);
      eventClaimEligibility[nftAddress][paymentTokenAddress][eventName] = lastTokenId;
      emit DistributionEvent(eventName,nftAddress,paymentTokenAddress,date,allocatedFund,fund,startTokenId,lastTokenId);
    }

    function retrieveOwnerTokens(address nftAddress,address owner) public view returns (uint256[] memory){
      uint256 [] memory tokens;
      ERC721Enumerable nft = ERC721Enumerable(nftAddress);
      uint totalOwnerTokens = nft.balanceOf(owner);
      tokens = new uint256[](totalOwnerTokens);
      for (uint i=0;i<totalOwnerTokens;i++){
        tokens[i] = nft.tokenOfOwnerByIndex(owner,i);
      }
      return tokens;
    }

    function isOperator(address _operator) public view returns (bool) {
      return hasRole(OPERATOR_ROLE, _operator);
    }

    function addOperator(address _operator) public onlyOwner{
      _setupRole(OPERATOR_ROLE,_operator);
    }

    function removeOperator(address _operator) public onlyOwner{
      _revokeRole(OPERATOR_ROLE,_operator);
    }
}"
