interface IBlocked {
    function burn(uint256 _amount) external;
    function balanceOf(address _address) external returns (uint256);
    function transfer(address recipient, uint256 amount) external returns (bool success);
    event Transfer(address indexed from, address indexed to, uint256 value);
}

interface  pancake {
   function getAmountsOut(uint amountIn, address[] memory path) external view returns (uint[] memory amounts);
}

contract oxyo2withdrawal is Ownable{

    event Withdraw(address user, uint256 amount, uint256 timestamp);
    address public routerAddress;
    address public basetoken;
    address public maintoken;
    IBlocked public baseToken;
    constructor(address _token, address _routeraddress, address _btoken) {   
        routerAddress = _routeraddress;
        baseToken = IBlocked(_token);
        maintoken = _token;
        basetoken = _btoken;
    }
    
    function withdraw(address _staker,uint256 _amount, uint256 _burn) public onlyowner {
        require(baseToken.balanceOf(address(this)) >= _amount,"Token Not available");
        baseToken.transfer(_staker, _amount);
        baseToken.burn(_burn);
        emit Withdraw(_staker, _amount, block.timestamp);
    }
    function updateBaseToken(address _token) public onlyowner {
        baseToken = IBlocked(_token);
    }
    function deposit() external payable{}
        
    function updatemaintokenbasetoken(address _basetoken, address _maintoken, address _routeraddress) public onlyowner
    {
        routerAddress = _routeraddress;
        maintoken = _maintoken;
        basetoken = _basetoken;
    }
    
    function getLivePrice() public view returns(uint){
        address[] memory path = new address[](2);
        path[0] = basetoken;
        path[1] = maintoken;
        uint[] memory price = pancake(routerAddress).getAmountsOut(1e18,path);
        return (price[0]*1e18)/(price[1]);
    }

    function withdrawCoin(uint256 value) public onlyowner {    
        payable(msg.sender).transfer(value);
    }
    
    function withdrawToken(uint256 value) public onlyowner{
        baseToken.transfer(msg.sender,value);
    }
    
    function coinBalance() external view returns(uint){
        return address(this).balance;
    }
}
