    "contracts/Staking.sol": {
      "content": "// SPDX-License-Identifier: SEE LICENSE IN LICENSE
pragma solidity ^0.8.20;

import {IERC20} from "@openzeppelin/contracts/token/ERC20/IERC20.sol";

contract Staking {
    IERC20 public token;

    struct StakeInfo {
        uint256 amount;
        uint256 timestamp;
    }

    // Reward = 0.05% of deposited amount per day = 0.0005, per year = 18.25%
    uint256 public constant rewardsPerDayNominator = 500;
    uint256 public constant rewardsPerDayDenominator = 1000000;

    mapping(address => StakeInfo) public stakes;

    constructor(address _token) {
        token = IERC20(_token);
    }

    function stake(uint256 amount) external {
        require(amount > 0, "Staking: amount must be greater than 0");
        require(
            token.transferFrom(msg.sender, address(this), amount),
            "Staking: transfer failed"
        );

        StakeInfo storage stakeInfo = stakes[msg.sender];
        if (stakeInfo.timestamp == 0) {
            stakeInfo.amount = amount;
        } else {
            stakeInfo.amount = stakingBalance(msg.sender) + amount;
        }
        stakeInfo.timestamp = block.timestamp;
    }

    function stakingBalance(address staker) public view returns (uint256) {
        StakeInfo memory stakeInfo = stakes[staker];
        require(stakeInfo.timestamp > 0, "Staking: staker has no stake info");
        uint256 timeDiff = block.timestamp - stakeInfo.timestamp;
        uint256 rewardsPerDay = (stakeInfo.amount * rewardsPerDayNominator) /
            rewardsPerDayDenominator;
        return stakeInfo.amount + (rewardsPerDay * timeDiff) / 1 days;
    }

    function withdraw() external {
        StakeInfo storage stakeInfo = stakes[msg.sender];
        require(stakeInfo.timestamp > 0, "Staking: staker has no stake info");
        uint256 rewards = stakingBalance(msg.sender);
        require(rewards > 0, "Staking: no rewards to withdraw");
        stakeInfo.timestamp = block.timestamp;
        stakeInfo.amount = 0;
        require(
            token.transfer(msg.sender, rewards),
            "Staking: transfer failed"
        );
    }
}
