// SPDX-License-Identifier: None

import "@openzeppelin/contracts/token/ERC20/ERC20.sol";
import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/utils/math/SafeMath.sol";
import "@pancakeswap-libs/pancake-swap-core/contracts/interfaces/IPancakePair.sol";
import "pancakeswap-peripheral/contracts/interfaces/IPancakeRouter02.sol";


contract MNSTRS is IERC20, Ownable {
    using SafeMath for uint256;

    uint256 private _totalSupply = 500e24;
    string private _name = "BlockMonsters";
    string private _symbol = "MNSTRS";

    uint8 private _operationsFee = 40;
    uint8 private _rewardsFee = 10;

    uint8 private _totalFee = 50;

    address private _operationsWallet;
    address private _rewardsVault;

    bool private _isSwapActive = false;
    bool private _creationPhase = true;

    mapping(address => uint256) _balances;
    mapping(address => mapping(address => uint256)) _allowances;

    IPancakeRouter02 public PancakeRouter;
    IPancakePair public PancakePair;

    address public PreSale;
    address public PrivateSale;

    bool private _liquiditySemaphore = false;
    bool private isSwapping = false;
    bool private feeActivated = true;

    uint256 private _swapThreshold = 10000e18;

    modifier liquiditySemaphore() {
        if (!_liquiditySemaphore) {
            _liquiditySemaphore = true;
            _;
            _liquiditySemaphore = false;
        }
    }

    constructor() {
        _balances[_msgSender()] = _totalSupply;
        emit Transfer(address(0), _msgSender(), _totalSupply);
    }

    receive() external payable {}

    function name() public view returns (string memory) {
        return _name;
    }

    function symbol() public view returns (string memory) {
        return _symbol;
    }

    function decimals() public pure returns (uint8) {
        return 18;
    }

    function totalSupply() public view override returns (uint256) {
        return _totalSupply;
    }

    function balanceOf(address account) public view override returns (uint256) {
        return _balances[account];
    }

    function allowance(address owner, address spender) public view override returns (uint256) {
        return _allowances[owner][spender];
    }

    function transfer(address to, uint256 amount) external override returns (bool) {
        _transfer(_msgSender(), to, amount);
        return true;
    }

    function transferFrom(
        address sender,
        address recipient,
        uint256 amount
    ) external override returns (bool) {
        _transfer(sender, recipient, amount);
        _approve(
            sender,
            _msgSender(),
            _allowances[sender][_msgSender()].sub(amount, "MNSTRS: transfer amount exceeds allowance")
        );
        return true;
    }

    function increaseAllowance(address spender, uint256 addedValue) public returns (bool) {
        _approve(_msgSender(), spender, _allowances[_msgSender()][spender].add(addedValue));
        return true;
    }

    function decreaseAllowance(address spender, uint256 subtractedValue) public returns (bool) {
        uint256 currentAllowance = _allowances[_msgSender()][spender];
        require(currentAllowance >= subtractedValue, "MNSTRS: decreased allowance below zero");
        _approve(_msgSender(), spender, currentAllowance.sub(subtractedValue));
        return true;
    }

    function distributeFees(uint256 amount) internal returns (bool) {
        bool didSendToMarketing = payable(_operationsWallet).send(amount);
        return didSendToMarketing;
    }

    function _transfer(
        address from,
        address to,
        uint256 amount
    ) internal {
        (uint256 totalFee, uint256 operationsFee, uint256 rewardFee) = _calculateFees(amount);

        if (from != address(PancakePair) && !isSwapping && feeActivated && !_claimTransfer(from)) {
            isSwapping = true;
            uint256 ignoreBalance = payable(this).balance;
            bool didSwap = _swapTokensForNative();
            uint256 swapBalance = payable(this).balance;
            uint256 profit = swapBalance.sub(ignoreBalance);
            if (didSwap) distributeFees(profit);
            isSwapping = false;
        }

        if (_shouldTakeFee(from, to) && feeActivated) {
            uint256 transferAmount = amount.sub(totalFee);

            _balances[from] = _balances[from].sub(transferAmount, "MNSTRS: transfer amount exceeds balance");
            _balances[to] = _balances[to].add(transferAmount);
            _balances[_rewardsVault] = _balances[_rewardsVault].add(rewardFee);
            _balances[address(this)] = _balances[address(this)].add(operationsFee);

            emit Transfer(from, to, transferAmount);
            emit Transfer(from, address(this), operationsFee);
            emit Transfer(from, address(_rewardsVault), rewardFee);
        } else {
            _balances[from] = _balances[from].sub(amount, "MNSTRS: transfer amount exceeds balance");
            _balances[to] = _balances[to].add(amount);

            emit Transfer(from, to, amount);
        }
    }

    function approve(address spender, uint256 amount) external override returns (bool) {
        _approve(_msgSender(), spender, amount);
        return true;
    }

    function _approve(
        address owner,
        address spender,
        uint256 amount
    ) private {
        require(spender != address(0), "MNSTRS: approve to the zero address");
        require(owner != address(0), "MNSTRS: approve from the zero address");

        _allowances[owner][spender] = amount;
        emit Approval(owner, spender, amount);
    }

    function setPairAddress(address pair) public onlyOwner {
        require(pair != address(0), "MNSTRS: Cannot set Pair to zero address");
        PancakePair = IPancakePair(pair);
    }

    function setRouterAddress(address router) public onlyOwner {
        require(router != address(0), "MNSTRS: Cannot set Router to zero address");
        PancakeRouter = IPancakeRouter02(router);
        _approve(address(this), address(PancakeRouter), ~uint256(0));
    }

    function setOperationsVaultAddress(address vault) external onlyOwner {
        require(vault != address(0), "MNSTRS: Cannot set Vault to zero address");
        _operationsWallet = vault;
    }

    function setRewardsVaultAddress(address vault) external onlyOwner {
        require(vault != address(0), "MNSTRS: Cannot set Vault to zero address");
        _rewardsVault = vault;
    }

    function setPreSaleAddress(address preSale) external onlyOwner {
        require(preSale != address(0), "MNSTRS: Cannot set PreSale to zero address");
        PreSale = preSale;
    }

    function setPrivateSaleAddress(address privateSale) external onlyOwner {
        require(privateSale != address(0), "MNSTRS: Cannot set PreSale to zero address");
        PrivateSale = privateSale;
    }

    function activateFeesOnTransfer() external onlyOwner {
        feeActivated = true;
    }

    function deactivateFeesOnTransfer() external onlyOwner {
        feeActivated = false;
    }

    function setSwapThreshold(uint256 threshold) external onlyOwner {
        require(threshold > 0, "MNSTRS: Cannot set threshold to zero");
        _swapThreshold = threshold;
    }

    function _calculateFees(uint256 amount)
        internal
        view
        returns (
            uint256 totalFeeAmount,
            uint256 rewardFeeAmount,
            uint256 _operationsFeeAmount
        )
    {
        _operationsFeeAmount = amount.mul(_operationsFee).div(1000);
        rewardFeeAmount = amount.mul(_rewardsFee).div(1000);
        totalFeeAmount = rewardFeeAmount.add(_operationsFeeAmount);
    }

    function _shouldTakeFee(address sender, address receiver) internal view returns (bool) {
        return (sender == address(PancakePair) || receiver == address(PancakePair)) && sender != address(this);
    }

    function _claimTransfer(address sender) internal view returns (bool) {
        return (sender == address(PrivateSale) || sender == address(PreSale));
    }

    function _swapTokensForNative() internal liquiditySemaphore returns (bool) {
        uint256 contractBalance = IERC20(address(this)).balanceOf(address(this));
        if (contractBalance > _swapThreshold) {
            address[] memory path = new address[](2);
            path[0] = address(this);
            path[1] = PancakeRouter.WETH();

            PancakeRouter.swapExactTokensForETH(_swapThreshold, 0, path, address(this), block.timestamp);
            return true;
        }
        return false;
    }

    function withdrawNative() external onlyOwner {
        payable(_msgSender()).transfer(address(this).balance);
    }

    function withdrawToken(address _token, uint256 amount) external onlyOwner {
        IERC20(_token).transfer(_msgSender(), amount);
    }
}
