contract ANUM is BEP20 
{
    using SafeMath for uint256;
    mapping(address => bool) public is_AntiBotBlackListed;
    mapping(address => bool) public _isPairAddress;
    uint256 public _maxTransferPerDay = 5 * 10**18;
    uint256 public secondsInDay = 86400; // 24 * 60 * 60
    mapping(address => bool) public allowedTransfer;

    struct userInfo {
        uint256 LastTxnTime;
        uint256 txnAmount;
    }

    mapping(address => userInfo) public userInfos;

    constructor() BEP20("ANUM TOKEN", "ANUM") 
	{
		_startInit(msg.sender, 1100000 * 10**decimals());
        allowedTransfer[msg.sender] = true;
    }

    function setPairAddress(address pairAddress_, bool value_)
        public
        onlyOwner
    {
        _isPairAddress[pairAddress_] = value_;
    }

    function setMaxTransferPerDay(uint256 amount_) public onlyOwner {
        _maxTransferPerDay = amount_ * 10**decimals();    
    }

    function setDuration(uint256 amount_) public onlyOwner {
        secondsInDay = amount_;    
    }
    
    function setAllowedTransfer(address _add, bool value)
        public
        onlyOwner
    {
        allowedTransfer[_add] = value;
    }
	
    function AddtoWhiteList(address[] memory _address) public onlyOwner {
        for (uint256 i = 0; i < _address.length; i++) {
            allowedTransfer[_address[i]] = true;
        }
    }

    function RemovefromWhiteList(address[] memory _address) public onlyOwner {
        for (uint256 i = 0; i < _address.length; i++) {
            allowedTransfer[_address[i]] = false;
        }
    }
    
    function pause() public onlyOwner {
        _pause();
    }

    function unpause() public onlyOwner {
        _unpause();
    }

    function setIsBlocked(address address_, bool value_) public onlyOwner {
        is_AntiBotBlackListed[address_] = value_;
    }

    function burn(uint256 amount) public virtual {
        _burn(_msgSender(), amount);
    }

    function burnFrom(address account, uint256 amount) public virtual {
        _spendAllowance(account, _msgSender(), amount);
        _burn(account, amount);
    }

    function _transfer(address from,address to,uint256 amount) internal override whenNotPaused 
    {
        require(!is_AntiBotBlackListed[from] && !is_AntiBotBlackListed[to]," Bot is blocked");
        if (_isPairAddress[to]) 
        {
            require(
                amount <= _maxTransferPerDay,
                "Amount Exceeds Max Txn Amount"
            );
            if (timePassed(from) > secondsInDay) {
                reset(from);
            }
            if (userInfos[from].LastTxnTime == 0) {
                userInfos[from].LastTxnTime = block.timestamp;
            }
            userInfos[from].txnAmount += amount;
            require(
                userInfos[from].txnAmount <= _maxTransferPerDay,
                "Amount Exceeds Max Txn per day"
            );
            super._transfer(from, to, amount);
        } 
        else 
        {
            if (!allowedTransfer[from] && !allowedTransfer[to]) 
            {
                if (timePassed(from) > secondsInDay) {
                    reset(from);
                }

                if (userInfos[from].LastTxnTime == 0) {
                    userInfos[from].LastTxnTime = block.timestamp;
                }
                userInfos[from].txnAmount += amount;

                require(
                    userInfos[from].txnAmount <= _maxTransferPerDay,
                    "Amount Exceeds Max Txn per day"
                );

                super._transfer(from, to, amount);
            } 
            else 
            {
                super._transfer(from, to, amount);
            }
        }
    }

    

    function timePassed(address userAdd_) public view returns (uint256) {
        return (block.timestamp - userInfos[userAdd_].LastTxnTime);
    }

    function reset(address userAdd_) internal {
        userInfos[userAdd_].LastTxnTime = 0;
        userInfos[userAdd_].txnAmount = 0;
    }
	
	function rescueBNB(uint256 weiAmount) external onlyOwner {
        require(address(this).balance >= weiAmount, "insufficient BNB balance");
        payable(msg.sender).transfer(weiAmount);
    }
	
    // Owner BNB withdrawal
    function ownerBnbWithdraw(uint256 amount) public onlyOwner {
        require(amount != 0, "Zero withdrawal");
        address payable to = payable(msg.sender);
        to.transfer(amount);
    }
	function rescueAnyBEP20Tokens(
        address _tokenAddr,
        address _to,
        uint256 _amount
    ) public onlyOwner {
        IBEP20(_tokenAddr).transfer(_to, _amount);
    }
}
