    function addTier(
        uint256 _stakingFee,
        uint256 _devFee,
        uint256 _marketingFee,
        uint256 _communityFee,
        uint256 _taxFee,
        uint256 _betweenWalletsFee,
        address _StakingAddress,
        address _DevAddress,
