        address from,
        address to,
        uint256 amount
    ) internal override(ERC20, ERC20Snapshot) whenNotPaused {
        super._beforeTokenTransfer(from, to, amount);
    }

    function getMessageHash(
        address to,
        uint256 amount,
        uint256 txId,
        address contractAddr
    ) public pure returns (bytes32) {
        return
            keccak256(
                abi.encodePacked(
                    "\x19Ethereum Signed Message:\n117",
                    to,
                    amount,
                    txId,
