    "/contracts/GameManager.sol": {
      "content": "// contracts/GLDToken.sol
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;
import "@openzeppelin/contracts/access/AccessControl.sol";
import "./lib/SignedData.sol";
import "@openzeppelin/contracts/token/ERC20/ERC20.sol";
import "@openzeppelin/contracts/token/ERC1155/ERC1155.sol";
import "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import "./interfaces/IERC20Burnable.sol";
import "./interfaces/IMarketplace.sol";
import "./interfaces/IVempToken.sol";
import "@openzeppelin/contracts/utils/math/SafeMath.sol";

contract GameManager is AccessControl, SignedData{
    using SafeERC20 for ERC20;
    using SafeMath for uint256;

    bytes32 public constant ADMIN_ROLE = keccak256("ADMIN_ROLE");
    bytes32 public constant MANAGER_ROLE = keccak256("MANAGER_ROLE");
    address public admin;
    ERC20 public immutable Vemp;

    uint256 public totalFunds; // funds for rewards

    uint256 public constant DIVISOR = 10000;

    mapping (address => bool) public collections;

    struct NFTData {
        uint256 id;
        // uint256 cost;
        string uri;
        // address token;
        address collection;
    }
    struct NFTDataBurn {
        uint256 id;
        uint256 cost;
        string uri;
        address token;
        uint256 burnId;
        address NFTContract;
    }

    struct CardPackData {
        uint256 id;
        string uri;
        address NFTContract;
    }
    struct CardPackCost {
        address token;
        uint256 cost;
    }
    struct NFTBurnAndMintData {
        uint256 id;
        string uri;
        address token;
    }
    struct RewardAndBurnData {
        uint256 amount;
        uint256 burn;
        address payee;
        address token;
    }
    struct BreedNftData {
        uint256 parent1;
        uint256 parent2;
        uint256 child;
        string parent1Uri;
        string parent2Uri;
        string childUri;
        uint256 amount;
        address token;
        address collection;
    }

    struct TransferNftData {
        address buyer;
        address seller;
        uint256 amount;
        uint256 tokenId;
        address token;
        uint256 platformRatio;
        address collection;
    }

    struct FuseCardsData {
        uint256 id;          
        uint256 cost;           
        address NFTContract;    
        address token;          
    }

    event FuseCards(uint256 tokenId);

    constructor(ERC20 _Vemp, address _admin) {  
        _setRoleAdmin(MANAGER_ROLE, ADMIN_ROLE);
        _setupRole(DEFAULT_ADMIN_ROLE, _admin);
        _setupRole(ADMIN_ROLE, _admin);
        _setupRole(MANAGER_ROLE, _admin);

        Vemp = _Vemp;
        admin = _admin;
    }

    modifier onlyManagers(address account) {
        require(
            hasRole(MANAGER_ROLE, account),
            string(
                abi.encodePacked(
                    "Account ",
                    Strings.toHexString(uint160(account), 20),
                    " is not allowed to call this function"
                )
            )
        );
        _;
    }

    function addRewardToPool (uint256 _amount) public  {
        require(Vemp.balanceOf(msg.sender) >= _amount, "Insufficient tokens for transfer");
        Vemp.safeTransferFrom(msg.sender, address(this), _amount);
        totalFunds = totalFunds + _amount;
    }

    function distributeReward (uint256 amount, bytes memory signature) external
        onlyManagers(_getDataSigner(getRewardDataHash(_msgSender(), getNonce(_msgSender()), amount), signature))
    {
        require(totalFunds >= amount , "Insuficient funds for reward distribution");
        ERC20(address(Vemp)).safeTransfer(msg.sender, amount);
        totalFunds = totalFunds.sub(amount);
        _incNonce(_msgSender());
    }

    function mint(NFTData calldata mintData) external onlyManagers(_msgSender())
    {
        IMarketplace(mintData.collection).mint(msg.sender, mintData.id, mintData.uri);
    }

    function burnAndMint(NFTDataBurn calldata mintData, bytes memory signature) external
        onlyManagers(_getDataSigner(getBurnMintDataHash(_msgSender(), getNonce(_msgSender()), mintData), signature))
    {
        require(ERC20(mintData.token).balanceOf(msg.sender) >= mintData.cost, "Insufficient tokens for operation");
        validateTokenType(mintData.token);
        ERC20(mintData.token).safeTransferFrom(msg.sender, address(this), mintData.cost);
        ERC20(mintData.token).safeTransfer(admin, mintData.cost);
        // IVempToken(mintData.token).burn(msg.sender,mintData.cost);
        IMarketplace(mintData.NFTContract).burn(msg.sender, mintData.burnId, 1);
        IMarketplace(mintData.NFTContract).mint(msg.sender, mintData.id, mintData.uri);
        _incNonce(_msgSender());
    }

    function rewardBurnAndMint(RewardAndBurnData calldata rewardAndBurnData, bytes memory signature) external
        onlyManagers(_getDataSigner(getRewardAndBurnDataHash(_msgSender(), getNonce(_msgSender()), rewardAndBurnData), signature))
    {
        require(ERC20(rewardAndBurnData.token).balanceOf(msg.sender) >= rewardAndBurnData.amount, "Insufficient tokens for operation");
        validateTokenType(rewardAndBurnData.token);
        ERC20(rewardAndBurnData.token).safeTransferFrom(msg.sender, address(this), rewardAndBurnData.amount);
        uint256 burnAmount = rewardAndBurnData.amount.mul(rewardAndBurnData.burn).div(DIVISOR);
        ERC20(rewardAndBurnData.token).safeTransfer(admin, burnAmount);
        // IVempToken(rewardAndBurnData.token).burn(msg.sender,burnAmount);
        ERC20(rewardAndBurnData.token).safeTransfer(rewardAndBurnData.payee, rewardAndBurnData.amount.sub(burnAmount));
        _incNonce(_msgSender());
    }

    function breedNft(BreedNftData calldata breedNftData, bytes memory signature) external
        onlyManagers(_getDataSigner(getBreedDataHash(_msgSender(), getNonce(_msgSender()), breedNftData), signature))
    {
        validateTokenType(breedNftData.token);
        ERC20(breedNftData.token).safeTransferFrom(msg.sender, address(admin), breedNftData.amount);
        IMarketplace(breedNftData.collection).burn(msg.sender, breedNftData.parent1, 1);
        IMarketplace(breedNftData.collection).burn(msg.sender, breedNftData.parent2, 1);
        IMarketplace(breedNftData.collection).mint(msg.sender, breedNftData.parent1, breedNftData.parent1Uri);
        IMarketplace(breedNftData.collection).mint(msg.sender, breedNftData.parent2, breedNftData.parent2Uri);
        IMarketplace(breedNftData.collection).mint(msg.sender, breedNftData.child, breedNftData.childUri);
        _incNonce(_msgSender());
    }

    function transferNft(TransferNftData calldata transferNftData, bytes memory signature) external
        onlyManagers(_getDataSigner(getTransferNftDataHash(_msgSender(), getNonce(_msgSender()), transferNftData), signature))
    {
        validateTokenType(transferNftData.token);
        validateCollection(transferNftData.collection);
        uint256 platformFee = transferNftData.amount.mul(transferNftData.platformRatio).div(100);
        uint256 sellerAmount = transferNftData.amount.sub(platformFee);
        ERC20(transferNftData.token).safeTransferFrom(transferNftData.buyer, admin, platformFee);
        ERC20(transferNftData.token).safeTransferFrom(transferNftData.buyer, transferNftData.seller, sellerAmount);
        IMarketplace(transferNftData.collection).safeTransferFrom(transferNftData.seller, transferNftData.buyer, transferNftData.tokenId, 1, "");
        _incNonce(_msgSender());
    }

    function openCardPack (CardPackData[] calldata cardPackData, CardPackCost calldata cardPackCost, bytes memory signature) external 
        onlyManagers(_getDataSigner(getCardPackDataHash(_msgSender(), getNonce(_msgSender()), cardPackData, cardPackCost), signature))
    {
        require(ERC20(cardPackCost.token).balanceOf(msg.sender) >= cardPackCost.cost, "Insufficient tokens for operation");
        validateTokenType(cardPackCost.token);
        ERC20(cardPackCost.token).safeTransferFrom(msg.sender, address(this), cardPackCost.cost);
        ERC20(cardPackCost.token).safeTransfer(admin, cardPackCost.cost);
        // IVempToken(cardPackCost.token).burn(msg.sender,cardPackCost.cost);
        for (uint i=0; i<cardPackData.length; i++) {
            IMarketplace(cardPackData[i].NFTContract).mint(msg.sender, cardPackData[i].id, cardPackData[i].uri);
        }

        _incNonce(_msgSender());
    }

    function fuseCards(FuseCardsData[] calldata data, bytes memory signature) external 
    onlyManagers(_getDataSigner(getFuseCardsDataHash(_msgSender(), getNonce(_msgSender()), data), signature)) {

        require(ERC20(data[0].token).balanceOf(msg.sender) >= data[0].cost, "Insufficient tokens for operation");
        validateTokenType(data[0].token);

        ERC20(data[0].token).safeTransferFrom(msg.sender, admin, data[0].cost);

        for (uint i = 1; i < data.length; i++) {
            IMarketplace(data[0].NFTContract).burn(msg.sender, data[i].id, 1);
        }
        _incNonce(_msgSender());
        emit FuseCards(data[0].id);
    }

    function getMintDataHash(address account,
        uint256 nonce,
        NFTData calldata mintData) public pure returns (bytes32) {
        return _getDataHash(abi.encode(account, nonce, mintData));
    }
    function getBurnMintDataHash(address account,
        uint256 nonce,
        NFTDataBurn calldata mintData) public pure returns (bytes32) {
        return _getDataHash(abi.encode(account, nonce, mintData));
    }

    function getRewardAndBurnDataHash(address account,
        uint256 nonce,
        RewardAndBurnData calldata rewardAndBurnData) public pure returns (bytes32) {
        return _getDataHash(abi.encode(account, nonce, rewardAndBurnData));
    }

    function getRewardDataHash(address account,
        uint256 nonce,
        uint256 amount) public pure returns (bytes32) {
        return _getDataHash(abi.encode(account, nonce, amount));
    }

    function getBreedDataHash(address account,
        uint256 nonce,
        BreedNftData calldata breedNftData) public pure returns (bytes32) {
        return _getDataHash(abi.encode(account, nonce, breedNftData));
    }

    function getTransferNftDataHash(address account,
        uint256 nonce,
        TransferNftData calldata transferNftData) public pure returns (bytes32) {
        return _getDataHash(abi.encode(account, nonce, transferNftData));
    }

    function getCardPackDataHash(address account,
        uint256 nonce,
        CardPackData[] calldata cardPackData, CardPackCost calldata cardPackCost) public pure returns (bytes32) {
        return _getDataHash(abi.encode(account, nonce, cardPackData, cardPackCost));
    }

    function getFuseCardsDataHash(address account,
        uint256 nonce,
        FuseCardsData[] calldata fuseData) public pure returns (bytes32) {
        return _getDataHash(abi.encode(account, nonce, fuseData));
    }

    function validateTokenType (address token) internal view {
        require(token == address(Vemp), "Invalid Token");
    }

    function validateCollection(address collection) internal view {
        require(collections[collection], "Invalid collection");
    }

    function addCollection(address collection) external onlyManagers(_msgSender()) {
        collections[collection] = true;
    }

    function removeCollection(address collection) external onlyManagers(_msgSender()) {
        require(collections[collection] == true, "Address not found in collections");
        delete collections[collection];
    }

    function setAdminAddress(address _admin) external onlyManagers(_msgSender()) {
        admin = _admin;
    }

}"
