    "/contracts/lib/SignedData.sol": {
      "content": "// SPDX-License-Identifier: GPL-3.0
pragma solidity ^0.8.0;


import "@openzeppelin/contracts/utils/cryptography/ECDSA.sol";

abstract contract SignedData {
    using ECDSA for bytes32;

    mapping(address => uint256) private _nonces;


    function getNonce(address account) public view returns (uint256) {
        return _nonces[account];
    }

    function _incNonce(address account) internal {
        unchecked {
            _nonces[account] += 1;
        }
    }

    function _getDataHash(bytes memory data) internal pure returns (bytes32) {
        return keccak256(data);
    }

    function _getDataSigner(bytes32 messageHash, bytes memory signature) internal pure returns (address) {
        return messageHash.toEthSignedMessageHash().recover(signature);
    }

}
"
    },
