interface IERC721CustomEnumerable {
    function tokenOfOwnerByIndex(address owner, uint256 index) external view returns (uint256 tokenId);
}

contract AmuletID is Ownable {
    mapping(address => bool) public approvedNFTContracts;
    address[] private allNFTContracts;

    // Function to update the list of approved NFT contracts
    function updateApprovedNFTContract(address _contract, bool _isApproved) external onlyOwner {
        approvedNFTContracts[_contract] = _isApproved;
        if (_isApproved && !isContractInArray(_contract)) {
            allNFTContracts.push(_contract);
        } else if (!_isApproved && isContractInArray(_contract)) {
            removeContractFromArray(_contract);
        }
    }

    // Function to check if a wallet holds any approved NFT
    function hasApprovedNFT(address _wallet) public view returns (bool) {
        for (uint256 i = 0; i < allNFTContracts.length; i++) {
            if (IERC721(allNFTContracts[i]).balanceOf(_wallet) > 0) {
                return true;
            }
        }
        return false;
    }

    // Helper function to check if a contract is in the array
    function isContractInArray(address _contract) private view returns (bool) {
        for (uint256 i = 0; i < allNFTContracts.length; i++) {
            if (allNFTContracts[i] == _contract) {
                return true;
            }
        }
        return false;
    }

    // Helper function to remove a contract from the array
    function removeContractFromArray(address _contract) private {
        uint256 length = allNFTContracts.length;
        for (uint256 i = 0; i < length; i++) {
            if (allNFTContracts[i] == _contract) {
                allNFTContracts[i] = allNFTContracts[length - 1];
                allNFTContracts.pop();
                break;
            }
        }
    }

    // Function to get the list of approved NFT contracts owned by a user and their Token IDs
    function getUserOwnedNFTs(address _wallet) public view returns (address[] memory, uint256[] memory) {
        address[] memory ownedNFTContracts = new address[](allNFTContracts.length);
        uint256[] memory ownedTokenIds = new uint256[](allNFTContracts.length);
        uint256 count = 0;

        for (uint256 i = 0; i < allNFTContracts.length; i++) {
            IERC721 nftContract = IERC721(allNFTContracts[i]);
            if (nftContract.balanceOf(_wallet) > 0) {
                IERC721CustomEnumerable nftEnumerableContract = IERC721CustomEnumerable(allNFTContracts[i]);
                uint256 tokenId = nftEnumerableContract.tokenOfOwnerByIndex(_wallet, 0);

                ownedNFTContracts[count] = allNFTContracts[i];
                ownedTokenIds[count] = tokenId;
                count++;
            }
        }

        // Resize the arrays to fit the actual number of owned NFTs
        return trimArrays(ownedNFTContracts, ownedTokenIds, count);
    }

    // Helper function to trim arrays
    function trimArrays(address[] memory contracts, uint256[] memory ids, uint256 count)
        private pure returns (address[] memory, uint256[] memory) {
        
        address[] memory trimmedContracts = new address[](count);
        uint256[] memory trimmedIds = new uint256[](count);
        for (uint256 i = 0; i < count; i++) {
            trimmedContracts[i] = contracts[i];
            trimmedIds[i] = ids[i];
        }
        return (trimmedContracts, trimmedIds);
    }
}
