    function swap(
        uint256 amount,
        address source,
        address target
    )
    external
    nonReentrant()
    {
        _removeLiquidity(amount, source, target);
        _addLiquidity(amount, target, source);

        emit TokensSwapped(amount, source, target, msg.sender);

