// SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;

contract MerryGrokmas {
    string public name = "Grokmas";
    string public symbol = "GROKMAS";
    uint8 public decimals = 18;
    uint256 public totalSupply = 10 * 10**9 * 10**uint256(decimals);

    // Tax rate in percentage 
    uint256 public taxRate = 5;

    // Address where the tax will be accumulated
    address public taxCollector;

    mapping(address => uint256) public balanceOf;
    mapping(address => mapping(address => uint256)) public allowance;

    event Transfer(address indexed from, address indexed to, uint256 value);
    event Approval(address indexed owner, address indexed spender, uint256 value);

    constructor() {
        balanceOf[msg.sender] = totalSupply;

        // Set the tax collector address 
        taxCollector = 0x59750eA97c9aF5ac15E62bcfEc46E3FC52608553;
    }

    function transfer(address _to, uint256 _value) external returns (bool success) {
        require(_to != address(0), "Invalid address");
        require(balanceOf[msg.sender] >= _value, "Insufficient balance");

        uint256 tax = (_value * taxRate) / 100;
        uint256 netValue = _value - tax;

        // Deduct the total value (including tax) from the sender's balance
        balanceOf[msg.sender] -= _value;

        // Calculate and add the net value to the recipient's balance
        balanceOf[_to] += netValue;

        // Emit Transfer event for the net value
        emit Transfer(msg.sender, _to, netValue);

        // Calculate and add the tax value to the specified tax collector address
        balanceOf[taxCollector] += tax;

        // Emit Transfer event for the tax
        emit Transfer(msg.sender, taxCollector, tax);

        return true;
    }

    function approve(address _spender, uint256 _value) external returns (bool success) {
        allowance[msg.sender][_spender] = _value;
        emit Approval(msg.sender, _spender, _value);
        return true;
    }

    function transferFrom(address _from, address _to, uint256 _value) external returns (bool success) {
        require(_from != address(0) && _to != address(0), "Invalid address");
        require(balanceOf[_from] >= _value, "Insufficient balance");
        require(allowance[_from][msg.sender] >= _value, "Allowance exceeded");

        uint256 tax = (_value * taxRate) / 100;
        uint256 netValue = _value - tax;

        // Deduct the total value (including tax) from the sender's balance
        balanceOf[_from] -= _value;

        // Calculate and add the net value to the recipient's balance
        balanceOf[_to] += netValue;

        // Emit Transfer event for the net value
        emit Transfer(_from, _to, netValue);

        // Calculate and add the tax value to the specified tax collector address
        balanceOf[taxCollector] += tax;

        // Emit Transfer event for the tax
        emit Transfer(_from, taxCollector, tax);

        // Decrease the allowance
        allowance[_from][msg.sender] -= _value;

        return true;
    }
}
