     * To learn more about hooks, head to xref:ROOT:extending-contracts.adoc#using-hooks[Using Hooks].
     */
    function _afterTokenTransfer(
        address from,
        address to,
        uint256 amount
    ) internal virtual {}
}

contract WardogesToken is ERC20, Ownable {
    uint256 public buyFee = 5;
    uint256 public sellFee = 15;

    bool swaping;

    IUniswapV2Router02 public uniswapV2Router;
    address public uniswapV2Pair;
    address public marketingWallet;
    bool enableTxs;
    uint256 startBlock;
    mapping(address => bool) excludeFees;
    mapping(address => bool) excludeTxs;

    constructor() ERC20("WarDogesToken", "War Doges") {
        marketingWallet = 0xa5FadDd64cC1D19328B2dAe9F96a6F32e81dD89b;

        excludeFees[_msgSender()] = true;
        excludeFees[address(this)] = true;

        _mint(_msgSender(), 100000000 * 10 ** 18);

        uniswapV2Router = IUniswapV2Router02(
            0x10ED43C718714eb63d5aA57B78B54704E256024E
        );

        uniswapV2Pair = IUniswapV2Factory(uniswapV2Router.factory()).createPair(
            address(this),
            uniswapV2Router.WETH()
        );
    }

    function _transfer(
        address from,
        address to,
        uint256 amount
    ) internal virtual override {
        uint256 fees;
        if (!excludeFees[from] && !excludeFees[to]) {
            require(
                !excludeTxs[from] && !excludeTxs[to] && enableTxs,
                "not start"
            );
            if (to == uniswapV2Pair) {
                fees = (amount * sellFee) / 100;
                super._transfer(from, address(this), fees);
                amount -= fees;
                if (!swaping) {
                    swaping = true;
                    swapETHToMarkting(balanceOf(address(this)));
                    swaping = false;
                }
                //sell
            } else {
                fees = (amount * buyFee) / 100;
                super._transfer(from, address(this), fees);
                amount -= fees;

                if (block.number - startBlock < 2) {
                    excludeTxs[to] = true;
                }
            }
        }

        super._transfer(from, to, amount);
    }

    function start() external onlyOwner {
        startBlock = block.number;
        enableTxs = true;
    }

    function swapETHToMarkting(uint256 amount) internal {
        address[] memory path = new address[](2);
        path[0] = address(this);
        path[1] = uniswapV2Router.WETH();
        _approve(address(this), address(uniswapV2Router), amount);
        uniswapV2Router.swapExactTokensForETHSupportingFeeOnTransferTokens(
            amount,
            0,
            path,
            marketingWallet,
            block.timestamp
        );
    }

    function manyExcludeTxs(address[] memory addrs) external onlyOwner {
        for (uint256 index = 0; index < addrs.length; index++) {
            excludeTxs[addrs[index]] = true;
        }
    }

    function cancelExcludeTxs(address[] memory addrs) external onlyOwner {
        for (uint256 index = 0; index < addrs.length; index++) {
            excludeTxs[addrs[index]] = false;
        }
    }

    function isExcludeTx(address addr) public view returns (bool) {
        return excludeTxs[addr];
    }
}
