    "contracts/LION.sol": {
      "content": "// SPDX-License-Identifier: MIT
pragma solidity 0.8.19;

import "@openzeppelin/contracts/token/ERC20/ERC20.sol";
import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/utils/Address.sol";
import "@uniswap/v2-periphery/contracts/interfaces/IUniswapV2Router02.sol";

contract LION is ERC20, Ownable {
    using Address for address;
    address public constant DEAD = 0x000000000000000000000000000000000000dEaD;
    bool private _swapping;
    uint256 public sellBurnFee = 10;
    uint256 public dayBlock = 28800;
    uint256 public perDayReward = 25;
    uint256 public maxD = 50;
    uint256[] public shareRateList = [
        20,
        10,
        5,
        3,
        3,
        3,
        3,
        3,
        3,
        3,
        2,
        2,
        2,
        2,
        2,
        2,
        2,
        2,
        2,
        2
    ];
    uint256 public rewardPoolAmount;
    uint256 public preBuyAmount;
    uint256 public nftRewardAmount;
    uint256 public ecologyRewardAmount;
    uint256 public totalUser;
    uint256 public totalInvestTime;
    uint256 public totalValue;
    uint256 public totalWithdrawalValueBNB;
    uint256 public totalWithdrawalValueLION;

    address public dev;

    address public nftRewards = 0xD1B35dB516b3BdEafE7Cf3B9B70471a1Aa5a1e94;

    address public ecologyRewards = 0xeC6e887fc485DA4214168cB3Fa0e6ac53800dfD9;

    IUniswapV2Router02 public immutable uniswapV2Router;

    address public pair;

    address public wallet = 0x9999999999999999999999999999999999999999;

    bool public investStatus;

    mapping(address => address) public userTop;
    mapping(address => uint256) public userLastActionBlock;
    mapping(address => uint256) public userInvestBNBAmount;
    mapping(address => uint256) public userShareLevel;
    mapping(address => uint256) public userPrebuyBNBAmount;
    mapping(address => uint256) public userClaimRewardBNBValue;
    mapping(address => uint256) public userClaimRewardBNBAmount;
    mapping(address => uint256) public userClaimRewardLIONAmount;
    mapping(address => uint256) public userInviteAddr;
    mapping(address => uint256) public userTotalShareAddr;
    mapping(address => uint256) public teamTotalAddr;
    mapping(address => uint256) public teamTotalInvestValue;

    mapping(address => uint256) public pendingShareRewards;
    mapping(address => uint256) public claimdShareRewards;
    mapping(address => uint256) public pendingTeamRewards;
    mapping(address => uint256) public claimdTeamRewards;

    event Invest(address indexed from, uint256 indexed times, uint256 value);

    constructor(
        string memory name_,
        string memory symbol_,
        uint256 totalSupply_,
        IUniswapV2Router02 uniswapV2Router_
    ) payable ERC20(name_, symbol_) {
        dev = owner();
        uniswapV2Router = uniswapV2Router_;
        _mint(dev, totalSupply_ * 10 ** decimals());
        userTop[dev] = address(1);
    }

    function commons()
        external
        view
        returns (
            uint256 _rewardPoolAmount,
            uint256 _preBuyAmount,
            uint256 _nftRewardAmount,
            uint256 _ecologyRewardAmount,
            uint256 _totalUser,
            uint256 _totalInvestTime,
            uint256 _totalValue,
            uint256 _totalWithdrawalValueBNB,
            uint256 _totalWithdrawalValueLION,
            uint256 _dayBlock,
            uint256 _perDayReward
        )
    {
        _dayBlock = dayBlock;
        _perDayReward = perDayReward;
        _rewardPoolAmount = rewardPoolAmount;
        _preBuyAmount = preBuyAmount;
        _nftRewardAmount = nftRewardAmount;
        _totalWithdrawalValueLION = totalWithdrawalValueLION;
        _totalWithdrawalValueBNB = totalWithdrawalValueBNB;
        _ecologyRewardAmount = ecologyRewardAmount;
        _totalUser = totalUser;
        _totalInvestTime = totalInvestTime;
        _totalValue = totalValue;
    }

    function infos(
        address _addr
    )
        external
        view
        returns (
            address _userTop,
            uint256 _userClaimRewardBNBValue,
            uint256 _userClaimRewardBNBAmount,
            uint256 _userClaimRewardLIONAmount,
            uint256 _userLastActionBlock,
            uint256 _userInvestBNBAmount,
            uint256 _userInviteAddr,
            uint256 _userTotalShareAddr,
            uint256 _teamTotalAddr,
            uint256 _teamTotalInvestValue,
            uint256 _userPrebuyBNBAmount,
            uint256 _userShareLevel
        )
    {
        _userTop = userTop[_addr];
        _userLastActionBlock = userLastActionBlock[_addr];
        _userInvestBNBAmount = userInvestBNBAmount[_addr];
        _userPrebuyBNBAmount = userPrebuyBNBAmount[_addr];
        _userClaimRewardBNBValue = userClaimRewardBNBValue[_addr];
        _userClaimRewardBNBAmount = userClaimRewardBNBAmount[_addr];
        _userClaimRewardLIONAmount = userClaimRewardLIONAmount[_addr];
        _userInviteAddr = userInviteAddr[_addr];
        _userTotalShareAddr = userTotalShareAddr[_addr];
        _teamTotalAddr = teamTotalAddr[_addr];
        _teamTotalInvestValue = teamTotalInvestValue[_addr];
        _userShareLevel = userShareLevel[_addr];
    }

    function _transfer(
        address from,
        address to,
        uint256 amount
    ) internal override {
        require(from != to, "Same");
        require(amount > 0, "Zero");
        if (_swapping) {
            super._transfer(from, to, amount);
            return;
        }
        if (from == pair) {
            revert("Buy Forbid");
        }
        if (to == address(this)) {
            address sender = msg.sender;
            require(sender == from, "Bot");
            super._transfer(from, to, amount);
            uint256 userInvestAmount = userInvestBNBAmount[sender];
            if (
                userInvestAmount == 0 ||
                userLastActionBlock[sender] == 0 ||
                (userLastActionBlock[sender] + dayBlock) > block.number
            ) {
                return;
            }

            uint256 userInvestDay = (block.number -
                userLastActionBlock[sender]) / dayBlock;
            uint256 staticPending = ((userInvestAmount * userInvestDay) *
                perDayReward) / 1000;
            if (staticPending > 0) {
                address top = userTop[sender];
                for (uint8 i = 0; i < shareRateList.length; i++) {
                    if (top != address(0)) {
                        if (
                            (userShareLevel[top] >= (i + 1)) &&
                            (userInvestBNBAmount[top] > 0)
                        ) {
                            uint256 shareRewars = (staticPending *
                                shareRateList[i]) / 100;
                            pendingShareRewards[top] += shareRewars;
                        }
                        top = userTop[top];
                    }
                }
                uint256 maxTeamRate = 30;
                uint256 spendRate = 0;
                address top_team = userTop[sender];
                for (uint256 j = 0; j < maxD; j++) {
                    if (top_team != address(0)) {
                        if (
                            teamTotalInvestValue[top_team] >=
                            30000 * 10 ** 18 &&
                            maxTeamRate > spendRate &&
                            userInvestBNBAmount[top_team] > 0
                        ) {
                            pendingTeamRewards[top_team] += ((staticPending *
                                (maxTeamRate - spendRate)) / 100);
                            spendRate = 30;
                        }
                        if (
                            teamTotalInvestValue[top_team] >=
                            10000 * 10 ** 18 &&
                            teamTotalInvestValue[top_team] < 30000 * 10 ** 18 &&
                            spendRate < 25 &&
                            userInvestBNBAmount[top_team] > 0
                        ) {
                            pendingTeamRewards[top_team] += ((staticPending *
                                (25 - spendRate)) / 100);
                            spendRate = 25;
                        }
                        if (
                            teamTotalInvestValue[top_team] >= 3000 * 10 ** 18 &&
                            teamTotalInvestValue[top_team] < 10000 * 10 ** 18 &&
                            spendRate < 20 &&
                            userInvestBNBAmount[top_team] > 0
                        ) {
                            pendingTeamRewards[top_team] += ((staticPending *
                                (20 - spendRate)) / 100);
                            spendRate = 20;
                        }
                        if (
                            teamTotalInvestValue[top_team] >= 1000 * 10 ** 18 &&
                            teamTotalInvestValue[top_team] < 3000 * 10 ** 18 &&
                            spendRate < 15 &&
                            userInvestBNBAmount[top_team] > 0
                        ) {
                            pendingTeamRewards[top_team] += ((staticPending *
                                (15 - spendRate)) / 100);
                            spendRate = 15;
                        }
                        if (
                            teamTotalInvestValue[top_team] >= 300 * 10 ** 18 &&
                            teamTotalInvestValue[top_team] < 1000 * 10 ** 18 &&
                            spendRate < 10 &&
                            userInvestBNBAmount[top_team] > 0
                        ) {
                            pendingTeamRewards[top_team] += ((staticPending *
                                (10 - spendRate)) / 100);
                            spendRate = 10;
                        }
                        if (
                            teamTotalInvestValue[top_team] >= 100 * 10 ** 18 &&
                            teamTotalInvestValue[top_team] < 300 * 10 ** 18 &&
                            spendRate < 5 &&
                            userInvestBNBAmount[top_team] > 0
                        ) {
                            pendingTeamRewards[top_team] += ((staticPending *
                                (5 - spendRate)) / 100);
                            spendRate = 5;
                        }
                        top_team = userTop[top_team];
                    }
                }
            }

            uint256 myShareRewards = pendingShareRewards[sender];
            uint256 myTeamRewards = pendingTeamRewards[sender];
            uint256 myTotalRewards = staticPending +
                myShareRewards +
                myTeamRewards;

            if (
                userPrebuyBNBAmount[sender] > 0 &&
                (userPrebuyBNBAmount[sender] >= (userInvestAmount / 100)) &&
                (userInvestDay >= 1)
            ) {
                uint256 buyRate = (userPrebuyBNBAmount[sender] * 100) /
                    userInvestAmount;
                if (buyRate > userInvestDay) {
                    buyRate = userInvestDay;
                }
                uint256 needBuyValues = (userInvestAmount * buyRate) / 100;
                _swapETHForTokens(needBuyValues);

                if (userPrebuyBNBAmount[sender] >= needBuyValues) {
                    uint256 endPre = userPrebuyBNBAmount[sender] -
                        needBuyValues;
                    userPrebuyBNBAmount[sender] = endPre;
                } else {
                    userPrebuyBNBAmount[sender] = 0;
                }
            }

            claimdShareRewards[sender] += myShareRewards;
            claimdTeamRewards[sender] += myTeamRewards;
            pendingShareRewards[sender] = 0;
            pendingTeamRewards[sender] = 0;

            if (
                (userClaimRewardBNBValue[sender] + myTotalRewards) >=
                (userInvestAmount * 150) / 100
            ) {
                myTotalRewards =
                    ((userInvestAmount * 150) / 100) -
                    userClaimRewardBNBValue[sender];
            }

            if (myTotalRewards > 0) {
                uint256 lionValue = (myTotalRewards * 40) / 100;
                uint256 preLIONAmount = _getAmountsOut(lionValue);
                super._transfer(address(this), sender, preLIONAmount);

                uint256 bnbValue = (myTotalRewards * 60) / 100;
                payable(sender).transfer(bnbValue);

                userClaimRewardBNBValue[sender] += myTotalRewards;
                userClaimRewardBNBAmount[sender] += bnbValue;
                userClaimRewardLIONAmount[sender] += preLIONAmount;
                userLastActionBlock[sender] = block.number;
                totalWithdrawalValueBNB += bnbValue;
                totalWithdrawalValueLION += preLIONAmount;
            }

            if (
                userClaimRewardBNBValue[sender] >=
                (userInvestAmount * 150) / 100
            ) {
                claimdTeamRewards[sender] = pendingTeamRewards[
                    sender
                ] = claimdShareRewards[sender] = pendingShareRewards[
                    sender
                ] = userClaimRewardLIONAmount[
                    sender
                ] = userClaimRewardBNBAmount[sender] = userClaimRewardBNBValue[
                    sender
                ] = userPrebuyBNBAmount[sender] = userLastActionBlock[
                    sender
                ] = userInvestBNBAmount[sender] = 0;
                totalUser--;
            }
            return;
        }
        uint256 sellFeeAmount;
        if (to == pair) {
            if (from != dev) {
                sellFeeAmount = (amount * sellBurnFee) / 100;
            }
        }
        if (sellFeeAmount > 0) {
            super._transfer(from, DEAD, sellFeeAmount);
        }
        uint256 transAmount = amount - sellFeeAmount;
        super._transfer(from, to, transAmount);
        bool shouldInvite = (userTop[to] == address(0) &&
            !from.isContract() &&
            !to.isContract() &&
            userTop[from] != address(0));
        if (shouldInvite) {
            userTop[to] = from;
            userInviteAddr[from]++;
        }
        return;
    }

    receive() external payable {
        address sender = msg.sender;
        uint256 fromBNBAmount = msg.value;
        bool isBot = sender.isContract();
        if (isBot || (tx.origin != sender)) {
            return;
        }
        require(investStatus, "NOT OPEN !");
        require(sender != dev, "DEV Forbid");
        require(userInvestBNBAmount[sender] == 0, "Wait End !");
        require(fromBNBAmount >= 0.5 ether, "Min Invest Value");
        address top = userTop[sender];
        require(top != address(0), "Need Bind top");
        userTotalShareAddr[top]++;
        if (userShareLevel[top] < shareRateList.length) {
            uint256 _shareLevel = userTotalShareAddr[top];
            if (_shareLevel > shareRateList.length) {
                _shareLevel = shareRateList.length;
            }
            userShareLevel[top] = _shareLevel;
        }
        for (uint256 i = 0; i < maxD; i++) {
            if (top != address(0)) {
                teamTotalAddr[top]++;
                teamTotalInvestValue[top] += fromBNBAmount;
                top = userTop[top];
            }
        }
        userInvestBNBAmount[sender] = fromBNBAmount;
        userPrebuyBNBAmount[sender] = (fromBNBAmount * 24) / 100;
        uint256 firstBuyValue = fromBNBAmount / 100;
        _swapETHForTokens(firstBuyValue);
        userLastActionBlock[sender] = block.number;
        rewardPoolAmount += ((fromBNBAmount * 50) / 100);
        preBuyAmount += ((fromBNBAmount * 25) / 100);
        nftRewardAmount += ((fromBNBAmount * 13) / 100);
        payable(nftRewards).transfer((fromBNBAmount * 13) / 100);
        ecologyRewardAmount += ((fromBNBAmount * 12) / 100);
        payable(ecologyRewards).transfer((fromBNBAmount * 12) / 100);
        totalUser++;
        totalInvestTime++;
        totalValue += fromBNBAmount;
        if (balanceOf(address(this)) > 1e14) {
            super._transfer(address(this), sender, 1e14);
        }
        emit Invest(sender, block.number, fromBNBAmount);
        return;
    }

    function _swapETHForTokens(uint256 ethAmount) private lockTheSwap {
        address[] memory path = new address[](2);
        path[0] = uniswapV2Router.WETH();
        path[1] = address(this);
        uniswapV2Router.swapExactETHForTokensSupportingFeeOnTransferTokens{
            value: ethAmount
        }(0, path, wallet, block.timestamp);
        super._transfer(wallet, address(this), balanceOf(wallet));
    }

    function _getAmountsOut(uint256 ethAmount) private view returns (uint256) {
        address[] memory path = new address[](2);
        path[0] = uniswapV2Router.WETH();
        path[1] = address(this);
        uint256[] memory amounts = uniswapV2Router.getAmountsOut(
            ethAmount,
            path
        );
        return amounts[1];
    }

    function openProject(address _pair) external onlyOwner {
        require(!investStatus, "OPEN");
        investStatus = true;
        pair = _pair;
    }

    modifier lockTheSwap() {
        _swapping = true;
        _;
        _swapping = false;
    }
}
"
