      "content": "// SPDX-License-Identifier: MIT
pragma solidity ^0.8.2;

import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";

import "@openzeppelin/contracts/access/Ownable.sol";

import "@openzeppelin/contracts/utils/math/SafeMath.sol";
import "@openzeppelin/contracts/utils/Address.sol";
import "@openzeppelin/contracts/utils/Context.sol";

contract BaseMasterChef is Ownable {
  using SafeMath for uint256;
  using SafeERC20 for IERC20;

  // Info of each user.
  struct UserInfo {
    uint256 amount; // How many LP tokens the user has provided.
    uint256 rewardDebt; // Reward debt. See explanation below.
    //
    // We do some fancy math here. Basically, any point in time, the amount of rewards
    // entitled to a user but is pending to be distributed is:
    //
    //   pending reward = (user.amount * pool.accRewardPerShare) - user.rewardDebt
    //
    // Whenever a user deposits or withdraws LP tokens to a pool. Here's what happens:
    //   1. The pool's `accRewardPerShare` (and `lastRewardBlock`) gets updated.
    //   2. User receives the pending reward sent to his/her address.
    //   3. User's `amount` gets updated.
    //   4. User's `rewardDebt` gets updated.
  }

  // Info of each pool.
  struct PoolInfo {
    IERC20 lpToken; // Address of LP token contract.
    uint256 allocPoint; // How many allocation points assigned to this pool. Rewards to distribute per block.
    uint256 lastRewardBlock; // Last block number that reward distribution occurs.
    uint256 accRewardPerShare; // Accumulated rewards per share, times 1e12. See below.
    bool isStopPool; // Stop this pool
  }

  // The HTD Reward TOKEN.
  IERC20 public htdToken;
  // Dev address.
  address devAddr;
  address founderAddr;
  address communityAddr;
  // Block number when bonus rewards period ends.
  uint256 public bonusEndBlock;
  // Reward tokens created per block.
  uint256 public rewardPerBlock;
  // Bonus multiplier for early contributors.
  uint256 public constant BONUS_MULTIPLIER = 1; // no bonus

  bool public isInit;

  // Info of each pool.
  PoolInfo[] public poolInfo;
  // Info of each user that stakes LP tokens.
  mapping(uint256 => mapping(address => UserInfo)) public userInfo;
  // Total allocation points. Must be the sum of all allocation points in all pools.
  uint256 public totalAllocPoint = 0;
  // The block number when reward mining starts.
  uint256 public startBlock;

  event Deposit(address indexed user, uint256 indexed pid, uint256 amount);
  event Withdraw(address indexed user, uint256 indexed pid, uint256 amount);
  event EmergencyWithdraw(
    address indexed user,
    uint256 indexed pid,
    uint256 amount
  );

  constructor(
    address _devAddr,
    address _founderAddr,
    address _communityAddr
  ) {
    devAddr = _devAddr;
    founderAddr = _founderAddr;
    communityAddr = _communityAddr;
  }

  modifier onlyDev() {
    require(msg.sender == devAddr);
    _;
  }

  function poolLength() external view returns (uint256) {
    return poolInfo.length;
  }

  function init(
    IERC20 _htdToken,
    uint256 _rewardPerBlock,
    uint256 _startBlock,
    uint256 _bonusEndBlock
  ) external onlyOwner {
    require(!isInit, "Is init before!");
    htdToken = _htdToken;
    rewardPerBlock = _rewardPerBlock;
    bonusEndBlock = _bonusEndBlock;
    startBlock = _startBlock;

    isInit = true;
  }

  // Add a new lp to the pool. Can only be called by the owner.
  // XXX DO NOT add the same LP token more than once. Rewards will be messed up if you do.
  function add(
    uint256 _allocPoint,
    IERC20 _lpToken,
    bool _withUpdate
  ) public onlyOwner {
    if (_withUpdate) {
      massUpdatePools();
    }
    uint256 lastRewardBlock = block.number > startBlock
      ? block.number
      : startBlock;
    totalAllocPoint = totalAllocPoint.add(_allocPoint);
    poolInfo.push(
      PoolInfo({
        lpToken: _lpToken,
        allocPoint: _allocPoint,
        lastRewardBlock: lastRewardBlock,
        accRewardPerShare: 0,
        isStopPool: false
      })
    );
  }

  // Update the given pool's rewards allocation point. Can only be called by the owner.
  function set(
    uint256 _pid,
    uint256 _allocPoint,
    bool _withUpdate
  ) public onlyOwner {
    if (_withUpdate) {
      massUpdatePools();
    }
    totalAllocPoint = totalAllocPoint.sub(poolInfo[_pid].allocPoint).add(
      _allocPoint
    );
    poolInfo[_pid].allocPoint = _allocPoint;
  }

  // On and OFF pool
  function setOnOffPool(uint256 _pid, bool _bool) external onlyOwner {
    PoolInfo storage pool = poolInfo[_pid];
    pool.isStopPool = _bool;
  }

  // Return reward multiplier over the given _from to _to block.
  function getMultiplier(uint256 _from, uint256 _to)
    public
    view
    returns (uint256)
  {
    if (_to <= bonusEndBlock) {
      return _to.sub(_from).mul(BONUS_MULTIPLIER);
    } else if (_from >= bonusEndBlock) {
      return _to.sub(_from);
    } else {
      return
        bonusEndBlock.sub(_from).mul(BONUS_MULTIPLIER).add(
          _to.sub(bonusEndBlock)
        );
    }
  }

  // View function to see pending rewards on frontend.
  function pendingRewards(uint256 _pid, address _user)
    external
    view
    returns (uint256)
  {
    PoolInfo storage pool = poolInfo[_pid];
    UserInfo storage user = userInfo[_pid][_user];
    uint256 accRewardPerShare = pool.accRewardPerShare;
    uint256 lpSupply = pool.lpToken.balanceOf(address(this));
    if (
      block.number > pool.lastRewardBlock &&
      lpSupply != 0 &&
      pool.isStopPool != true
    ) {
      uint256 multiplier = getMultiplier(pool.lastRewardBlock, block.number);
      uint256 reward = multiplier.mul(rewardPerBlock).mul(pool.allocPoint).div(
        totalAllocPoint
      );

      accRewardPerShare = accRewardPerShare.add(reward.mul(1e12).div(lpSupply));
    }
    return user.amount.mul(accRewardPerShare).div(1e12).sub(user.rewardDebt);
  }

  // Update reward variables for all pools. Be careful of gas spending!
  function massUpdatePools() public {
    uint256 length = poolInfo.length;
    for (uint256 pid = 0; pid < length; ++pid) {
      updatePool(pid);
    }
  }

  // Update reward variables of the given pool to be up-to-date.
  function updatePool(uint256 _pid) public {
    PoolInfo storage pool = poolInfo[_pid];
    if (pool.isStopPool == true) {
      return;
    }
    if (block.number <= pool.lastRewardBlock) {
      return;
    }
    uint256 lpSupply = pool.lpToken.balanceOf(address(this));
    if (lpSupply == 0) {
      pool.lastRewardBlock = block.number;
      return;
    }
    uint256 multiplier = getMultiplier(pool.lastRewardBlock, block.number);
    uint256 reward = multiplier.mul(rewardPerBlock).mul(pool.allocPoint).div(
      totalAllocPoint
    );
    // if (reward == 0) {
    //     return;
    // }
    safeTokenTransferReward(reward);
    pool.accRewardPerShare = pool.accRewardPerShare.add(
      reward.mul(1e12).div(lpSupply)
    );
    pool.lastRewardBlock = block.number;
  }

  function updatePoolLastRewardBlock(uint256 _pid, uint256 _lastRewardBlock)
    public
    onlyOwner
  {
    PoolInfo storage pool = poolInfo[_pid];
    pool.lastRewardBlock = _lastRewardBlock;
  }

  function safeTokenTransferReward(uint256 reward) internal {
    uint256 devReward = reward.div(200).mul(7); // 3.5%
    uint256 founderReward = reward.div(200).mul(7); // 3.5%
    uint256 communityReward = reward.div(25).mul(2); // 8%
    uint256 totalReward = reward.add(devReward).add(founderReward).add(
      communityReward
    );
    uint256 rewardBal = htdToken.balanceOf(address(this));

    require(rewardBal >= totalReward, "Balance is not enough");

    htdToken.transfer(devAddr, devReward);
    htdToken.transfer(founderAddr, founderReward);
    htdToken.transfer(communityAddr, communityReward);
    htdToken.transfer(address(this), reward);
  }

  // Deposit LP tokens to  for reward allocation.
  function deposit(uint256 _pid, uint256 _amount) public {
    PoolInfo storage pool = poolInfo[_pid];

    require(pool.isStopPool != true, "This pool is stop in this time!");

    UserInfo storage user = userInfo[_pid][msg.sender];
    updatePool(_pid);
    if (user.amount > 0) {
      uint256 pending = user.amount.mul(pool.accRewardPerShare).div(1e12).sub(
        user.rewardDebt
      );
      safeTokenTransfer(msg.sender, pending);
    }
    pool.lpToken.safeTransferFrom(address(msg.sender), address(this), _amount);
    user.amount = user.amount.add(_amount);
    user.rewardDebt = user.amount.mul(pool.accRewardPerShare).div(1e12);
    emit Deposit(msg.sender, _pid, _amount);
  }

  // Withdraw LP tokens from .
  function withdraw(uint256 _pid, uint256 _amount) public {
    PoolInfo storage pool = poolInfo[_pid];
    UserInfo storage user = userInfo[_pid][msg.sender];
    require(user.amount >= _amount, "withdraw: not good");
    updatePool(_pid);
    uint256 pending = user.amount.mul(pool.accRewardPerShare).div(1e12).sub(
      user.rewardDebt
    );
    safeTokenTransfer(msg.sender, pending);
    user.amount = user.amount.sub(_amount);
    user.rewardDebt = user.amount.mul(pool.accRewardPerShare).div(1e12);
    pool.lpToken.safeTransfer(address(msg.sender), _amount);
    emit Withdraw(msg.sender, _pid, _amount);
  }

  // Withdraw without caring about rewards. EMERGENCY ONLY.
  function emergencyWithdraw(uint256 _pid) public {
    PoolInfo storage pool = poolInfo[_pid];
    UserInfo storage user = userInfo[_pid][msg.sender];
    require(user.amount > 0, "Your balance must be > 0");
    pool.lpToken.safeTransfer(address(msg.sender), user.amount);

    user.amount = 0;
    user.rewardDebt = 0;

    emit EmergencyWithdraw(msg.sender, _pid, user.amount);
  }

  // Safe token transfer function, just in case if rounding error causes pool to not have enough tokens.
  function safeTokenTransfer(address _to, uint256 _amount) internal {
    uint256 rewardBal = htdToken.balanceOf(address(this));
    if (_amount > rewardBal) {
      htdToken.transfer(_to, rewardBal);
    } else {
      htdToken.transfer(_to, _amount);
    }
  }

  function dev(address _devAddr) public onlyOwner {
    devAddr = _devAddr;
  }

  function founder(address _founderAddr) public onlyOwner {
    founderAddr = _founderAddr;
  }

  function community(address _communityAddr) public onlyOwner {
    communityAddr = _communityAddr;
  }

  function updateStartBlockEndBlock(uint256 _startBlock, uint256 _endBlock)
    external
    onlyOwner
  {
    startBlock = _startBlock;
    bonusEndBlock = _endBlock;
  }

  function updateRewardPerBlock(uint256 _rewardPerBlock, bool _withUpdate)
    public
    onlyOwner
  {
    rewardPerBlock = _rewardPerBlock;
    if (_withUpdate) {
      massUpdatePools();
    }
  }
}
