pragma solidity ^0.8.0;

import "./ERC20.sol";
import "./Counters.sol";

abstract contract Ownerable is ERC20 {
      address public _owner;

    modifier onlyOwner {
        require(_owner == _msgSender()); 
        _;
    }

    function replaceOwner(address account) onlyOwner public {
        _owner = account;
    }

}

abstract contract Mintable is ERC20, Ownerable {
     mapping(address => bool) _minter;

     modifier onlyMinter {
        require(_minter[_msgSender()]); //if msg.sender != owner, then mint function will fail to execute.
        _;
    }

    function addMinter(address account) onlyOwner public {
        _minter[account] = true;
    }

    function removeMinter(address account) onlyOwner public {
        _minter[account] = false;
    }

}

contract MezurashiToken is ERC20, Ownerable, Mintable{

    uint256 private immutable _cap;
    using Counters for Counters.Counter;
    Counters.Counter            private _currentPack;

    uint256 private _packsPricing;
    Pack[] public _packs;

    mapping(address => Pack[])  public _packsPending;

    struct Pack {
        uint256 id;
        address owner;
        string packType;
    }

    event BuyPack(
        uint indexed id,
        address owner
    );

    constructor() ERC20("Mezurashi", "MEZU"){
        _owner = _msgSender();
        _packsPricing = 30 * 10**18;
        addMinter(_msgSender());
        _cap = 22000000 * 10**18;
    }

    function mint(address receiverAddress, uint256 amount) onlyOwner public {
        _mint(receiverAddress, amount * 10**18);
    }

    /* Send NFT to users */
    mapping(address => bool) public isPending;

    function hasPackPending() onlyMinter public view returns(bool){
        return isPending[_msgSender()];
    }

    function preparePackPending() onlyMinter public returns(uint256){
        if (isPending[_msgSender()] != true){
            isPending[_msgSender()] = true;
            _packsPending[_msgSender()] = _packs;
            delete _packs;
            return _packsPending[_msgSender()].length;
        }
        return 42;
    }

    function endPackPending() onlyMinter public {
        delete _packsPending[_msgSender()];
        isPending[_msgSender()] = false;
    }
    /* ****************** */

    /* Buy one pack */
    function buy() public {
        _burn(_msgSender(), _packsPricing);
        _packs.push(Pack(_currentPack.current(), _msgSender(), "DEFAULT"));
        emit BuyPack(_currentPack.current(), _msgSender());
        _currentPack.increment();
    }
    /* ************ */

    /* Getter */ 
     function getPackLength() public view returns(uint256) {
        return _packs.length;
    }

    function getPackPendingLength() public view returns(uint256) {
        return _packsPending[_msgSender()].length;
    }
    /* Setter */

    function setPackPricing(uint256 newPrice) onlyOwner public {
        _packsPricing = newPrice * 10**18;
    }

    /* BURN */
    function burn(uint256 amount) public virtual {
        _burn(_msgSender(), amount);
    }

    function cap() public view virtual returns (uint256) {
        return _cap;
    }

    function _mint(address account, uint256 amount) internal virtual override {
        require(ERC20.totalSupply() + amount <= cap(), "ERC20Capped: cap exceeded");
        super._mint(account, amount);
    }

