      "content": "// SPDX-License-Identifier: MIT
pragma solidity ^0.8.9;

import "@openzeppelin/contracts/token/ERC20/ERC20.sol";
import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/utils/math/SafeMath.sol";
import "@openzeppelin/contracts/security/ReentrancyGuard.sol";

contract StakeContract is Ownable, ReentrancyGuard {
    using SafeMath for uint;
    ERC20 public tokenAddress;
    ERC20 public bnbAddress;
    uint256 public supplyToken;
    uint256 public lastRewardReleaseTime;
    uint256 public monthsRewards = 12;
    uint256 public daysRewards = 30;

    uint256 public constant secondsInADay = 86400; // 24 hours in seconds
    uint256 public constant percentageMultiplier = 10000; // To support 2 decimal points precision

    uint256 public feePercentage; // Fee for withdrawal (e.g., 1000 for 10%)
    address public walletToReceiveFee;
    address public ownerAddress;

    struct StakeInfo {
        uint256 stakeId;
        uint256 stakedAmount;
        uint256 stakingTime;
        bool inStake;
    }

    mapping(address => StakeInfo[]) public stakers;

    event TokensStaked(address indexed user, uint256 amount);
    event RewardsWithdrawn(address indexed user, uint256 amount);
    event StakeRemoved(address indexed user, uint256 amount);
    event TokensTransferred(
        address indexed tokenAddress,
        address indexed recipient,
        uint256 amount
    );

    constructor(
        ERC20 _tokenAddress,
        ERC20 _bnbAddress,
        uint256 _initialFeePercentage,
        uint256 _totalSupply
    ) {
        tokenAddress = _tokenAddress;
        bnbAddress = _bnbAddress;
        lastRewardReleaseTime = block.timestamp;
        supplyToken = _totalSupply;

        setFeePercentage(_initialFeePercentage);
    }

    function stake(uint256 _amount) external nonReentrant {
        require(_amount > 0, "Staked amount should be greater than 0");
        require(
            tokenAddress.balanceOf(msg.sender) >= _amount,
            "Not enough tokens to stake"
        );

        tokenAddress.approve(address(this), _amount);
        require(
            tokenAddress.transferFrom(msg.sender, address(this), _amount),
            "Error: Error transferring tokens to the contract"
        );

        // Se o usuário já tem um stake, apenas incrementa o valor e atualiza o timestamp
        stakers[msg.sender].push(
                StakeInfo({
                    stakeId: stakers[msg.sender].length,
                    stakedAmount: _amount,
                    stakingTime: block.timestamp,
                    inStake: true
                })
            );

        emit TokensStaked(msg.sender, _amount);
    }

    function calculateRewards(address stakerAddress) public view returns (uint256) {
        require(stakers[stakerAddress].length > 0, "No stake found for this address");

        uint256 totalRewards = 0;
        uint256 poolRewards = rewardsPool();
        uint256 bnbPerToken = poolRewards.mul(1e18).div(supplyToken);

        for (uint i = 0; i < stakers[stakerAddress].length; i++) {
            if (stakers[stakerAddress][i].inStake) {
                uint256 timeSinceStake = block.timestamp - stakers[stakerAddress][i].stakingTime;
                uint256 minutesSinceStake = timeSinceStake.div(60); // Convert seconds to minutes

                uint256 individualReward = stakers[stakerAddress][i].stakedAmount.mul(bnbPerToken);
                uint256 totalRewardsPerMonth = individualReward.div(monthsRewards);
                uint256 totalRewardsPerDay = totalRewardsPerMonth.div(daysRewards);
                uint256 totalRewardsPerHour = totalRewardsPerDay.div(24);
                uint256 accumulatedReward = totalRewardsPerHour.mul(minutesSinceStake).div(60);

                totalRewards += accumulatedReward;
            }
        }

        return totalRewards.div(1e18); // Convertendo para obter o valor em Ether
    }

    function rewardsPool() public view returns (uint256) {
        uint256 poolRewards = bnbAddress.balanceOf(address(this));
        return poolRewards;
    }

    function stakedsTokens() public view returns (uint256) {
        uint256 poolRewards = tokenAddress.balanceOf(address(this));
        return poolRewards;
    }

    function withdrawRewards() external nonReentrant {
        // Primeiro, calculamos o total de rewards para o usuário
        uint256 totalRewards = calculateRewards(msg.sender);

        require(totalRewards > 0, "No rewards to withdraw");

        // Transferimos os rewards para o usuário
        bnbAddress.transfer(msg.sender, totalRewards);

        // Após a retirada, atualizamos o timestamp de cada stake do usuário
        for (uint i = 0; i < stakers[msg.sender].length; i++) {
            if (stakers[msg.sender][i].inStake) {
                stakers[msg.sender][i].stakingTime = block.timestamp;
            }
        }

        emit RewardsWithdrawn(msg.sender, totalRewards);
    }

    function removeStake() external nonReentrant {
        uint256 totalStakedAmount = 0;

        for (uint i = 0; i < stakers[msg.sender].length; i++) {
            if (stakers[msg.sender][i].inStake) {
                totalStakedAmount += stakers[msg.sender][i].stakedAmount;
                // Define o stake como inativo após a retirada
                stakers[msg.sender][i].inStake = false;
            }
        }

        require(totalStakedAmount > 0, "No stake to remove");

        // Calcula a taxa a ser deduzida
        uint256 feeAmount = (totalStakedAmount * feePercentage) /
            percentageMultiplier;
        uint256 amountAfterFee = totalStakedAmount - feeAmount;

        // Transfere a taxa para o endereço designado
        tokenAddress.transfer(walletToReceiveFee, feeAmount);
        // Transfere o montante após a dedução da taxa de volta para o usuário
        tokenAddress.transfer(msg.sender, amountAfterFee);

        emit StakeRemoved(msg.sender, totalStakedAmount);
    }

    function setFeePercentage(uint256 _newFeePercentage) public onlyOwner {
        require(
            _newFeePercentage <= percentageMultiplier,
            "Fee percentage should be in valid range (0 to 10000)"
        );

        feePercentage = _newFeePercentage;
    }

    function setTimesRewards(uint256 _months, uint256 _days) public onlyOwner {
        require(_months > 0, "Months should be greater than 0");
        require(_days > 0, "Days should be greater than 0");

        monthsRewards = _months;
        daysRewards = _days;
    }

    function withdrawToken(uint256 _amount) external onlyOwner {
        uint256 contractBalance = bnbAddress.balanceOf(address(this));
        require(
            contractBalance >= _amount,
            "Not enough tokens in the contract"
        );

        bnbAddress.approve(address(this), _amount);

        bnbAddress.transfer(ownerAddress, _amount);
    }

    function totalStakedAmountWallet(
        address stakerAddress
    ) public view returns (uint256) {
        uint256 total = 0;

        for (uint i = 0; i < stakers[stakerAddress].length; i++) {
            if (stakers[stakerAddress][i].inStake) {
                total += stakers[stakerAddress][i].stakedAmount;
            }
        }

        return total;
    }

    function setSupplyToken(uint256 _supply) public onlyOwner {
        require(
            _supply > 0,
            "Reward calculation base should be greater than 0"
        );

        supplyToken = _supply;
    }

    function setWalletToReceiveFee(address _wallet) public onlyOwner {
        walletToReceiveFee = _wallet;
    }

    function setWalletOwner(address _wallet) public onlyOwner {
        ownerAddress = _wallet;
    }

    function withdrawBNB() external onlyOwner nonReentrant {
        uint256 balance = address(this).balance;
        payable(ownerAddress).transfer(balance);
        balance = 0;
    }
}
"
