pragma solidity ^0.8.4;










/// @custom:security-contact contact@getfitmining.com
contract DOCK is Pausable, Ownable, IERC721Receiver {

    mapping ( address => bool ) private  allowedContracts;
    bool private active;

    uint stakeFees;
    uint seed = 0;
    mapping (uint => tokenData) public Tokens;
    mapping(address => uint[]) public userDocks;
    mapping(address => uint) public userSlots;
    uint public docksNumber;
    uint public maxSlots;
    mapping (uint => uint) public prices;
    mapping (uint => uint) public pricesToken;
    mapping (uint => uint) public slotCurrency;

    bytes32 private tknHash;
    uint256 public total;
    bool public partCom;


    VAB public nftCon;
    PARTNERSHIP_MASTER public partnershipCon;
    ERC20 public token20;

    constructor() Ownable() {
        active = true;
        total = 0;
        maxSlots = 4;
        nftCon = VAB(0x95C1B791d5C76ef25eE87815B8FaC68182eD3880);
        token20 = ERC20(0xDD2D2521d939c63360d19A6397ccD542759B2740);
        partnershipCon = PARTNERSHIP_MASTER(0x516b4d8286b90B34591E0B9b7CB9204f6ef3E9F1);
        stakeFees = 3000000000000000;
    }

    struct tokenData {address owner;address int_wallet; uint32 _days;}
    struct intWalletData {uint256 tokenId;address owner;uint32 _days;}
    struct userDock {uint _userSlots;uint[] _userDock;}
    struct configData {uint _maxSlots;uint _stakeFees; uint[] _currencies; uint[] prices; uint[] pricesToken;}

    event stake(uint tokenId,address owner,uint32 date);
    event unstake(uint tokenId,address owner);

    //Prices Fees Config    
    function setSlotCurrency(uint _slotNumber, uint _currency) public onlyOwner {
        require(_currency<3, "Invalid currency");
        slotCurrency[_slotNumber] = _currency;
    }
    function setPrice(uint _slotNumber, uint _price) public onlyOwner {
        prices[_slotNumber] = _price;
    }
    function setPriceToken(uint _slotNumber, uint _price) public onlyOwner {
        pricesToken[_slotNumber] = _price;
    }
    function setFees (uint _amount) public onlyOwner returns (bool) {
        stakeFees = _amount;
        return true;
    }
    function getFees () public view returns (uint) {
        return stakeFees;
    }
    function setMaxSlots (uint _nbr) public onlyOwner {
        maxSlots = _nbr;
    }

    function getConfig() public view returns (configData memory) {
        uint[] memory _currencies = new uint[](maxSlots-1);
        uint[] memory _prices = new uint[](maxSlots-1);
        uint[] memory _pricesToken = new uint[](maxSlots-1);
        for (uint256 i = 2; i <= maxSlots; i++) {
            _currencies[i-2] = slotCurrency[i];
            _prices[i-2] = prices[i];
            _pricesToken[i-2] = pricesToken[i];
        }
        configData memory data = configData(
            maxSlots,
            stakeFees,
            _currencies,
            _prices,
            _pricesToken
        );
        return data;
    }
    function getUserDock(address user) public view returns (userDock memory) {
        return userDock(
            userSlots[user],
            userDocks[user]
        );
    }
    //Prices Fees Config//

    function appStakeNft(string memory _tkn, uint256 tokenId, address owner, uint32 _days) public payable returns (bool) {
        require(keccak256(abi.encodePacked(_tkn)) == tknHash, "App not allowed");
        if(userSlots[owner] == 0) userSlots[owner] = 1;
        require(msg.value >= stakeFees, "Not enough BNB, check fees!");
        require(userDocks[owner].length < userSlots[owner], "Out of slots");
        address _owner = nftCon.ownerOf(tokenId);
        require(owner == _owner, "Owner not found");
        require(_owner != address(this), "Dock full");
        if(partCom){
            partnersCom(0, stakeFees, owner);
        }
        return _holdItem(owner, msg.sender, tokenId, _days);
    }
    function appUnstakeNft (string memory _tkn, uint tknId) public whenNotPaused returns (bool) {
        require(keccak256(abi.encodePacked(_tkn)) == tknHash, "App not allowed");
        return _releaseItemOwner(tknId);
    }

    function _holdItem (address from, address intWallet, uint256 tokenId, uint32 _days) private returns (bool){
        ERC721(nftCon).safeTransferFrom(from, address(this), tokenId);
        Tokens[tokenId] = tokenData(from, intWallet,_days);
        userDocks[from].push(tokenId);
        emit stake(
            tokenId,
            from,
            _days
        );
        return true;
    }
    function _releaseItemOwner (uint tokenId) private returns (bool){
        address owner = Tokens[tokenId].owner;
        if(tokenId > 0){
            ERC721(nftCon).safeTransferFrom(address(this), owner, tokenId);
            removeItemFromDock(owner, tokenId);            
            emit unstake(
                tokenId,
                owner
            );
            return true;
        }
        return false;
    }

    function addSlotToDock(string memory _tkn, address owner, uint32 _currency) public payable {
        // currency [0, 1, 2]  -  Coin, Token, both
        if(userSlots[owner] == 0){
            userSlots[owner] = 1;
            docksNumber += 1;
        }
        require(keccak256(abi.encodePacked(_tkn)) == tknHash, "App not allowed");
        require(userSlots[owner] <= maxSlots, "Max slots reached");
        require(_currency == 0 || _currency == 1, "Invalid currency");
        slotCreationFees(msg.value, owner, _currency);
        userSlots[owner] += 1;

    }
    function slotCreationFees(uint amount, address owner, uint32 _currency) private returns (bool) {
        require(slotCurrency[userSlots[owner]+1] == 2 || slotCurrency[userSlots[owner]+1] == _currency, "Currency not available for this slot");
        if(_currency == 1){
            require(pricesToken[userSlots[owner]+1]>0, "Price not set yet");
            require(token20.balanceOf(msg.sender) >= pricesToken[userSlots[owner]+1], "Not enough funds");
            token20.transferFrom(msg.sender, address(this), pricesToken[userSlots[owner]+1]);
            return true;
        } else if(_currency == 0){

            require(prices[userSlots[owner]+1]>0, "Price not set yet");
            require(amount >= prices[userSlots[owner]+1], "Not enough funds");
            return true;
        }
        else{
            return false;
        }
    }
    function isOwnerOfAll(address _owner, uint[] calldata _nfts) public view returns (bool) {
        uint owned = 0;
        for (uint256 i = 0; i < _nfts.length; i++) {
            if(Tokens[_nfts[i]].owner == _owner){
                owned += 1;
            }
        }
        return _nfts.length == owned;
    }
    function ownerOf (uint256 tokenId) public view returns (address _owner, address _int_wallet ,uint32 _day, uint32 _month, uint32 _year, uint32 _days){
        _owner =  nftCon.ownerOf(tokenId);
        _int_wallet =  address(this);
        _day = 0;
        _month = 0;
        _year = 0;
        _days = 0;
    }
    function contains (uint[] storage list, uint element) internal view returns (bool) {
        bool exists = false;    
        for (uint i=0; i < list.length; i++) {
            if (element == list[i]) {
                exists = true;
                break;
            }
        }
        return exists;
    }

    //ADMIN

    function adminUnstake (uint tokenId) public onlyOwner {
        _releaseItemOwner(tokenId);
    }
    function adminUnstakeAll () public onlyOwner {
        uint nbr = ERC721(nftCon).balanceOf(address(this));
        for (uint256 index = 0; index < nbr; index++) {
            uint id = nftCon.tokenOfOwnerByIndex(address(this), index);
            _releaseItemOwner(id);
        }
    }
    function adminUnstakeNbr (uint nbr) public onlyOwner {
        for (uint256 index = 0; index < nbr; index++) {
            uint id = nftCon.tokenOfOwnerByIndex(address(this), index);
            _releaseItemOwner(id);
        }
    }
    function adminTransferNft (address wallet, uint tokenId) public onlyOwner {
        address owner = Tokens[tokenId].owner; 
        ERC721(nftCon).safeTransferFrom(address(this), wallet, tokenId);        
        removeItemFromDock(owner, tokenId);
    }
    
    //ADMIN
    function removeItemFromDock(address owner, uint item) internal returns (bool) {
        uint l = userDocks[owner].length;
        if(l == 1){
            userDocks[owner].pop();
            return true;
        }
        for (uint i = 0; i < l; i++) {
            if (userDocks[owner][i] == item) {
                userDocks[owner][i] = userDocks[owner][l-1];
                userDocks[owner].pop();
                delete Tokens[item];
                total = total - 1;
                return true;
            }
        }
        return false;
    }

    function setPartCom(bool _value) public onlyOwner {
        partCom = _value;
    }
    function partnersCom (uint8 _type, uint _rawFee, address _owner) private  {
        address affil = partnershipCon.getAffiliation(_owner);
        if(affil != address(0)){
            uint _ratio = Partner(affil).getRatio(_type);
            uint rlty = (_rawFee * _ratio)/100;
            partnershipCon.logPartnerRoyalties{value: rlty}(affil);
        }
    }
    //ALLOW
    function pause() public onlyOwner {
        _pause();
    }

    function unpause() public onlyOwner {
        _unpause();
    }
    modifier onlyAllowedContract (){
        require(allowedContracts[msg.sender] == true);
        _;
    }

    function allowContract ( address _addr ) onlyOwner public returns ( bool ) {
        allowedContracts[_addr ] = true;
        return true;
    }
    
    function disallowContract ( address _addr ) onlyOwner public returns ( bool ) {
        allowedContracts[_addr ] = false;
        return true;
    }
    //ALLOW
    function setNftContract (address _addr) public onlyOwner returns (bool) {
        nftCon = VAB(_addr);
        return true;
    }
    
    function adminTransfer(uint256 tokenId, address new_owner) public onlyOwner returns(bool) {
        ERC721(nftCon).safeTransferFrom(address(this), new_owner, tokenId);
        return true;
    }
    //ERC20
    function setTokenAddress(address _addr) public onlyOwner returns (bool) {
        token20 = ERC20(_addr);
        return true;
    }
    function thisTokenBalance () public view returns (uint256) {
        return token20.balanceOf(address(this));
    }
    
    function tokenBalance (address addr) public view returns (uint256) {
        return ERC20(addr).balanceOf(address(this));
    }
    
    function transferToken (address _tokenAddress, address addr, uint amount) public onlyOwner returns (uint256) {
        ERC20(_tokenAddress).transfer(addr, amount);
        return ERC20(_tokenAddress).balanceOf(address(this));
    }
    //ERC20   

    //BNB   
    function getBalance() public view returns(uint) {
        return address(this).balance;
    }
    function withdraw() public payable onlyOwner {
        address payable to = payable(msg.sender);
        to.transfer(getBalance());
    }
    function withdrawAmount(uint _amount) public payable onlyOwner {
        require(_amount <= getBalance(), "err : Amount > Balance");
        address payable to = payable(msg.sender);
        to.transfer(getBalance());
    }    

    function setTknHash (string memory _str) public onlyOwner returns (bool){
        tknHash = keccak256(abi.encodePacked(_str));
        return true;
    }
    // ##################################"
    // receive() external payable{}
    function onERC721Received(address, address, uint256, bytes memory) public virtual override returns (bytes4) {
        return this.onERC721Received.selector;
    }

}


// File: contracts/MiningV6/PromoIgnite.sol


pragma solidity ^0.8.4;






contract PromoIgnite is Pausable, Ownable {
    mapping(address => bool) public allowedContracts;

    VAB public nftCon;
    IStake public stakeCon;
    IComp public compCon;
    IDock public dockCon;
    ERC20 public token20;

    mapping(uint => uint) public mintedPerTier;
    mapping(address => User) public users;
    mapping(address => Sponsor) public sponsors;
    mapping(uint => address) public nftsUsers;
    mapping(address => uint) public usersNfts;
    mapping(string => address) public codes;

    uint[] public allNfts;
    address[] public allSponsors;
    uint total;

    constructor() {
        nftCon = VAB(0x95C1B791d5C76ef25eE87815B8FaC68182eD3880);
        stakeCon = IStake(0x0AA18a17B5A66059c01Fa44Ab04Dd2629bBd9275);
        compCon = IComp(0xd8cE5642bF6Ee1E7299b212747bDba20e35061F6);
        dockCon = IDock(0x10F3E647340726cd63C4791BC794C790F1cb5695);
    }

    struct Sponsor {address addr;string code;uint8 nft_tier;uint limit;uint nbr;uint minStake;bool active;}
    struct User {address wallet;address sponsor;uint staked;uint stakedValue;}


    function action() public whenNotPaused {
        //Sponsor
        address _sponsor = getAllowedSponsor(msg.sender);
        //Stake Value
        (uint stake_amount ,uint stake_value) = getStakeValue(msg.sender);
        //CHECK 
        require (checkEligible(msg.sender, _sponsor, stake_value), "not eligible");
        //NFT
        uint nft_id = nftCon.safeMint(msg.sender, sponsors[_sponsor].nft_tier);
        allNfts.push(nft_id);
        usersNfts[msg.sender] = nft_id;
        nftsUsers[nft_id] = msg.sender;
        total ++;
        sponsors[_sponsor].nbr ++;
        mintedPerTier[sponsors[_sponsor].nft_tier] ++;
        users[msg.sender] = User({
            wallet:msg.sender,
            sponsor: _sponsor,
            staked: stake_amount,
            stakedValue: stake_value
        });
    }
    function checkEligible(address _user, address _sponsor, uint _stakeValue) public view returns (bool) {        
        return (
            sponsors[_sponsor].limit - sponsors[_sponsor].nbr > 0 && 
            sponsors[_sponsor].minStake <= _stakeValue && 
            usersNfts[_user] == 0
        );
    }
    function unlockMiner(uint nft_id) external onlyAllowedContract returns (bool){
        nftsUsers[nft_id] = address(0);
        return true;
    }
    function getAllowedSponsor(address _user) public view returns (address){
        uint levels = compCon.maxLevel();
        address user_check = _user;
        address _sponsor; 
        if(sponsors[_user].active){
            return _user;
        }
        for (uint i = 0; i < levels; i++) {
            (address parent,,,,) = compCon.getUserDetails(user_check);
            if(sponsors[parent].active){
                _sponsor = parent;
                break;
            }else{
                user_check = parent;
            }            
        }
        return _sponsor;
    }
    function getStakeValue(address _user) public view returns (uint _tokens, uint _value) {
        uint _total_tokens;
        uint _total_value;
        IStake.Stake[] memory stakes = stakeCon.getUserStakes(_user);
        for (uint i = 0; i < stakes.length; i++) {
            if(!stakes[i].isCom){
                _total_tokens += stakes[i].initTokenAmount;
                _total_value += stakes[i].stakeValue;
            }
        }
        _tokens = _total_tokens;
        _value = _total_value;
    }
    function getSponsor(address _user) view external returns(Sponsor memory){
        return sponsors[_user];
    }
    function getAllSponsors() view external returns (address[] memory){
        return allSponsors;
    }
    function getAllSponsorsData() view external returns (Sponsor[] memory){
        Sponsor[] memory array = new Sponsor[](allSponsors.length);
        for (uint i = 0; i < allSponsors.length; i++) {
            array[i] = sponsors[allSponsors[i]];
        }
        return array;
    }
    function isValidNft(uint nft_id) external view returns(bool){
        if(nftsUsers[nft_id] == address(0)) return true;
        address _owner = nftCon.ownerOf(nft_id);
        if(_owner == address(dockCon)){
            IDock.tokenData memory _data = dockCon.Tokens(nft_id);
            _owner = _data.owner;
        }
        return nftsUsers[nft_id] == _owner;
    }
    
    function getSponsorBycode (string calldata _code) public view returns (Sponsor memory)  {
     return sponsors[codes[_code]];
    }
    //ADMIN
    function setNftContract (address _addr) external onlyOwner  {nftCon = VAB(_addr);}
    function setStakeContract (address _addr) external onlyOwner {stakeCon = IStake(_addr);}
    function setCompContract (address _addr) external onlyOwner {compCon = IComp(_addr);}
    function setDockContract (address _addr) external onlyOwner {dockCon = IDock(_addr);}
    function setToken20Contract (address _addr) external onlyOwner  {token20 = ERC20(_addr);}

    function setCode (string calldata _code) external  {
        require (codes[_code] == address(0));
        sponsors[msg.sender].code = _code;
        codes[_code] = msg.sender;
    }
    function setCodeAdmin (address _addr, string calldata _code) external onlyOwner  {
        require (codes[_code] == address(0));
        sponsors[_addr].code = _code;
        codes[_code] = _addr;
    }
    function removeCode (string calldata _code) public onlyOwner  {
        address _user = codes[_code];
        delete sponsors[_user].code;
        delete codes[_code];
    }

    function importAction (address _user, uint nft_id) public onlyOwner {
        address _sponsor = getAllowedSponsor(_user);
        (uint stake_amount ,uint stake_value) = getStakeValue(_user);
        allNfts.push(nft_id);
        usersNfts[_user] = nft_id;
        nftsUsers[nft_id] = _user;
        total ++;
        sponsors[_sponsor].nbr ++;
        mintedPerTier[sponsors[_sponsor].nft_tier] ++;
        users[_user] = User({
            wallet:_user,
            sponsor: _sponsor,
            staked: stake_amount,
            stakedValue: stake_value
        });
    }
    
    function addSponsor (address _sponsor, uint8 nft_tier,uint limit,uint minStake) external onlyOwner  {
        sponsors[_sponsor] = Sponsor({
            addr: _sponsor,
            code: sponsors[_sponsor].code,
            nft_tier: nft_tier,
            limit: limit,
            nbr: sponsors[_sponsor].nbr,
            minStake: minStake,
            active: sponsors[_sponsor].active
        });
        bool exists = false;
        for (uint256 i = 0; i < allSponsors.length; i++) {
            if (allSponsors[i] == _sponsor) {
                exists = true;
                break;
            }
        }
        if(!exists){
            allSponsors.push(_sponsor);
        }
    }
    function enableSponsor(address _sponsor) external onlyOwner{
        sponsors[_sponsor].active = true;
    }
    function disableSponsor(address _sponsor) external onlyOwner{
        sponsors[_sponsor].active = false;
    }


    //
    function adminTransfer(uint256 tokenId, address new_owner) public onlyOwner {ERC721(nftCon).safeTransferFrom(address(this), new_owner, tokenId);}
    function tokenBalance (address addr) public view returns (uint256) {return ERC20(addr).balanceOf(address(this));}    
    function transferToken (address _tokenAddress, address addr, uint amount) public onlyOwner returns (uint256) {
        ERC20(_tokenAddress).transfer(addr, amount);
        return ERC20(_tokenAddress).balanceOf(address(this));
    }
    //BNB
    function getBalance() public view returns(uint) {return address(this).balance;}
    function withdraw() public payable onlyOwner {
        address payable to = payable(msg.sender);
        to.transfer(getBalance());
    }
    function withdrawAmount(uint _amount) public payable onlyOwner {
        require(_amount <= getBalance(), "err : Amount > Balance");
        address payable to = payable(msg.sender);
        to.transfer(getBalance());
    }

    //ALLOW
    function pause() public onlyOwner {
        _pause();
    }

    function unpause() public onlyOwner {
        _unpause();
    }

    modifier onlyAllowedContract() {
        require(allowedContracts[msg.sender] == true);
        _;
    }

    function allowContract(address _addr) public onlyOwner {
        allowedContracts[_addr] = true;
    }

    function disallowContract(address _addr) public onlyOwner {
        allowedContracts[_addr] = false;
    }
}


interface IStake {
    struct Stake {
        uint initTokenAmount;
        uint initTokenPrice;
        uint stakeValue;
        uint rewardGoal;
        uint startDate;
        uint startDay;
        uint dayIndex;
        uint lastClaimDate;
        uint lastClaimDay;
        uint rewardClaimed;
        bool isCom;
        bool ended;
    }
    function getUserStakes(address _user) external view returns (Stake[] memory);
    function getPrice(uint _amount) external view returns (uint);
}
interface IComp {
    function getUserDetails (address _user) external view returns (
        address _parent,
        uint _directChildren,
        uint _children,
        uint _directSales,
        uint _sales
    );
    function maxLevel() external view returns (uint);
}
interface IDock {
    struct tokenData {address owner;address int_wallet; uint32 _days;}
    function Tokens(uint nft_id) external view returns (tokenData memory);
}
