library TransferHelper {
    function safeTransfer(
        address token,
        address to,
        uint256 value
    ) internal {
        // bytes4(keccak256(bytes('transfer(address,uint256)')));
        (bool success, bytes memory data) = token.call(
            abi.encodeWithSelector(0xa9059cbb, to, value)
        );
        require(
            success && (data.length == 0 || abi.decode(data, (bool))),
            "TransferHelper: TRANSFER_FAILED"
        );
    }

    function safeTransferFrom(
        address token,
        address from,
        address to,
        uint256 value
    ) internal {
        // bytes4(keccak256(bytes('transferFrom(address,address,uint256)')));
        (bool success, bytes memory data) = token.call(
            abi.encodeWithSelector(0x23b872dd, from, to, value)
        );
        require(
            success && (data.length == 0 || abi.decode(data, (bool))),
            "TransferHelper: TRANSFER_FROM_FAILED"
        );
    }
}

contract NostepCard is Ownable, ERC721 {
    uint256 private _cardId;
    address public constant usdAddress =
        0x1F20F26a747916FaB87C1Fe342fa4FA787f2B2BF;
    address public marketAddress;

    bool public disableTransfer = true;
    bool public isCheckMiner = false;
    mapping(address => bool) public miners;
    mapping(uint256 => mapping(address => bool)) public whiteList;

    mapping(address => uint256) public userOwner;
    mapping(uint256 => uint256) public cardPrice;
    mapping(uint256 => uint256) public cardDurable;
    mapping(uint256 => uint256) public cardLevel;
    mapping(uint256 => bool) public isActivated;

    mapping(uint256 => Card) public cardInfo;
    struct Card {
        uint256 price;
        uint256 durable;
        uint256 maxNum;
        uint256 num;
        string url;
        bool isWhite;
    }

    event ActivatedCard(
        address indexed account_,
        address indexed to,
        uint256 indexed tokenId_
    );

    constructor() ERC721("Nostep Card", "NST-CARD") {
        cardInfo[1].price = 170000000000000000000;
        cardInfo[1].durable = 500000000000000000000;
        cardInfo[1].maxNum = 500;
        cardInfo[1].isWhite = false;

        cardInfo[2].price = 500000000000000000000;
        cardInfo[2].durable = 1500000000000000000000;
        cardInfo[2].maxNum = 0;
        cardInfo[2].isWhite = true;
    }

    modifier onlyMiner() {
        require(miners[msg.sender], "caller is not the miner");
        _;
    }

    function setCheckMiner(bool _value) external onlyOwner {
        isCheckMiner = _value;
    }

    function setDisableTransfer(bool _value) external onlyOwner {
        disableTransfer = _value;
    }

    function setMarketAddress(address _value) external onlyOwner {
        marketAddress = _value;
    }

    function setWhiteList(
        uint256 _id,
        address[] memory addrlist,
        bool _value
    ) external onlyOwner {
        require(addrlist.length > 0, "addrlist error");
        for (uint256 i = 0; i < addrlist.length; i++) {
            whiteList[_id][addrlist[i]] = _value;
        }
    }

    function setMiner(address addr, bool _value) external onlyOwner {
        miners[addr] = _value;
    }

    function setCard(
        uint256 _id,
        uint256 _price,
        uint256 _durable,
        uint256 _maxNum,
        bool _isWhite
    ) external onlyOwner {
        require(_id > 0, "id error");
        cardInfo[_id].price = _price;
        cardInfo[_id].durable = _durable;
        cardInfo[_id].maxNum = _maxNum;
        cardInfo[_id].isWhite = _isWhite;
    }

    function setCardMaxNum(uint256 _id, uint256 _maxNum) external onlyOwner {
        require(_id > 0, "id error");
        cardInfo[_id].maxNum = _maxNum;
    }

    function setCardCheckWhite(uint256 _id, bool _isWhite) external onlyOwner {
        require(_id > 0, "id error");
        cardInfo[_id].isWhite = _isWhite;
    }

    function setCardInfo(uint256 _id, string memory _url) external onlyOwner {
        require(_id > 0, "id error");
        cardInfo[_id].url = _url;
    }

    function withdrawToken(
        address token,
        address to,
        uint256 amount
    ) external onlyOwner {
        TransferHelper.safeTransfer(token, to, amount);
    }

    function mintToCaller(address _user, uint256 id)
        external
        returns (uint256)
    {
        if (isCheckMiner) {
            require(miners[msg.sender], "caller is not the miner");
        }

        uint256 _price = cardInfo[id].price;
        if (_price > 0) {
            TransferHelper.safeTransferFrom(
                usdAddress,
                msg.sender,
                marketAddress,
                _price
            );
        }
        return _mintTo(_user, id, _price);
    }

    function mintTo(
        address _user,
        uint256 id,
        uint256 _price
    ) external onlyMiner returns (uint256) {
        return _mintTo(_user, id, _price);
    }

    function _mintTo(
        address _user,
        uint256 id,
        uint256 _price
    ) private returns (uint256) {
        require(marketAddress != address(0), "marketAddress error");
        require(userOwner[_user] == 0, "Do not repeat mint");
        require(cardInfo[id].num < cardInfo[id].maxNum, "card maxnum error");
        require(cardInfo[id].durable > 0, "card durable error");

        if (cardInfo[id].isWhite) {
            require(whiteList[id][_user], "user is not the white list");
        }

        _cardId += 1;
        cardInfo[id].num += 1;
        uint256 newItemId = _cardId;
        uint256 _durable = cardInfo[id].durable;

        _mint(_user, newItemId);

        cardLevel[newItemId] = id;
        cardPrice[newItemId] = _price;
        cardDurable[newItemId] = _durable;
        userOwner[_user] = newItemId;
        return newItemId;
    }

    function activateCard(address account_)
        external
        onlyMiner
        returns (uint256 cardId)
    {
        cardId = userOwner[account_];
        require(cardId > 0, "Not mint error");
        require(!isActivated[cardId], "Card has been activated");
        require(ownerOf(cardId) == account_, "Card owner error");

        isActivated[cardId] = true;
        emit ActivatedCard(ownerOf(cardId), msg.sender, cardId);
    }

    function _transfer(
        address from,
        address to,
        uint256 tokenId
    ) internal virtual override {
        require(!disableTransfer, "Disable Transfer error");
        super._transfer(from, to, tokenId);
    }

    function tokenURI(uint256 tokenId)
        public
        view
        virtual
        override
        returns (string memory)
    {
        require(
            _exists(tokenId),
            "ERC721Metadata: URI query for nonexistent token"
        );

        return cardInfo[cardLevel[tokenId]].url;
    }
}
