    "contracts/others/RoyaltiesV2Impl.sol": {
      "content": "// SPDX-License-Identifier: MIT

pragma solidity 0.8.17;

import "./interfaces/IERC2981.sol";
import "./libraries/LibPart.sol";
import "./libraries/LibRoyaltiesV2.sol";
import "./interfaces/RoyaltiesV2.sol";
import "./interfaces/AbstractRoyalties.sol";

/**
 * @title RoyaltiesV2Impl
 * @author karan (@cryptofluencerr, https://cryptofluencerr.com)
 * @dev This Contract will be used set the royalty of the particular NFT
 * @dev also to fetch royalties in marketplace
 */

// ============== RoyaltiesV2Impl ==============
contract RoyaltiesV2Impl is AbstractRoyalties, RoyaltiesV2, IERC2981 {
    function getRaribleV2Royalties(
        uint256 id
    ) external view override returns (LibPart.Part[] memory) {
        return royalties[id];
    }

    function _onRoyaltiesSet(
        uint256 id,
        LibPart.Part[] memory _royalties
    ) internal override {
        emit RoyaltiesSet(id, _royalties);
    }

    /*
     *Token (ERC721, ERC721Minimal, ERC721MinimalMeta, ERC1155 ) can have a number of different royalties beneficiaries
     *calculate sum all royalties, but royalties beneficiary will be only one royalties[0].account, according to rules of IERC2981
     */

    function royaltyInfo(
        uint256 id,
        uint256 _salePrice
    ) external view override returns (address receiver, uint256 royaltyAmount) {
        if (royalties[id].length == 0) {
            receiver = address(0);
            royaltyAmount = 0;
            return (receiver, royaltyAmount);
        }
        LibPart.Part[] memory _royalties = royalties[id];
        receiver = _royalties[0].account;
        uint256 percent;
        for (uint256 i = 0; i < _royalties.length; i++) {
            percent += _royalties[i].value;
        }
        //don`t need require(percent < 10000, "Token royalty > 100%"); here, because check later in calculateRoyalties
        royaltyAmount = (percent * _salePrice) / 10000;
    }
}
"
    },
    "contracts/others/VRFConsumerBaseV2Upgradeable.sol": {
      "content": "// SPDX-License-Identifier: MIT

pragma solidity 0.8.17;

import "@openzeppelin/contracts-upgradeable/utils/AddressUpgradeable.sol";
import "@openzeppelin/contracts-upgradeable/access/OwnableUpgradeable.sol";

import "@chainlink/contracts/src/v0.8/interfaces/LinkTokenInterface.sol";
import "@chainlink/contracts/src/v0.8/interfaces/VRFCoordinatorV2Interface.sol";

/**
 * @title VRFConsumerBaseV2Upgradeable
 * @author karan (@cryptofluencerr, https://cryptofluencerr.com)
 * @dev This Contract will be used to mint Underground Waifus (UW) NFTs.
 * @dev Users will be able to purchase packs of Underground Waifus (UW) NFTs,
 * @dev This Contract use Chainlink VRF for minting random NFT's.
 */

// ============== VRFConsumerBaseV2Upgradeable ==============
contract VRFConsumerBaseV2Upgradeable is OwnableUpgradeable {
    error OnlyCoordinatorCanFulfill(address have, address want);

    using AddressUpgradeable for address;

    // VRF Variables
    VRFCoordinatorV2Interface internal COORDINATOR;
    LinkTokenInterface internal LINKTOKEN;

    address internal vrfCoordinator;
    address internal linkTokenAddress;
    bytes32 internal keyHash;
    uint32 internal callbackGasLimit;
    uint16 internal requestConfirmations;
    uint32 internal numWords;
    uint64 internal s_subscriptionId;
    uint256 internal s_requestId;

    /**
     * @dev Initializes the contract.
     */
    function __VRFConsumerBaseV2_init(
        address _vrfCoordinator,
        address _link,
        bytes32 _keyHash,
        uint32 _callbackGasLimit,
        uint16 _requestConfirmations,
        uint32 _numWords,
        uint64 _subscriptionId
    ) internal onlyInitializing {
        __VRFConsumerBaseV2_init_unchained(
            _vrfCoordinator,
            _link,
            _keyHash,
            _callbackGasLimit,
            _requestConfirmations,
            _numWords,
            _subscriptionId
        );
    }

    function __VRFConsumerBaseV2_init_unchained(
        address _vrfCoordinator,
        address _linkTokenAddress,
        bytes32 _keyHash,
        uint32 _callbackGasLimit,
        uint16 _requestConfirmations,
        uint32 _numWords,
        uint64 _subscriptionId
    ) internal onlyInitializing {
        vrfCoordinator = _vrfCoordinator;
        linkTokenAddress = _linkTokenAddress;
        keyHash = _keyHash;
        callbackGasLimit = _callbackGasLimit;
        requestConfirmations = _requestConfirmations;
        numWords = _numWords;
        s_subscriptionId = _subscriptionId;
        COORDINATOR = VRFCoordinatorV2Interface(_vrfCoordinator);
        LINKTOKEN = LinkTokenInterface(_linkTokenAddress);
    }

    /**
     * @notice fulfillRandomness handles the VRF response. Your contract must
     * @notice implement it. See "SECURITY CONSIDERATIONS" above for important
     * @notice principles to keep in mind when implementing your fulfillRandomness
     * @notice method.
     *
     * @dev VRFConsumerBaseV2 expects its subcontracts to have a method with this
     * @dev signature, and will call it once it has verified the proof
     * @dev associated with the randomness. (It is triggered via a call to
     * @dev rawFulfillRandomness, below.)
     *
     * @param requestId The Id initially returned by requestRandomness
     * @param randomWords the VRF output expanded to the requested number of words
     */
    function fulfillRandomWords(
        uint256 requestId,
        uint256[] memory randomWords
    ) internal virtual {}

    // rawFulfillRandomness is called by VRFCoordinator when it receives a valid VRF
    // proof. rawFulfillRandomness then calls fulfillRandomness, after validating
    // the origin of the call
    function rawFulfillRandomWords(
        uint256 requestId,
        uint256[] memory randomWords
    ) external {
        if (msg.sender != vrfCoordinator) {
            revert OnlyCoordinatorCanFulfill(msg.sender, vrfCoordinator);
        }
        fulfillRandomWords(requestId, randomWords);
    }

    //============== ONLY OWNER FUNCTIONS =============
    function changeCoordinator(address _vrfCoordinator) external onlyOwner {
        vrfCoordinator = _vrfCoordinator;
    }

    function changeCallbackGasLimit(
        uint32 _callbackGasLimit
    ) external onlyOwner {
        callbackGasLimit = _callbackGasLimit;
    }

    function changeSubscriptionId(uint64 _subscriptionId) external onlyOwner {
        s_subscriptionId = _subscriptionId;
    }

    function changeRequestConfirmations(
        uint16 _requestConfirmations
    ) external onlyOwner {
        requestConfirmations = _requestConfirmations;
    }

    function changeKeyHash(bytes32 _keyHash) external onlyOwner {
        keyHash = _keyHash;
    }

    /**
     * @notice Cancels the chainlink vrf subscription and sends the remaining LINK to a wallet address.
     * @dev This function can only be called by the contract owner.
     */
    function cancelSubscription() external onlyOwner {
        COORDINATOR.cancelSubscription(s_subscriptionId, _msgSender());
        s_subscriptionId = 0;
    }
}
"
    },
