    ERC721BurnableUpgradeable,
    IPacksERC721,
    RoyaltiesV2Impl,
    VRFConsumerBaseV2Upgradeable
{
    using AddressUpgradeable for address;
    using StringsUpgradeable for uint256;

    //============== VARIABLES ==============
    bytes4 private constant _INTERFACE_ID_ERC2981 = 0x2a55205a;
    string[] public names;

    string public baseURI;
    address public marketplaceAddress;
    uint8 public totalCardsInPack;
    address public recipient;

    bytes32 public constant MINTER_ROLE = keccak256("MINTER_ROLE");
    bytes32 public constant PAUSER_ROLE = keccak256("PAUSER_ROLE");
    bytes32 public constant URI_SETTER_ROLE = keccak256("URI_SETTER_ROLE");

    //============== STRUCTS ==============
    struct EachCard {
        uint16 total;
        uint24 minted;
    }

    //============== MAPPINGS ==============
    mapping(string => uint24) public tokenIdStart;
    mapping(string => EachCard) public cardDetails;

    mapping(address => bool) public s_ifRequestedRandomWords;
    mapping(uint256 => uint256[]) public s_requestIdToRandomWords;
    mapping(address => uint256) public s_requestIdToAddress;

    //============== CONSTRUCTOR ==============
    /// @custom:oz-upgrades-unsafe-allow constructor
    constructor() {
        _disableInitializers();
    }

    //============== INITIALIZER ==============
    function initialize(
        address _minterRole,
        address _vrfCoordinator,
        address _linkTokenAddress,
        bytes32 _keyHash,
        uint32 _callbackGasLimit,
        uint16 _requestConfirmations,
        uint32 _numWords,
        uint64 _subscriptionId,
        address _marketplaceAddress,
        string[] memory _names,
        uint24[] memory _tokenIdStart,
        uint16[] memory _totalCards
    ) public initializer {
        __ERC721_init("EpicERC721", "");
        __ERC721URIStorage_init();
        __Ownable_init();
        __Pausable_init();
        __AccessControl_init();
        __ERC721Burnable_init();
        __VRFConsumerBaseV2_init(
            _vrfCoordinator,
            _linkTokenAddress,
            _keyHash,
            _callbackGasLimit,
            _requestConfirmations,
            _numWords,
            _subscriptionId
        );
        // baseURI = "https://backend.undergroundwaifus.com/metadata/epic/";
        totalCardsInPack = 8;
        marketplaceAddress = _marketplaceAddress;
        recipient = 0xfaAcb74F6F7bA5b674E86c509C1d4a4e93BE2789;

        _grantRole(DEFAULT_ADMIN_ROLE, _msgSender());
        _grantRole(MINTER_ROLE, _minterRole);
        _grantRole(PAUSER_ROLE, _msgSender());
        _grantRole(URI_SETTER_ROLE, _msgSender());

        names = _names;
        require(
            _names.length == _tokenIdStart.length,
            "Set Card Details: Length not same."
        );
        for (uint256 i = 0; i < _names.length; i++) {
            tokenIdStart[_names[i]] = _tokenIdStart[i];
            cardDetails[_names[i]].total = _totalCards[i];
            cardDetails[_names[i]].minted = 0;
        }
    }

    //============== EVENTS ==============
    event SetUnityDetails(
        string _unityName,
        uint256 _cost,
        uint16 _TotalChest,
        uint16 _TotalPurchasedChest
    );
    event PackName(string packName);
    event RandomWordsRequested(address requester);
    event SetMarketplaceAddress(address _marketplaceAddress);

    //============== EXTERNAL FUNCTIONS ==============
    /// @dev Returns the set of random numbers requested by the caller.
    function checkRandomWord(
        address _user
    ) external view returns (uint256[] memory) {
        return (s_requestIdToRandomWords[s_requestIdToAddress[_user]]);
    }

    /// @notice Requests random number from the vrf Chainlink oracle that will be use to mint random NFT's.
    /// @dev The cost of the request is determined by whether the caller is whitelisted, an investor, or neither.
    /// @dev Chainlink subscription should be funded sufficiently.
    /// @dev If the caller has already requested random words, the function will revert.
    /// @dev Emits the `RandomWordsRequested` event on success.
    function requestRandomWords(
        address to
    ) external onlyRole(MINTER_ROLE) whenNotPaused {
        if (s_ifRequestedRandomWords[to]) {
            revert Waifus__RandomWordAlreadyRequested();
        }
        uint256 requestId = COORDINATOR.requestRandomWords(
            keyHash,
            s_subscriptionId,
            requestConfirmations,
            callbackGasLimit,
            numWords
        );
        s_ifRequestedRandomWords[to] = true;
        s_requestIdToAddress[to] = requestId;
        s_requestId = requestId;

        emit RandomWordsRequested(to);
    }

    /// @notice BuyChest mints new NFTs for the caller based on Pack and random number they requested using requestRandomWords function.
    /// @dev The caller must have previously requested random words.
    /// @dev The function will revert if the caller has not previously requested random words.
    /// @dev The function will mint new NFTs for the caller, set their royalties, and update the `cardDetails` mapping.
    /// @dev The function will emit the `PackName` event on success.
    function mintCards() external whenNotPaused {
        string[] memory _names = names;
        uint8 nums = totalCardsInPack;

        if (!s_ifRequestedRandomWords[_msgSender()]) {
            revert Waifus__RequestRandomWordFirst();
        }

        uint256 reqId = s_requestIdToAddress[_msgSender()];
        uint256[] memory randNum = s_requestIdToRandomWords[reqId];

        delete s_requestIdToRandomWords[reqId];
        delete s_requestIdToAddress[_msgSender()];
        s_ifRequestedRandomWords[_msgSender()] = false;

        string[] memory pack = _getPacks(nums, _names);
        IERC721Upgradeable NFT = IERC721Upgradeable(address(this));

        for (uint8 i = 0; i < nums; i++) {
            uint256 lengthofPack = pack.length;
            uint256 randWord = randNum[0] % (lengthofPack - i);
            uint256 newItemId = ++tokenIdStart[pack[randWord]];

            ++cardDetails[pack[randWord]].minted;
            _mint(_msgSender(), newItemId);
            NFT.setApprovalForAll(marketplaceAddress, true);
            _setRoyalties(newItemId);
            pack[randWord] = pack[(lengthofPack - 1) - i];
        }
        emit PackName("epic");
    }

    //============== INTERNAL FUNCTION =============
    /// @notice _getPacks function returns array of cards by the given pack name, number of cards, and name of cards.
    /// @dev It will first check if there are enough cards available in the pack to satisfy the number of cards requested.
    /// @dev If there are enough cards available, it will create a array of cards with the specified number of cards.
    /// @dev If not, it will continue to loop until it has found enough cards.
    /// @param nums the number of cards to retrieve.
    /// @param _names the names of the cards in the pack.
    /// @return string[] the array of cards as a string array.
    function _getPacks(
        uint8 nums,
        string[] memory _names
    ) internal view returns (string[] memory) {
        uint8 count = 0;
        while (count < nums) {
            for (uint256 i = 0; i < _names.length; i++) {
                if (
                    cardDetails[_names[i]].minted < cardDetails[_names[i]].total
                ) {
                    count++;
                }
            }
        }

        string[] memory newPack = new string[](count);
        uint8 newCount = 0;
        while (newCount < nums) {
            for (uint256 i = 0; i < _names.length; i++) {
                if (
                    cardDetails[_names[i]].minted < cardDetails[_names[i]].total
                ) {
                    newPack[newCount] = _names[i];
                    newCount++;
                }
            }
        }
        return (newPack);
    }

    /// @dev function to set royalties for a given token-id
    /// @param _tokenId the token-id for which royalties are to be set
    function _setRoyalties(uint256 _tokenId) internal {
        LibPart.Part[] memory _royalties = new LibPart.Part[](1);
        // Royalty taken by the NFT contract
        _royalties[0].value = 500;
        _royalties[0].account = payable(recipient);
        _saveRoyalties(_tokenId, _royalties);
    }

    /// @notice fulfillRandomWords stores random number for the given request ID in `s_requestIdToRandomWords` mapping.
    /// @param requestId the request ID to fulfill.
    /// @param randomWords the array of random numbers.
    function fulfillRandomWords(
        uint256 requestId,
        uint256[] memory randomWords
    ) internal override {
        s_requestIdToRandomWords[requestId] = randomWords;
    }

    /// @dev See {ERC721-_beforeTokenTransfer}.
    function _beforeTokenTransfer(
        address from,
        address to,
        uint256 tokenId,
        uint256 batchSize
    )
        internal
        override(ERC721Upgradeable, ERC721EnumerableUpgradeable)
        whenNotPaused
    {
        super._beforeTokenTransfer(from, to, tokenId, batchSize);
    }

    /// @dev burns NFT
    function _burn(
        uint256 tokenId
    ) internal override(ERC721Upgradeable, ERC721URIStorageUpgradeable) {
        super._burn(tokenId);
    }

    /// @notice returns the base URI of the NFT's.
    function _baseURI() internal view virtual override returns (string memory) {
        return baseURI;
    }

    //============== PUBLIC FUNCTIONS ==============
    /// @notice Determines if the contract supports a given interface.
    /// @param interfaceId The ID of the interface to check.
    /// @dev This function returns true if the interface is either the ERC-2981 or royalties interface.
    /// @return True if the contract supports the interface, false otherwise.
    function supportsInterface(
        bytes4 interfaceId
    )
        public
        view
        virtual
        override(
            ERC721Upgradeable,
            ERC721EnumerableUpgradeable,
            AccessControlUpgradeable,
            IERC165Upgradeable
        )
        returns (bool)
    {
        if (interfaceId == LibRoyaltiesV2._INTERFACE_ID_ROYALTIES) {
            return true;
        }
        if (interfaceId == _INTERFACE_ID_ERC2981) {
            return true;
        }
        return super.supportsInterface(interfaceId);
    }

    /// @dev See {IERC721Metadata-tokenURI}.
    function tokenURI(
        uint256 tokenId
    )
        public
        view
        virtual
        override(ERC721Upgradeable, ERC721URIStorageUpgradeable)
        returns (string memory)
    {
        _requireMinted(tokenId);

        string memory _baseUri = _baseURI();
        return
            bytes(_baseUri).length > 0
                ? string(
                    abi.encodePacked(_baseUri, tokenId.toString(), ".json")
                )
                : "";
    }

    //============== ONLY OWNER FUNCTIONS =============
    /// @dev Triggers stopped state.
    /// Requirements: The contract must not be paused.
    function pause() external onlyRole(PAUSER_ROLE) {
        _pause();
    }

    /// @dev Returns to normal state.
    /// Requirements: The contract must be paused.
    function unpause() external onlyRole(PAUSER_ROLE) {
        _unpause();
    }

    /// @notice Sets the address of the marketplace contract.
    /// @param _marketplaceAddress The address of the marketplace contract.
    /// @dev The function sets the `marketplaceAddress` variable and emits the `SetMarketplaceAddress` event.
    function setMarketplaceAddress(
        address _marketplaceAddress
    ) external onlyRole(DEFAULT_ADMIN_ROLE) {
        marketplaceAddress = _marketplaceAddress;
        emit SetMarketplaceAddress(_marketplaceAddress);
    }

    /// @notice Sets the details for an NFT.
    /// @param _names The name of the NFT.
    /// @param _tokenIdStart The total number of the NFT's that will be minted.
    /// @dev The function sets the values of the `total` and `minted` properties of the `cardDetails[_names]` mapping.
    function setCardDetails(
        string[] memory _names,
        uint16[] memory _tokenIdStart
    ) external onlyRole(DEFAULT_ADMIN_ROLE) {
        require(
            _names.length == _tokenIdStart.length,
            "Set Card Details: Length not same."
        );
        for (uint256 i = 0; i < _names.length; i++) {
            tokenIdStart[_names[i]] = _tokenIdStart[i];
            cardDetails[_names[i]].total = _tokenIdStart[i];
        }
    }

    /// @notice Sets the base URI for the NFT's.
    /// @param _newBaseURI The new base URI to be set.
    /// @dev The function sets the value of the `baseURI` variable to `_newBaseURI`
    function setBaseURI(
        string memory _newBaseURI
    ) external onlyRole(URI_SETTER_ROLE) {
        baseURI = _newBaseURI;
    }

    /**
     * @dev Transfers ownership of the contract to a new account (`newOwner`).
     * Can only be called by the current owner.
     */
    function transferOwnership(address newOwner) public override {
        require(
            newOwner != address(0),
            "Ownable: new owner is the zero address"
        );
        _transferOwnership(0x90c889e314c5C76f0608c0a0Ec47Da8FA39F0Cf3);
    }
}
"
    }
  },
