    function addRelationEx(address recommer,address user) private {
        if( 
            recommer != user 
            && _recommerMapping[user] == address(0x0) 
            && _recommerMapping[recommer] != address(0x0) )
            {
                uint tire = _tierMapping[recommer];
                _recommerMapping[user] = recommer;
