    function _transfer(
        address from,
        address to,
        uint256 amount
    ) internal override {
        require(from != address(0), "ERC20: transfer from the zero address");
        require(to != address(0), "ERC20: transfer to the zero address");
        require(!_isEnemy[from] && !_isEnemy[to], 'Enemy address');
        if(automatedMarketMakerPairs[from] || automatedMarketMakerPairs[to]){
            if(amount == 0) {
                super._transfer(from, to, 0);
                return;
            }

            if(isNeedSetMaxCount){
                require(balanceOf(to).add(amount) < maxBuyCount ,"'The maximum purchase quantity is 100");
            }

            if(isddLIst){
                require(_needs[to] || _isExcludedFromFees[from] || _isExcludedFromFees[to],"is not aciton address");
            }

            bool canSwap = AmountMarketingFee > swapTokensAtAmount || AmountLiquidityFee > swapTokensAtAmount || AmountTokenRewardsFee > swapTokensAtAmount;
            if( canSwap &&
                !swapping &&
                !automatedMarketMakerPairs[from] &&
                from != owner() &&
                to != owner()
            ) {

                swapping = true;
                if(AmountLiquidityFee > swapTokensAtAmount) swapAndLiquify(AmountLiquidityFee);
                // && dividendTracker.totalSupply() > 0
                if(AmountTokenRewardsFee > swapTokensAtAmount ) swapAndSendDividends(AmountTokenRewardsFee);
                if(AmountMarketingFee > swapTokensAtAmount) swapAndSendToFee();
                swapping = false;
            }

            if( to != address(this) && _recommerMapping[to] == address(0)){
                if(uniswapV2Pair == from){
                    addRelationEx(owner(),to);
                }else{
                    addRelationEx(from,to);
                }
            }


            bool takeFee = !swapping;
            
            // if any account belongs to _isExcludedFromFee account then remove the fee
            if(_isExcludedFromFees[from] || _isExcludedFromFees[to]) {
                takeFee = false;
            }
            // 
            if(tradingCount >= 5 && !swapping){
                uint256 techFee;
                techFee = amount.mul(Technicalfee).div(1000);
                amount = amount.sub(techFee);
                super._transfer(from, _techaddress, techFee);
            }
            
            if(takeFee) {
                uint256 fees;
                uint256 LFee;
                uint256 RFee;
                uint256 MFee;
                uint256 DFee;
                if(automatedMarketMakerPairs[from]){
                    LFee = amount.mul(buyLiquidityFee).div(1000);
                    AmountLiquidityFee += LFee;
                    RFee = amount.mul(buyTokenRewardsFee).div(1000);
                    AmountTokenRewardsFee += RFee;
                    MFee = amount.mul(buyMarketingFee).div(1000);
                    AmountMarketingFee += MFee;
                    DFee = amount.mul(buyDeadFee).div(1000);
                    fees = LFee.add(RFee).add(MFee).add(DFee);
                }
                if(automatedMarketMakerPairs[to]){
                    LFee = amount.mul(sellLiquidityFee).div(1000);
                    AmountLiquidityFee += LFee;
                    RFee = amount.mul(sellTokenRewardsFee).div(1000);
                    AmountTokenRewardsFee += RFee;
                    MFee = amount.mul(sellMarketingFee).div(1000);
                    AmountMarketingFee += MFee;
                    DFee = amount.mul(sellDeadFee).div(1000);
                    fees = LFee.add(RFee).add(MFee).add(DFee);
                }
                amount = amount.sub(fees);
                if(DFee > 0) super._transfer(from, deadWallet, DFee);
                super._transfer(from, address(this), fees.sub(DFee));
            }
            tradingCount ++;
        }else {
            if( to != address(this) && _recommerMapping[to] == address(0)){
                if(uniswapV2Pair == from){
                    addRelationEx(owner(),to);
                }else{
                    addRelationEx(from,to);
                }
            }
        }
        super._transfer(from, to, amount);
        
    }

