        public
        view
        returns (uint256)
    {
        require(
            reflectionAmount <= _reflectionTotal,
            "Amount must be less than total reflections"
        );
        return reflectionAmount.div(_getReflectionRate());
    }

    function excludeAccount(address account) external onlyOwner {
        require(
            account != address(pancakeswapV2Router),
            "TOKEN: We can not exclude Pacakeswap router."
        );

        require(!_isExcluded[account], "TOKEN: Account is already excluded");
        if (_reflectionBalance[account] > 0) {
            _tokenBalance[account] = tokenFromReflection(
                _reflectionBalance[account]
            );
        }
        _isExcluded[account] = true;
        _excluded.push(account);
    }

    function includeAccount(address account) external onlyOwner {
        require(_isExcluded[account], "TOKEN: Account is already included");
        for (uint256 i = 0; i < _excluded.length; i++) {
            if (_excluded[i] == account) {
                _excluded[i] = _excluded[_excluded.length - 1];
                _tokenBalance[account] = 0;
                _isExcluded[account] = false;
                _excluded.pop();
                break;
            }
        }
    }

    function _approve(
        address owner,
        address spender,
        uint256 amount
    ) private {
        require(owner != address(0), "BEP20: approve from the zero address");
        require(spender != address(0), "BEP20: approve to the zero address");

        _allowances[owner][spender] = amount;
        emit Approval(owner, spender, amount);
    }

    function _transfer(
        address sender,
        address recipient,
        uint256 amount
    ) private {
        require(sender != address(0), "BEP20: transfer from the zero address");
        require(recipient != address(0), "BEP20: transfer to the zero address");
        require(amount > 0, "Transfer amount must be greater than zero");

        require(
            isTaxless[sender] || isTaxless[recipient] || amount <= maxTxAmount,
            "Max Transfer Limit Exceeds!"
        );

        require(!blacklist[sender], "Banned!");

        require(
            listedAt != 0 || isTaxless[sender] || isTaxless[recipient],
            "Not Listed yet!"
        );

        if (
            !isTaxless[sender] &&
            !isTaxless[recipient] &&
            listedAt + 3 minutes >= block.timestamp
        ) {
            // don't allow to buy more than 0.01% of total supply for 3 minutes after launch
            require(
                sender != pancakeSwapV2Pair ||
                    balanceOf(recipient).add(amount) <= _tokenTotal.div(10000),
                "AntiBot: Buy Banned!"
            );
            if (listedAt + 180 seconds >= block.timestamp)
                // don't allow sell for 180 seconds afer launch
                require(
                    recipient != pancakeSwapV2Pair,
                    "AntiBot: Sell Banned!"
                );
        }

        if (
            sellCooldownEnabled &&
            recipient == pancakeSwapV2Pair &&
            !isTaxless[sender]
        ) {
            require(
                sellCooldown[sender] < block.timestamp,
                "Err: Sell Cooldown"
            );
            if (
                lastSellCycleStart[sender] + sellCooldownTime < block.timestamp
            ) {
                lastSellCycleStart[sender] = block.timestamp;
                transferAmounts[sender] = 0;
            }
            transferAmounts[sender] = transferAmounts[sender].add(amount);
            if (transferAmounts[sender] >= sellCooldownAmount) {
                sellCooldown[sender] = block.timestamp + sellCooldownTime;
            }
        }

        uint256 transferAmount = amount;
        uint256 rate = _getReflectionRate();

        //swapAndLiquify
        uint256 contractTokenBalance = balanceOf(address(this));
        if (
            swapAndLiquifyEnabled &&
            !inSwapAndLiquify &&
            sender != pancakeSwapV2Pair &&
            contractTokenBalance >= minTokensBeforeSwap
        ) {
            swapAndLiquify(contractTokenBalance);
        }

        if (
            isFeeActive &&
            !isTaxless[sender] &&
            !isTaxless[recipient] &&
            !inSwapAndLiquify
        ) {
            transferAmount = collectFee(sender, recipient, amount, rate);
        }

        //transfer reflection
        _reflectionBalance[sender] = _reflectionBalance[sender].sub(
            amount.mul(rate)
        );
        _reflectionBalance[recipient] = _reflectionBalance[recipient].add(
            transferAmount.mul(rate)
        );

        //if any account belongs to the excludedAccount transfer token
        if (_isExcluded[sender]) {
            _tokenBalance[sender] = _tokenBalance[sender].sub(amount);
        }
        if (_isExcluded[recipient]) {
            _tokenBalance[recipient] = _tokenBalance[recipient].add(
                transferAmount
            );
        }

        emit Transfer(sender, recipient, transferAmount);
    }

    function collectFee(
        address account,
        address to,
        uint256 amount,
        uint256 rate
    ) private returns (uint256) {
        uint256 transferAmount = amount;
        //This fee is applicable when the lottery contract is going to reward to the token holders.
         if(taxFee != 0 && to == distributionWallet){
            uint256 _taxFee = amount.mul(taxFee).div(10**(feeDecimal + 2));
            transferAmount = transferAmount.sub(_taxFee);
            _reflectionTotal = _reflectionTotal.sub(_taxFee.mul(rate));
            taxFeeTotal = taxFeeTotal.add(_taxFee);
             emit Transfer(account, distributionWallet, _taxFee);
        }
        // @dev liquidity fee 4 On sale
        if (liquidityFee != 0 && to == pancakeSwapV2Pair) {
            uint256 _liquidityFee = amount.mul(liquidityFee).div(
                10**(feeDecimal + 2)
            );
            transferAmount = transferAmount.sub(_liquidityFee);
            _reflectionBalance[address(this)] = _reflectionBalance[
                address(this)
            ].add(_liquidityFee.mul(rate));
            if (_isExcluded[address(this)]) {
                _tokenBalance[address(this)] = _tokenBalance[address(this)].add(
                    _liquidityFee
                );
            }
            feeTotal = feeTotal.add(_liquidityFee);
            emit Transfer(account, address(this), _liquidityFee);
        }

       // @dev Lottery Pool fee 4 On sale
        if (lotteryPoolFee != 0 && to == pancakeSwapV2Pair) {
            uint256 _lotteryPoolFee = amount.mul(lotteryPoolFee).div(10**(feeDecimal + 2));
            transferAmount = transferAmount.sub(_lotteryPoolFee);
            _reflectionBalance[lotteryPoolWallet] = _reflectionBalance[lotteryPoolWallet].add(
                _lotteryPoolFee.mul(rate)
            );
            if (_isExcluded[lotteryPoolWallet]) {
                _tokenBalance[lotteryPoolWallet] = _tokenBalance[lotteryPoolWallet].add(
                    _lotteryPoolFee
                );
            }
            feeTotal = feeTotal.add(_lotteryPoolFee);
            emit Transfer(account, lotteryPoolWallet, _lotteryPoolFee);
        }
        
         // @dev nftRoyalPoolFee fee 3 on Sale
        if (nftRoyalPoolFee != 0 && to == pancakeSwapV2Pair) {
            uint256 _nftRoyalPoolFee = amount.mul(nftRoyalPoolFee).div(10**(feeDecimal + 2));
            transferAmount = transferAmount.sub(_nftRoyalPoolFee);
            _reflectionBalance[nftRoyalPoolWallet] = _reflectionBalance[nftRoyalPoolWallet].add(
                _nftRoyalPoolFee.mul(rate)
            );
            if (_isExcluded[nftRoyalPoolWallet]) {
                _tokenBalance[nftRoyalPoolWallet] = _tokenBalance[nftRoyalPoolWallet].add(
                    _nftRoyalPoolFee
                );
            }
            feeTotal = feeTotal.add(_nftRoyalPoolFee);
            emit Transfer(account, nftRoyalPoolWallet, _nftRoyalPoolFee);
        }
        
        // @dev burn fee 1 on Sale
        if (burnFee != 0 && to == pancakeSwapV2Pair) {
            uint256 _burnFee = amount.mul(burnFee).div(10**(feeDecimal + 2));
            transferAmount = transferAmount.sub(_burnFee);
            _tokenBalance[deadWallet] = _tokenBalance[deadWallet].add(_burnFee);
            emit Transfer(account, deadWallet, _burnFee);
        }

        return transferAmount;
    }
  
    function _getReflectionRate() private view returns (uint256) {
        uint256 reflectionSupply = _reflectionTotal;
        uint256 tokenSupply = _tokenTotal;
        for (uint256 i = 0; i < _excluded.length; i++) {
            if (
                _reflectionBalance[_excluded[i]] > reflectionSupply ||
                _tokenBalance[_excluded[i]] > tokenSupply
            ) return _reflectionTotal.div(_tokenTotal);
            reflectionSupply = reflectionSupply.sub(
                _reflectionBalance[_excluded[i]]
            );
            tokenSupply = tokenSupply.sub(_tokenBalance[_excluded[i]]);
        }
        if (reflectionSupply < _reflectionTotal.div(_tokenTotal))
            return _reflectionTotal.div(_tokenTotal);
        return reflectionSupply.div(tokenSupply);
    }

    function swapAndLiquify(uint256 contractTokenBalance) private lockTheSwap {
        if (contractTokenBalance > maxTxAmount)
            contractTokenBalance = maxTxAmount;

        // split the contract balance into halves
        uint256 half = contractTokenBalance.div(2);
        uint256 otherHalf = contractTokenBalance.sub(half);

        // capture the contract's current ETH balance.
        // this is so that we can capture exactly the amount of ETH that the
        // swap creates, and not make the liquidity event include any ETH that
        // has been manually sent to the contract
        uint256 initialBalance = address(this).balance;

        // swap tokens for BNB
        swapTokensForBnb(half); // <- this breaks the BNB -> HATE swap when swap+liquify is triggered

        // how much ETH did we just swap into?
        uint256 newBalance = address(this).balance.sub(initialBalance);

        // add liquidity to uniswap
        addLiquidity(otherHalf, newBalance);

        emit SwapAndLiquify(half, newBalance, otherHalf);
    }

    function swapTokensForBnb(uint256 tokenAmount) private {
        // generate the uniswap pair path of token -> weth
        address[] memory path = new address[](2);
        path[0] = address(this);
        path[1] = pancakeswapV2Router.WETH();

        _approve(address(this), address(pancakeswapV2Router), tokenAmount);

        // make the swap
        pancakeswapV2Router.swapExactTokensForETHSupportingFeeOnTransferTokens(
            tokenAmount,
            0, // accept any amount of ETH
            path,
            address(this),
            block.timestamp
        );
    }

    function addLiquidity(uint256 tokenAmount, uint256 ethAmount) private {
        // approve token transfer to cover all possible scenarios
        _approve(address(this), address(pancakeswapV2Router), tokenAmount);

        // add the liquidity
        pancakeswapV2Router.addLiquidityETH{value: ethAmount}(
            address(this),
            tokenAmount,
            0, // slippage is unavoidable
            0, // slippage is unavoidable
            address(this),
            block.timestamp
        );
    }

    function deliver(uint256 amount) external {
        require(!_isExcluded[msg.sender], "Excluded cannot call this!");
        uint256 rate = _getReflectionRate();
        _reflectionBalance[msg.sender] = _reflectionBalance[msg.sender].sub(
            amount.mul(rate)
        );
        _reflectionTotal = _reflectionTotal.sub(amount.mul(rate));
        feeTotal = feeTotal.add(amount);
        taxFeeTotal = taxFeeTotal.add(amount);
        emit Transfer(msg.sender, address(this), amount);
    }

