    "contracts/prize-pots/player-pot/v3/Discounts.sol": {
      "content": "// SPDX-License-Identifier: MIT

pragma solidity ^0.8.19;

import {EnumerableSet} from "@openzeppelin/contracts/utils/structs/EnumerableSet.sol";
import {IERC721} from "@openzeppelin/contracts/token/ERC721/IERC721.sol";

contract Discounts {
    using EnumerableSet for EnumerableSet.UintSet;

    struct DiscountConfig {
        uint16 id;
        uint16 discountFactor;
        uint16 discountedTickets;
        uint8 collections;
        address collection1;
        address collection2;
    }

    uint256 internal constant MAX_DISCOUNT_FACTOR = 9000;
    uint256 internal constant DISCOUNT_FACTOR_BASE = 10000;

    mapping(address user => mapping(uint256 roundId => mapping(uint256 discountId => uint256 used)))
        public discountsUsed;

    mapping(uint256 => DiscountConfig) private _discounts;
    EnumerableSet.UintSet private _discountIds;

    event DiscountSet(DiscountConfig);

    function discounts() external view returns (DiscountConfig[] memory) {
        uint256 length = _discountIds.length();
        DiscountConfig[] memory data = new DiscountConfig[](length);
        for (uint256 i = 0; i < length; ++i) {
            data[i] = _discounts[_discountIds.at(i)];
        }
        return data;
    }

    function _discountedCost(
        uint256 roundId,
        address user,
        uint256 price,
        uint256 tickets,
        uint256[] calldata discountIds
    ) internal view returns (uint256 cost, uint256[] memory discountedTicketss, uint256[] memory discountFactors) {
        uint256 length = discountIds.length;
        discountedTicketss = new uint256[](length);
        discountFactors = new uint256[](length);
        for (uint256 i = 0; i < length; ++i) {
            (uint256 discountedTickets, uint256 discountFactor) = _checkDiscount(
                roundId,
                user,
                discountIds[i]
            );
            discountedTickets = _min(discountedTickets, tickets);
            discountedTicketss[i] = discountedTickets;
            discountFactors[i] = discountFactor;

            tickets -= discountedTickets;
            cost += (price * discountFactor / DISCOUNT_FACTOR_BASE * discountedTickets);
        }

        cost += (price * tickets);
    }

    function _useDiscounted(
        uint256 roundId,
        address user,
        uint256 price,
        uint256 tickets,
        uint256[] calldata discountIds
    ) internal returns (uint256 cost) {
        uint256 length = discountIds.length;
        for (uint256 i = 0; i < length; ++i) {
            uint256 discountId = discountIds[i];
            (uint256 discountedTickets, uint256 discountFactor) = _checkDiscount(
                roundId,
                user,
                discountId
            );
            discountedTickets = _min(discountedTickets, tickets);
            discountsUsed[user][roundId][discountId] += discountedTickets;

            tickets -= discountedTickets;
            cost += (price * discountFactor / DISCOUNT_FACTOR_BASE * discountedTickets);
        }

        cost += (price * tickets);
    }

    function _checkDiscount(
        uint256 roundId,
        address user,
        uint256 discountId
    ) internal view returns (uint256 tickets, uint256 discountFactor) {
        DiscountConfig memory discount = _discounts[discountId];
        (
            // uint16 id_,
            uint16 discountFactor_,
            uint16 discountedTickets_,
            uint8 collections_,
            address collection1_
        ) = (
                // discount.id,
                discount.discountFactor,
                discount.discountedTickets,
                discount.collections,
                discount.collection1
            );
        uint256 balance = IERC721(collection1_).balanceOf(user);
        if (collections_ == 2) {
            balance = _min(
                balance,
                IERC721(discount.collection2).balanceOf(user)
            );
        }

        uint256 used = discountsUsed[user][roundId][discountId];
        tickets = balance * uint256(discountedTickets_);
        tickets = used > tickets ? 0 : tickets - used;
        discountFactor = uint256(discountFactor_);
    }

    function _setDiscount(DiscountConfig memory discount) internal {
        (
            uint16 id,
            uint16 discountFactor,
            uint16 discountedTickets,
            uint8 collections,
            address collection1
        ) = (
                discount.id,
                discount.discountFactor,
                discount.discountedTickets,
                discount.collections,
                discount.collection1
            );
        require(
            discountFactor >= MAX_DISCOUNT_FACTOR,
            "Invalid discount factor"
        );
        require(discountedTickets > 0, "Invalid discount tickets");
        require(collection1 != address(0), "Invalid collection1");
        require(
            collections == 1 ||
                (collections == 2 && discount.collection2 != address(0)),
            "Invalid collections"
        );
        uint256 _id = uint256(id);

        _discountIds.add(_id);
        _discounts[_id] = discount;

        emit DiscountSet(discount);
    }

    function _removeDiscount(uint256 id) internal {
        _discountIds.remove(id);
        delete _discounts[id];
    }

    function _min(uint256 a, uint256 b) internal pure returns (uint256) {
        return a < b ? a : b;
    }
}
"
