    "contracts/prize-pots/player-pot/v3/PixelSweeperPlayerPotV3Base.sol": {
      "content": "// SPDX-License-Identifier: MIT

pragma solidity ^0.8.19;

import {ReentrancyGuard} from "@openzeppelin/contracts/security/ReentrancyGuard.sol";
import {Ownable} from "@openzeppelin/contracts/access/Ownable.sol";
import {ERC165Checker} from "@openzeppelin/contracts/utils/introspection/ERC165Checker.sol";
import {ERC165, IERC165} from "@openzeppelin/contracts/utils/introspection/ERC165.sol";
import {IERC721Receiver} from "@openzeppelin/contracts/token/ERC721/IERC721Receiver.sol";
import {IERC721Enumerable} from "@openzeppelin/contracts/token/ERC721/extensions/IERC721Enumerable.sol";
import {IERC721} from "@openzeppelin/contracts/token/ERC721/IERC721.sol";
import {Strings} from "@openzeppelin/contracts/utils/Strings.sol";
import {Address} from "@openzeppelin/contracts/utils/Address.sol";
import {Pausable} from "@openzeppelin/contracts/security/Pausable.sol";

import {ITicket} from "../../token/ITicket.sol";
import {IControlledBurnableToken} from "../../token-upgradeable/IControlledBurnableToken.sol";
import {BurnableTicketFactoryInternal} from "../../token-upgradeable/BurnableTicketFactoryInternal.sol";
import {BurnableTicket, ITicket} from "../../token-upgradeable/BurnableTicket.sol";
import {ITokenController} from "../../token/ITokenController.sol";
import {ITicketProvider} from "../../token/ITicketProvider.sol";
import {IDynamicPeriodicPrizeStrategy} from "../../prize-strategy/strategies/IDynamicPeriodicPrizeStrategy.sol";
import {IPeriodicPrizeStrategyListener} from "../../prize-strategy/IPeriodicPrizeStrategyListener.sol";
import {IBeforeAwardListener} from "../../prize-strategy/IBeforeAwardListener.sol";
import {IRareboardSweeper} from "../../../sweeper/v2/IRareboardSweeper.sol";
import {EmergencyWithdrawable} from "../../../sweeper/utils/EmergencyWithdrawable.sol";
import {Discounts} from "./Discounts.sol";

abstract contract PixelSweeperPlayerPotV3Base is
    EmergencyWithdrawable,
    ReentrancyGuard,
    ERC165,
    IERC721Receiver,
    BurnableTicketFactoryInternal,
    ITicketProvider,
    IPeriodicPrizeStrategyListener,
    IBeforeAwardListener,
    Pausable,
    Discounts
{
    using ERC165Checker for address;
    using Strings for uint256;

    struct Round {
        uint256 roundId;
        uint256 ticketPrice;
        BurnableTicket ticket;
        uint256 ticketsSold;
        uint256 nftsSweept;
    }

    struct FundConfig {
        address treasury;
        address partner;
        uint32 treasurySplit;
        uint32 partnerSplit;
        uint32 sweepSplit;
    }

    /// @dev Event emitted when executorsOnly is changed
    event ExecutorsOnlySet(bool executorsOnly);

    /// @dev Event emitted when a executor is added/removed from the whitelist
    event ExecutorSet(address indexed user, bool isExecutor);

    /// @dev Event emitted when the Prize Strategy is set
    event PrizeStrategySet(address indexed prizeStrategy);

    /// @dev Event emitted when new ticket price is set
    event TicketPriceSet(uint256 price);

    /// @dev Event emitted when fund receivers are changed
    event FundConfigSet(FundConfig fundConfig);

    /// @dev Event emitted when tickets are bought
    event TicketsBought(
        uint256 indexed rountId,
        address indexed buyer,
        address indexed recipient,
        uint256 amount
    );

    event PrizeAdded(uint256 tokenId, uint256 price, uint256 nftsSwept);

    address public immutable collection;

    bool public immutable ticketsRollOver;

    uint256 private constant FUND_SPLIT_BASE = 10000;

    /// @dev The Prize Strategy that this Prize Pool is bound to.
    IDynamicPeriodicPrizeStrategy public prizeStrategy;

    /// @dev Sweeper used by player pot
    IRareboardSweeper public immutable sweeper;

    /// @dev Sweeping funds used to fund sweeps for playerpot
    address public immutable sweepingFunds;

    /// @dev current round id
    uint256 public roundId;

    /// @dev ticket price for next round
    uint256 public ticketPrice;

    /// @dev Record for all past and current rounds
    mapping(uint256 => Round) public rounds;

    /// @dev Receiver addresses for ticket sale funds
    FundConfig public fundConfig;

    mapping(address => bool) public isExectutor;
    bool public executorsOnly;

    bool private ignoringOnReceive;

    modifier ignoreOnReceived() {
        ignoringOnReceive = true;
        _;
        ignoringOnReceive = false;
    }

    constructor(
        address _owner,
        IRareboardSweeper _sweeper,
        address _collection,
        uint256 _ticketPrice,
        FundConfig memory _fundConfig,
        bool _ticketsRollOver
    ) {
        _transferOwnership(_owner);

        sweeper = _sweeper;
        collection = _collection;
        ticketPrice = _ticketPrice;
        ticketsRollOver = _ticketsRollOver;

        sweepingFunds = sweeper.createSweepingFunds(collection, address(this));
        require(sweepingFunds != address(0), "Setup failed");

        _setFundConfig(_fundConfig);

        _pause();

        _prepareNextRound();
    }

    // -------------------------
    // User interface
    // -------------------------

    /// @notice Donate `tokenIds` to the prize pool for this round.
    function donatePrizes(
        uint256[] calldata tokenIds
    ) external ignoreOnReceived {
        require(address(prizeStrategy) != address(0), "No strategy set");
        for (uint256 i = 0; i < tokenIds.length; ++i) {
            uint256 tokenId = tokenIds[i];
            IERC721(collection).transferFrom(
                msg.sender,
                address(this),
                tokenId
            );
            _addPrize(tokenId, 0);
        }
    }

    // -------------------------
    // Sweep executor interface
    // -------------------------

    /// @notice Sweep the floor and add to the award
    function sweep(
        address _collection,
        uint256 _tokenId,
        uint256 _price,
        bytes calldata _data,
        uint256 _timestamp,
        bytes memory _signature
    ) external ignoreOnReceived {
        require(_collection == collection, "Only main collection");
        require(!executorsOnly || isExectutor[_msgSender()], "Only executors");

        sweeper.sweep(
            _collection,
            _tokenId,
            _price,
            _data,
            _timestamp,
            _signature,
            address(this)
        );

        _addPrize(_tokenId, _price);
    }

    // -------------------------
    // ITokenController interface
    // -------------------------

    /// @notice hook used by tickets
    function beforeTokenTransfer(
        address from,
        address to,
        uint256
    ) external view override {
        require(from != to, "Self transfer");
        require(
            from == address(0) || to == address(0),
            "Tickets non transferable"
        );
        require(
            address(prizeStrategy) == address(0) ||
                prizeStrategy.awardNotInProgress(),
            "Award in progress"
        );
    }

    // -------------------------
    // IPeriodicPrizeStrategyListener interface
    // -------------------------
    /// @notice hook to get notified before prize will be awarded
    function beforePrizePoolAwarded(uint256, uint256) external override {
        require(_msgSender() == address(prizeStrategy), "only prize strategy");

        // check if nft was transfered to this contract without using donate, sweep or safeTransfer
        uint256 prizes = IERC721(collection).balanceOf(address(this));
        if (prizes > rounds[roundId].nftsSweept) {
            rounds[roundId].nftsSweept = prizes;
        }
    }

    /// @notice hook to get notified once prize was awarded
    function afterPrizePoolAwarded(uint256, uint256) external override {
        require(_msgSender() == address(prizeStrategy), "only prize strategy");
        _prepareNextRound();
    }

    // -------------------------
    // Owner interface
    // -------------------------

    /// @notice Set the receivers and split of ticket sale funds
    function setFundConfig(FundConfig memory _fundConfig) external onlyOwner {
        _setFundConfig(_fundConfig);
    }

    /// @notice Set to only allow whitelisted executors to sweep
    function setExecutorsOnly(bool _executorsOnly) external onlyOwner {
        executorsOnly = _executorsOnly;
        emit ExecutorsOnlySet(_executorsOnly);
    }

    /// @notice Allow/disallow executor to sweep
    function setExecutor(
        address executor,
        bool _isExecutor
    ) external onlyOwner {
        isExectutor[executor] = _isExecutor;
        emit ExecutorSet(executor, _isExecutor);
    }

    /// @notice Sets the prize strategy of the prize pool.  Only callable by the owner.
    /// @param _prizeStrategy The new prize strategy
    function setPrizeStrategy(
        IDynamicPeriodicPrizeStrategy _prizeStrategy
    ) external onlyOwner whenPaused {
        _setPrizeStrategy(_prizeStrategy);
    }

    /// @notice Sets the price per ticket. Does not effect current round.  Only callable by the owner.
    /// @param _ticketPrice The new ticket price
    function setTicketPrice(uint256 _ticketPrice) external onlyOwner {
        ticketPrice = _ticketPrice;
        emit TicketPriceSet(_ticketPrice);
    }

    /// @notice Pauses ticket sale
    /// @dev Callable by owner, pausing the contract is used to complete the setup and for migration purposes.
    function pause() external onlyOwner {
        _pause();
    }

    /// @notice Resume normal operations
    /// @dev Callable by owner
    function unpause() external onlyOwner {
        require(address(prizeStrategy) != address(0), "no-prize-startegy");
        require(
            address(prizeStrategy.periodicPrizeStrategyListener()) ==
                address(this),
            "not-set-as-after-listener"
        );
        require(
            address(prizeStrategy.beforeAwardListener()) == address(this),
            "not-set-as-before-listener"
        );
        _unpause();
    }

    /// @notice Add or change a discount config.
    /// @dev Callable by owner
    function setDiscount(DiscountConfig memory discount) external onlyOwner {
        _setDiscount(discount);
    }

    /// @notice Remove a discount config.
    /// @dev Callable by owner
    function removeDiscount(uint256 id) external onlyOwner {
        _removeDiscount(id);
    }

    // -------------------------
    // View interface
    // -------------------------

    /// @notice Get the current ticket balance of user and totalSupply.
    function currentTicketBalance(
        address _user
    ) external view returns (uint256 balance, uint256 totalSupply) {
        BurnableTicket _ticket = rounds[roundId].ticket;
        balance = _ticket.balanceOf(_user);
        totalSupply = _ticket.totalSupply();
    }

    function currentRound() external view returns (Round memory) {
        return rounds[roundId];
    }

    function ticket() external view override returns (ITicket) {
        return rounds[roundId].ticket;
    }

    function calculateCost(
        address user,
        uint256 tickets,
        uint256[] calldata discountIds
    )
        external
        view
        returns (
            uint256 cost,
            uint256[] memory discountedTicketss,
            uint256[] memory discountFactors
        )
    {
        return
            _discountedCost(
                roundId,
                user,
                rounds[roundId].ticketPrice,
                tickets,
                discountIds
            );
    }

    function supportsInterface(
        bytes4 interfaceId
    ) public view virtual override(ERC165, IERC165) returns (bool) {
        return
            interfaceId == type(IPeriodicPrizeStrategyListener).interfaceId ||
            interfaceId == type(IBeforeAwardListener).interfaceId ||
            super.supportsInterface(interfaceId);
    }

    function onERC721Received(
        address,
        address,
        uint256 tokenId,
        bytes memory
    ) public virtual override returns (bytes4) {
        require(msg.sender == collection, "Only collection");

        if (!ignoringOnReceive) {
            _addPrize(tokenId, 0);
        }

        return this.onERC721Received.selector;
    }

    // -------------------------
    // Internal interface
    // -------------------------

    function _buyTickets(
        uint256 amount,
        address recipient,
        uint256[] calldata discountIds
    ) internal nonReentrant whenNotPaused returns (uint256 cost) {
        address buyer = _msgSender();
        cost = _useDiscounted(
            roundId,
            buyer,
            rounds[roundId].ticketPrice,
            amount,
            discountIds
        );
        BurnableTicket ticket_ = rounds[roundId].ticket;

        ticket_.controllerMint(recipient, amount * 1 ether);
        emit TicketsBought(roundId, buyer, recipient, amount * 1 ether);

        _distributeFunds(cost);
    }

    function _addPrize(uint256 tokenId, uint256 price) internal virtual {
        rounds[roundId].nftsSweept += 1;
        emit PrizeAdded(tokenId, price, rounds[roundId].nftsSweept);
    }

    function _setPrizeStrategy(
        IDynamicPeriodicPrizeStrategy _prizeStrategy
    ) internal {
        require(
            address(_prizeStrategy) != address(0),
            "prizeStrategy-not-zero"
        );
        require(
            address(_prizeStrategy).supportsInterface(
                type(IDynamicPeriodicPrizeStrategy).interfaceId
            ),
            "prizeStrategy-invalid"
        );

        if (address(prizeStrategy) != address(0)) {
            IERC721(collection).setApprovalForAll(
                address(prizeStrategy),
                false
            );
        }
        IERC721(collection).setApprovalForAll(address(_prizeStrategy), true);

        prizeStrategy = _prizeStrategy;

        emit PrizeStrategySet(address(_prizeStrategy));
    }

    function _prepareNextRound() internal {
        BurnableTicket lastTicket = rounds[roundId].ticket;
        bool rollOverTicket = ticketsRollOver &&
            address(lastTicket) != address(0) &&
            rounds[roundId].nftsSweept == 0;

        if (!rollOverTicket && address(lastTicket) != address(0)) {
            rounds[roundId].ticketsSold = lastTicket.totalSupply();
            lastTicket.setBurnable(true);
        }

        if (!rollOverTicket) {
            Round storage round = rounds[++roundId];

            string memory id = roundId.toString();
            string memory name = string(
                abi.encodePacked("PixelSweeper PlayerPot - Kreepy Kritters - Ticket #", id)
            );
            string memory symbol = string(abi.encodePacked("SWPR-P KK #", id));
            BurnableTicket ticket_ = _createTicket(
                name,
                symbol
            );

            round.ticket = ticket_;
            round.roundId = roundId;
            round.ticketPrice = ticketPrice;
        }
    }

    function _setFundConfig(FundConfig memory _fundConfig) internal {
        require(
            _fundConfig.sweepSplit +
                _fundConfig.treasurySplit +
                _fundConfig.partnerSplit ==
                FUND_SPLIT_BASE,
            "Invalid Split"
        );
        require(_fundConfig.treasury != address(0), "Invalid fund address");
        require(_fundConfig.partner != address(0), "Invalid fund address");

        emit FundConfigSet(_fundConfig);

        fundConfig = _fundConfig;
    }

    function _distributeFunds(uint256 _value) internal {
        address treasury = fundConfig.treasury;
        (address partner, uint32 partnerSplit, uint32 sweepSplit) = (
            fundConfig.partner,
            fundConfig.partnerSplit,
            fundConfig.sweepSplit
        );

        uint256 valueSweep = (_value * uint256(sweepSplit)) / FUND_SPLIT_BASE;
        uint256 valuePartner = (_value * uint256(partnerSplit)) /
            FUND_SPLIT_BASE;
        uint256 valueTreasury = _value - valueSweep - valuePartner;

        Address.sendValue(payable(sweepingFunds), valueSweep);
        Address.sendValue(payable(treasury), valueTreasury);
        Address.sendValue(payable(partner), valuePartner);
    }
}
