    "contracts/prize-pots/player-pot/v3/PixelSweeperPlayerPotV3CrossChainSender.sol": {
      "content": "// SPDX-License-Identifier: MIT

pragma solidity ^0.8.19;

import {Address} from "@openzeppelin/contracts/utils/Address.sol";
import {Client} from "@chainlink/contracts-ccip/src/v0.8/ccip/libraries/Client.sol";
import {IRouterClient} from "@chainlink/contracts-ccip/src/v0.8/ccip/interfaces/IRouterClient.sol";

import {PixelSweeperPlayerPotV3Base, IRareboardSweeper, BurnableTicket} from "./PixelSweeperPlayerPotV3Base.sol";
import {CCIPApprovedSource} from "../../../ccip/CCIPApprovedSource.sol";
import {PriceConversion} from "./PriceConversion.sol";
import {CrossChainPlayerPotEncoder, BytesLib} from "./CrossChainPlayerPotEncoder.sol";

contract PixelSweeperPlayerPotV3CrossChainSender is
    PixelSweeperPlayerPotV3Base,
    PriceConversion,
    CrossChainPlayerPotEncoder
{
    using BytesLib for bytes;

    error InvalidCommandEncoding(uint256, uint256);
    error InsufficientFees();

    event CrossChainTagetChanged(uint64 chainSelector, address target);
    event SetSecondsLeft(uint256 minSecondsLeft);

    address public immutable crossChainTarget;
    uint64 public immutable crossChainSelector;
    address public immutable router;

    uint256 public minSecondsLeft = 60 minutes;

    modifier whenEnoughTimeLeft() {
        require(address(prizeStrategy) != address(0), "No prize strategy");
        require(
            prizeStrategy.prizePeriodRemainingSeconds() > minSecondsLeft,
            "Round finished"
        );
        _;
    }

    constructor(
        address _owner,
        IRareboardSweeper _sweeper,
        address _prize,
        uint256 _ticketPrice,
        FundConfig memory _fundConfig,
        address _router,
        address _crossChainTarget,
        uint64 _crossChainSelector
    )
        PixelSweeperPlayerPotV3Base(
            _owner,
            _sweeper,
            _prize,
            _ticketPrice,
            _fundConfig,
            false
        )
    {
        crossChainTarget = _crossChainTarget;
        crossChainSelector = _crossChainSelector;
        router = _router;
        emit SetSecondsLeft(minSecondsLeft);
    }

    /* -----------------------*/
    /* --- User interface --- */
    /* -----------------------*/

    /**
     * @notice Buy tickets to enter the current player pot
     * @param amount amount of tickets to be bought
     * @param recipient ticket recipient
     * @param discountIds ids of discounts to check and apply
     * @param extraArgs CCIP extraArgs, encoding gasLimit on destination chain and possibly future protocol options.
     */
    function buyTickets(
        uint256 amount,
        address recipient,
        uint256[] calldata discountIds,
        bytes calldata extraArgs
    ) external payable whenEnoughTimeLeft {
        uint256 cost = _buyTickets(amount, recipient, discountIds);

        uint256 ticketValueUsd = _usdFromNative(
            amount * rounds[roundId].ticketPrice
        );

        bytes memory data = encodeTicketTransfer(recipient, ticketValueUsd);

        Client.EVM2AnyMessage memory message = Client.EVM2AnyMessage({
            receiver: abi.encode(crossChainTarget),
            data: data,
            tokenAmounts: new Client.EVMTokenAmount[](0),
            extraArgs: extraArgs,
            feeToken: address(0)
        });

        uint256 fee = IRouterClient(router).getFee(crossChainSelector, message);
        uint256 totalCost = cost + fee;

        require(msg.value >= totalCost, "Insufficient value sent");

        IRouterClient(router).ccipSend{value: fee}(crossChainSelector, message);

        if (msg.value > totalCost) {
            Address.sendValue(payable(msg.sender), msg.value - totalCost);
        }
    }

    /**
     * @notice Calculate CCIP fees charged by {buyTickets} for the given parameters.
     * @param amount amount of tickets bought
     * @param recipient ticket recipient
     * @param extraArgs CCIP extraArgs, encoding gasLimit on destination chain and possibly future protocol options.
     * @return fee in native gas token
     */
    function getTransferTicketFee(
        uint256 amount,
        address recipient,
        bytes calldata extraArgs
    ) external view returns (uint256 fee) {
        uint256 ticketValueUsd = _usdFromNative(
            amount * rounds[roundId].ticketPrice
        );

        bytes memory data = encodeTicketTransfer(recipient, ticketValueUsd);

        Client.EVM2AnyMessage memory message = Client.EVM2AnyMessage({
            receiver: abi.encode(crossChainTarget),
            data: data,
            tokenAmounts: new Client.EVMTokenAmount[](0),
            extraArgs: extraArgs,
            feeToken: address(0)
        });

        fee = IRouterClient(router).getFee(crossChainSelector, message);
    }

    /* ------------------------*/
    /* --- Owner interface --- */
    /* ------------------------*/

    /**
     * @notice Configure the Eth (value token) to usd price feed.
     * @param feed chainlink price feed
     */
    function setEthUsdPriceFeed(address feed) external onlyOwner {
        _setEthUsdFeed(feed);
    }

    /**
     * @notice Set delay before rounds ends in which no tickets can be bought anymore.
     * @dev This delay is used to make sure CCIP has enough time to transfer the cross chain
     * messages.
     * @param _minSecondsLeft new minSecondsLeft
     */
    function setMinSecondsLeft(uint256 _minSecondsLeft) external onlyOwner {
        minSecondsLeft = _minSecondsLeft;
        emit SetSecondsLeft(_minSecondsLeft);
    }
}
